//
// PUBLIC DOMAIN CRT STYLED SCAN-LINE SHADER
//
//   by Timothy Lottes
//
//   edited by getItemFromBlock

// This is more along the style of a really good CGA arcade monitor.
// With RGB inputs instead of NTSC.
// The shadow mask example has the mask rotated 90 degrees for less chromatic aberration.
//
// Left it unoptimized to show the theory behind the algorithm.
//
// It is an example what I personally would want as a display option for pixel art games.
// Please take and use, change, or whatever.
//

// Emulated input resolution.
#define resizer (vec2(80,45))
#define pixel_width (iResolution.y / resizer.y)

// Hardness of scanline.
//  8.0 = soft
// 16.0 = medium
float hardScan = 8.0;

// Hardness of pixels in scanline.
// 2.0 = soft
// 4.0 = hard
float hardPix = 3.0;

// Display warp.
// 0.0 = none
// 1.0/8.0 = extreme
vec2 warp = vec2(1.0/16.0, 1.0/12.0); 

// Amount of shadow mask.
float maskDark = 0.5;
float maskLight = 1.5;

//------------------------------------------------------------------------

// sRGB to Linear.
// Assuming using sRGB typed textures this should not be needed.
float ToLinear1(float c){
    //return c <= 0.04045 ? c/12.92 : pow((c + 0.055)/1.055, 2.4);
    return pow(c, 2.2); // straight up gamma coding 
}
vec3 ToLinear(vec3 c){
    return vec3(ToLinear1(c.r),ToLinear1(c.g),ToLinear1(c.b));
}

// Linear to sRGB.
// Assuing using sRGB typed textures this should not be needed.
float ToSrgb1(float c){
    //return c < 0.0031308 ? c*12.92 : 1.055 * pow(c, 0.41666) - 0.055;
    return pow(c, 0.45454545454545454545454545454545); // 0.4545... ~= 1.0/2.2 
}
vec3 ToSrgb(vec3 c){return vec3(ToSrgb1(c.r),ToSrgb1(c.g),ToSrgb1(c.b));}

// Nearest emulated sample given floating point position and texel offset.
// Also zero's off screen.
vec3 SampleLinear(vec2 position, vec2 offset){
    position = floor(position * resizer + offset) / resizer;

    // any coordinate of position is out of image 
    if (max(abs(position.x-0.5), abs(position.y-0.5)) > 0.5)  
        return vec3(0.0,0.0,0.0);

    // -32.0 to get 'lowest' resolution image?
    return ToLinear( texture(iChannel0, position.xy, -32.0).rgb );
}


// Distance in emulated pixels to nearest texel.
vec2 Dist(vec2 pos){
    pos = pos * resizer; // scale

    // how far is the true pixel from the center
    return ( 0.5 - fract(pos)); 
}

// 1D Gaussian.
float Gauss(float pos, float scale) {
    // scale here is likely (1 / sigma^2)
    return exp( -scale * pos*pos);
}

// interpolate + apply gaussian convolution to a 
// 3-pixel horizontal window
vec3 Horz3(vec2 position, float vertical_offset, float scale){
    vec3 b = SampleLinear(position, vec2(-1.0, vertical_offset));
    vec3 c = SampleLinear(position, vec2( 0.0, vertical_offset));
    vec3 d = SampleLinear(position, vec2( 1.0, vertical_offset));
    float dst = Dist(position).x;

    // Convert distance to weight.
    float wb = Gauss(dst - 1.0, scale);
    float wc = Gauss(dst      , scale); // dst + 0
    float wd = Gauss(dst + 1.0, scale);

    // Return filtered sample.
    return (b*wb + c*wc + d*wd) / (wb + wc + wd);
}


// 5-tap Gaussian filter along horz line.
vec3 Horz5(vec2 position, float vertical_offset, float scale){
    vec3 a = SampleLinear(position, vec2(-2.0, vertical_offset));
    vec3 b = SampleLinear(position, vec2(-1.0, vertical_offset));
    vec3 c = SampleLinear(position, vec2( 0.0, vertical_offset));
    vec3 d = SampleLinear(position, vec2( 1.0, vertical_offset));
    vec3 e = SampleLinear(position, vec2( 2.0, vertical_offset));

    float dst = Dist(position).x;

    // apply Gaussian envelope to interpolation weights
    float wa = Gauss(dst - 2.0, scale);
    float wb = Gauss(dst - 1.0, scale);
    float wc = Gauss(dst      , scale); // dst + 0
    float wd = Gauss(dst + 1.0, scale);
    float we = Gauss(dst + 2.0, scale);

    // Return filtered sample.
    return (a*wa + b*wb + c*wc + d*wd + e*we) / (wa + wb + wc + wd + we);
}


// Allow nearest three lines to affect pixel.
// This is sort-of doing bilinear interpolation and
// Gaussian filtering at the same time. Closer to bicubic  
// due to 3 pixels taken into account but weights are a Gaussian curve
vec3 TriGaussianInterpolation(vec2 position, float pixel_scale, float scanline_scale){
    // interpolate 3 vertical pixels
    vec3 a = Horz3(position, -1.0, pixel_scale); // above current pixel
    vec3 b = Horz5(position,  0.0, pixel_scale); // current pixel
    vec3 c = Horz3(position,  1.0, pixel_scale); // below current pixel

    float dst = Dist(position).y; // vertical distance to center of pixel

    // apply Gaussian envelope to interpolation weights
    float wa = Gauss(dst - 1.0, scanline_scale); 
    float wb = Gauss(dst      , scanline_scale); // dst + 0
    float wc = Gauss(dst + 1.0, scanline_scale);

    return a*wa + b*wb + c*wc;
}

// Distortion of scanlines, and end of screen alpha.
vec2 Warp(vec2 pos){
    // [0 -> 1] => [-0.5 -> 0.5]
    pos = 2.0 * pos - 1.0; 
    // the closer pos is to 0,0 the less distortion is
    // added to the multiplier
    // because the coords are inverted, the corners are 
    // affected the most
    pos *= vec2(1.0 + (pos.y*pos.y) * warp.y,  
                1.0 + (pos.x*pos.x) * warp.x);

    // get it back to the [0 -> 1] range
    return (pos + 1.0) * 0.5;
}

// Shadow mask.
vec3 Mask(vec2 pos, float mask_dark, float mask_light){
    pos.x += pos.y * pixel_width * 3.0;//(pixels_width / 2.0);

    vec3 mask = vec3(mask_dark, mask_dark, mask_dark);

    pos.x = fract(pos.x / pixel_width); // 1/6

    // simulating per-physical-pixel intensity
    if(pos.x < 0.333) 
      mask.r = mask_light;
    else if(pos.x<0.666) 
        mask.g = mask_light;
    else 
        mask.b = mask_light;

    return mask;
}

// Entry.
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Unmodified.
    vec2 norm_coord = fragCoord.xy/iResolution.xy; // uv
    norm_coord = norm_coord * 1.2 - .1;

    if(texelFetch(iChannel1, ivec2(32, 0), 0).x > 0.5 || iResolution.x < 1000.)
    {
        // distortion + scanlines
        vec2 pos = Warp(norm_coord); // warp uv coord to get screen edge distortion

        // interpolate + gaussian filter 3x3 window
        fragColor.rgb= TriGaussianInterpolation(pos, hardPix, hardScan);
    }
    else {
        // distortion + scanlines + 'bayer' masking
        vec2 pos = Warp(norm_coord);
        fragColor.rgb = TriGaussianInterpolation(pos, hardPix, hardScan) * 
                        Mask(fragCoord.xy, maskDark, maskLight); 
        // mask should take in the distorted uv in my opinion
    }    

    fragColor.a = 1.0;  

    fragColor.rgb= ToSrgb(fragColor.rgb);


}

