vec4 grad[] = vec4[](
    vec4(0,0.027,0.391,0),
    vec4(0.125,0.418,791,0.16),
    vec4(0.926,1.0,1.0,0.42),
    vec4(1.0,0.664,0,0.6425),
    vec4(0,0.008,0,0.8575),
    vec4(0,0.027,0.391,1.0)
);

vec3 lerpGrad(vec4[6] g, float x){
    x = fract(x);
    for (int i = 0; i < g.length(); i++){
        if (g[i].a >= x){
            vec4 gradStart = g[i - 1];
            vec4 gradEnd = g[i];
            return mix(gradStart.rgb,gradEnd.rgb,smoothstep(gradStart.a,gradEnd.a,x));
        }
    }
}

vec2 getBulbNormal(float t){
    return vec2(cos(t) - cos(2.0*t),sin(t) - sin(2.0*t))/(2.0*sqrt((1.0 - cos(t))/2.0));
}

vec2 getBulbPoint(float t){
    return vec2(cos(t)/2.0 - cos(2.0*t)/4.0, sin(t)*(1.0 - cos(t))/2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 drag_offset = texelFetch(iChannel1, ivec2(1,0), 0).xy/iResolution.y;
    float zoom = texelFetch(iChannel0, ivec2(1,0), 0).y/iResolution.y;
    vec2 uv = ((fragCoord - iResolution.xy/2.0)/iResolution.y)*pow(2.0,zoom) - drag_offset;
    float t = uv.x*2.0*PI;
    float l = length(getBulbPoint(t));
    vec2 coord = getBulbPoint(t) + getBulbNormal(t)*abs(uv.y)*6.0;

    float n = -1.0;
    int c = 0;
    float r0 = coord.x;
    float i0 = coord.y;
    float r = 0.0;
    float i = 0.0;
    float r2 = 0.0;
    float i2 = 0.0;

    for (c; c < max_steps && r2 + i2 <= 4.0; c++) {        
        i = 2.0*r*i + i0;
        r = r2 - i2 + r0;
        r2 = r*r;
        i2 = i*i;
    }
    if (r2 + i2 > 4.0) {
        n = float(c) + 1.0 - log(log(r2*r2 + i2*i2)/2.0)/log(2.0);
    }

    vec3 col = n == -1.0 ? vec3(0) : lerpGrad(grad, n/100.0);

    fragColor = vec4(col,1.0);
}

