float intersect_plane(Ray ray, vec3 center, vec3 normal)
{
    float denom = dot(ray.rd, normal);
    float t = dot(center - ray.ro, normal) / denom;
	return t > 0.0 ? t : c_superFar;
}

bool intersect_light(Ray r, inout HitResult res)
{
	float t = intersect_plane(r, light_position, light_normal);

	vec3 p = ray_at(r, t);
	if(all(lessThan(abs(light_position - p).xz, vec2(light_size * 0.5))) && t < res.distance) {
		res.distance = t;
        res.viewDir = r.rd;
        res.hitPoint = p;
        res.normal = light_normal;
        return true;
	}

	return false;
}

vec3 sample_light(vec2 rng)
{
	return light_position + vec3(rng.x - 0.5, 0, rng.y - 0.5) * light_size;
}

bool iQuad(in Ray r, inout HitResult res, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    vec3 normal = normalize(cross(c-a, c-b));
    if (dot(normal, r.rd) > 0.0f)
    {
        normal *= -1.0f;

		vec3 temp = d;
        d = a;
        a = temp;

        temp = b;
        b = c;
        c = temp;
    }

    vec3 p = r.ro;
    vec3 q = r.ro + r.rd;
    vec3 pq = q - p;
    vec3 pa = a - p;
    vec3 pb = b - p;
    vec3 pc = c - p;

    vec3 m = cross(pc, pq);
    float v = dot(pa, m);
    vec3 intersectPos;
    if (v >= 0.0f)
    {
        float u = -dot(pb, m);
        if (u < 0.0f) return false;
        float w = ScalarTriple(pq, pb, pa);
        if (w < 0.0f) return false;
        float denom = 1.0f / (u+v+w);
        u*=denom;
        v*=denom;
        w*=denom;
        intersectPos = u*a+v*b+w*c;
    }
    else
    {
        vec3 pd = d - p;
        float u = dot(pd, m);
        if (u < 0.0f) return false;
        float w = ScalarTriple(pq, pa, pd);
        if (w < 0.0f) return false;
        v = -v;
        float denom = 1.0f / (u+v+w);
        u*=denom;
        v*=denom;
        w*=denom;
        intersectPos = u*a+v*d+w*c;
    }

    float dist;
    if (abs(r.rd.x) > 0.1f)
    {
        dist = (intersectPos.x - r.ro.x) / r.rd.x;
    }
    else if (abs(r.rd.y) > 0.1f)
    {
        dist = (intersectPos.y - r.ro.y) / r.rd.y;
    }
    else
    {
        dist = (intersectPos.z - r.ro.z) / r.rd.z;
    }

	if (dist > c_minimumRayHitTime && dist < res.distance)
    {
        res.distance = dist;
        res.viewDir = r.rd;
        res.hitPoint = r.ro + res.distance * r.rd;
        res.normal = normal;        
        return true;
    }    

    return false;
}

bool iSphere(in Ray r, inout HitResult res, in vec4 sphere)
{
	vec3 m = r.ro - sphere.xyz;

	float b = dot(m, r.rd);

	float c = dot(m, m) - sphere.w * sphere.w;

	if(c > 0.0 && b > 0.0)
		return false;

	float discr = b * b - c;

	if(discr < 0.0)
		return false;

    bool fromInside = false;
	float dist = -b - sqrt(discr);
    if (dist < 0.0f)
    {
        fromInside = true;
        dist = -b + sqrt(discr);
    }

	if (dist > c_minimumRayHitTime && dist < res.distance)
    {
        res.distance = dist;  
        res.viewDir = r.rd;
        res.hitPoint = r.ro + r.rd * res.distance;
        res.normal = normalize((r.ro+r.rd*dist) - sphere.xyz) * (fromInside ? -1.0f : 1.0f);
        return true;
    }

    return false;
}

