bool test_visibility(vec3 p1, vec3 p2)
{
    const float eps = 1e-5;

    Ray r = Ray(p1, normalize(p2 - p1));
    r.ro += eps * r.rd;

    HitResult res;
    res.distance = c_superFar;
    SceneTrace(r, res);

    if(res.distance > distance(p1, p2) - 2.0 * eps)
        return true;

    return false;
}

vec3 pathTracing(in HitResult hit)
{
    vec3 Lo = vec3(0.);
    vec3 history = vec3(1.);

    for(int bounce = 0; bounce < c_numBounces; bounce++)
    {
        vec3 V = -hit.viewDir;
        vec3 N = hit.normal;

        vec2 uv = sobolVec2(uint(iFrame+1), uint(bounce));
        uv = CranleyPattersonRotation(uv);
        vec3 L = SampleHemisphere(uv.x, uv.y);
        L = toNormalHemisphere(L, hit.normal);
        //vec3 L = toNormalHemisphere(SampleHemisphere(), hit.normal);

        float pdf = 1.0 / c_twopi;
        float cosine_o = max(0., dot(V, hit.normal));
        float cosine_i = max(0., dot(L, hit.normal));
        vec3 tangent, bitangent;
        getTangent(N, tangent, bitangent);

        vec3 f_r = brdf_Evaluate_aniso(V, N, L, tangent, bitangent, hit.material);

        Ray randomRay;
        randomRay.ro = hit.hitPoint;
        randomRay.rd = L;

        HitResult newHit;
        newHit.distance = c_superFar;
        SceneTrace(randomRay, newHit);

        if(newHit.distance == c_superFar)
        {
            Lo += texture(iChannel1, randomRay.rd).rgb * history * f_r * cosine_i / pdf;
            break;
        }

        vec3 Le = newHit.material.emissive;
        Lo += history * Le * f_r * cosine_i / pdf;
        hit = newHit;
        history *= f_r * cosine_i / pdf;
    }

    return Lo;
}

vec3 hdrColor(vec3 L) 
{
    vec3 color = texture(iChannel1, L).rgb;
    return color;
}

vec3 pathTracingImportanceSampling(HitResult hit) 
{
    vec3 Lo = vec3(0);
    vec3 history = vec3(1);

    for(int bounce = 0; bounce< c_numBounces; bounce++) 
    {
        vec3 V = -hit.viewDir;
        vec3 N = hit.normal;

        vec2 uv = sobolVec2(uint(iFrame+1), uint(bounce));
        uv = CranleyPattersonRotation(uv);
        float xi_3 = RandomFloat01();

        vec3 L = SampleBRDF(uv.x, uv.y, xi_3, V, N, hit.material);

        float NdotL = dot(N, L);
        if(NdotL <= 0.0) break;

        Ray randomRay;
        randomRay.ro = hit.hitPoint;
        randomRay.rd = L;

        HitResult newHit;
        newHit.distance = c_superFar;
        SceneTrace(randomRay, newHit);

        vec3 tangent, bitangent;
        getTangent(N, tangent, bitangent);

        vec3 f_r = brdf_Evaluate_aniso(V, N, L, tangent, bitangent, hit.material);

        float pdf_brdf = brdf_Pdf(V, N, L, hit.material);
        if(pdf_brdf <= 1e-7) break;

        if(newHit.distance == c_superFar)
        {
            vec3 color = texture(iChannel1, L).rgb;
            Lo += history * color * f_r * NdotL / pdf_brdf;
            break;
        }

        vec3 Le = newHit.material.emissive;
        Lo += history * Le * f_r * NdotL / pdf_brdf;
        hit = newHit;
        history *= f_r * NdotL / pdf_brdf;
    }
    return Lo;
}

vec3 pathTracingImportanceSampling2(in HitResult hit)
{
    vec3 Lo = vec3(0);
    vec3 history = vec3(1);

    for(int bounce = 0; bounce < c_numBounces; bounce++)
    {
        vec3 V = -hit.viewDir;
        vec3 N = hit.normal;

        {
            vec3 pos_ls = sample_light(vec2(RandomFloat01(), RandomFloat01()));
            vec3 l_nee = pos_ls - hit.hitPoint;
            float rr_nee = dot(l_nee, l_nee);
            l_nee /= sqrt(rr_nee);
            float G = max(0., dot(hit.normal, l_nee)) * max(0., -dot(l_nee, light_normal)) / rr_nee;
            vec3 L = l_nee;

            if(G > 0.0)
            {
                float light_pdf = 1.0 / (light_area * G);
                float brdf_pdf = brdf_Pdf(V, N, L, hit.material);
                float w = light_pdf / (light_pdf + brdf_pdf);

                vec3 tangent, bitangent;
                getTangent(N, tangent, bitangent);
                vec3 brdf = brdf_Evaluate_aniso(V, N, L, tangent, bitangent, hit.material);

                if(test_visibility(hit.hitPoint, pos_ls))
                {
                    vec3 Le = hit.material.baseColor;
                    Lo += history * (Le * w * brdf) / light_pdf;
                }
            }
        }

        {
            vec3 V = -hit.viewDir;
            vec3 N = hit.normal;

            vec2 uv = sobolVec2(uint(iFrame+1), uint(bounce));
            uv = CranleyPattersonRotation(uv);
            float xi_3 = RandomFloat01();

            vec3 L = SampleBRDF(uv.x, uv.y, xi_3, V, N, hit.material);

            float NdotL = dot(N, L);
            if(NdotL <= 0.0) break;

            Ray randomRay;
            randomRay.ro = hit.hitPoint;
            randomRay.rd = L;

            HitResult newHit;
            newHit.distance = c_superFar;
            SceneTrace(randomRay, newHit);

            vec3 tangent, bitangent;
            getTangent(N, tangent, bitangent);

            vec3 f_r = brdf_Evaluate_aniso(V, N, L, tangent, bitangent, hit.material);

            float pdf_brdf = brdf_Pdf(V, N, L, hit.material);
            if(pdf_brdf <= 1e-7) break;

            if(newHit.distance == c_superFar)
            {
                vec3 color = texture(iChannel1, L).rgb;
                Lo += history * color * f_r * NdotL / pdf_brdf;
                break;
            }

            vec3 Le = newHit.material.emissive;
            Lo += history * Le * f_r * NdotL / pdf_brdf;
            hit = newHit;
            history *= f_r * NdotL / pdf_brdf;
        }
    }

    return Lo;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    seed = uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);

    light_size = 8.0f;
    light_area = light_size * light_size;
    light_position = vec3(0.0f, 12.4f, 30.0f);
    light_normal = vec3(0.f, -1.f, 0.f);

    vec3 ro = vec3(0.);
    float cameraDistance = 1.0 / tan(c_FOVDegrees * 0.5f * c_pi / 180.0f);
    vec3 rt = vec3((fragCoord/iResolution.xy) * 2.0f - 1.0f, cameraDistance);

    float aspectRatio = iResolution.x / iResolution.y;
    rt.y /= aspectRatio;

    vec3 rd = normalize(rt - ro);

    vec3 color = vec3(0.);
    Ray r;
    r.ro = ro;
    r.rd = rd;

    HitResult firstHit;
    firstHit.distance = c_superFar;
    SceneTrace(r, firstHit);

    if(firstHit.distance == c_superFar)
    {
        color = texture(iChannel1, r.rd).rgb;
    }
    else
    {
        vec3 Le = firstHit.material.emissive;
        vec3 Li;
        for(int i = 0; i < c_numSPP; i++)
        {
            Li += pathTracingImportanceSampling2(firstHit);
        }
        Li /= float(c_numSPP);
        color = Le + Li;
    }

    vec3 lastFrameColor = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    color = vec3(1.0) - exp(-1.0 * color);
    color = pow(color, vec3(1.0 / 2.2));

    color = mix(lastFrameColor, color, 1.0f / float(iFrame+1));

    fragColor = vec4(color, 1.0);
}

