// Ray marching constants
#define MAX_STEPS 100
#define SURF_DIST 0.0001
#define MAX_DIST 100.0
#define PI 3.141592653592

////////////////////////////////////////////////////////////////
// Signed Distance Functions
////////////////////////////////////////////////////////////////
float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,normalize(n)) + h;
}

float sdSphere( vec3 p, vec3 c, float r )
{
    return length(p-c) - r;
}

////////////////////////////////////////////////////////////////
// SDF Operations
////////////////////////////////////////////////////////////////

float opUnion(float d1, float d2 ) { return min(d1,d2); }

float opSubtraction(float d1, float d2) {
    //NOTE: Flipped order because it makes more sense to me
    return max(-d2, d1);
}
float opIntersection(float d1, float d2) {
    return max(d1, d2);
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h);
}
float opSmoothIntersection(float d1, float d2, float k){
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

vec2 map(vec3 p) {
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;  
    float y = 0.1 * sin(iTime);
    float x = 0.2 * cos(iTime);


    vec2 res;

    // Sphere 1: red
    float s1 = sdSphere(p, vec3(0.5, y, 0.),0.6);
    res = vec2(s1, 1.0); // id to track color

    // Sphere 2: blue
    float s2 = sdSphere(p, vec3(x, -y, 0.),0.7);
    if (s2 < res.x) res.y = 2.0; // if the distance is smaller here, assign the color
    res.x = opSmoothUnion(s1, s2, 0.1); // assign after combining, since it's a union

    // Sphere 3: green
    float s3 = sdSphere(p, vec3(m.x, m.y, -1.),0.8);
    res.x = opSmoothSubtraction(res.x, s3, 0.05); // combine before assigning, since it's a subtraction
    if (s3 < res.x+SURF_DIST) res.y = 3.0; // offset slightly to prevent artifacts

   // Sphere 4: purple
    float s4 = sdSphere(p, vec3(m.x, m.y, -1.),0.2);
    if (s4 < res.x) res.y = 4.0; // if the distance is smaller here, assign the color
    res.x = opUnion(res.x, s4); // assign after combining, since it's a union


    return res;
}

vec2 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;
        mat = ds.y;
        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec2(d, mat);
}

vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching
    vec3 rt = vec3(0., 0., 0.);
    vec3 ro = vec3(0., 0., -10.02);

    vec3 rd = rayDirection(uv, ro, rt);
    vec2 d = rayMarch(ro, rd);

    // Background
    float v = length(uv) * .75;
    fragColor = vec4(mix(0.1, 0.2, smoothstep(0.0, 1.0, uv.y)));

    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 N = gradient(p);
        float light1 = dot(N, normalize(vec3(1, 1,-1)))*.5+.5;
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 color = 0.5 * vec3(light1) + 0.5 * vec3(light2);
        fragColor = vec4(color, 1.0);

        // 
        if (d.y == 1.) fragColor.yz *= 0.5;
        if (d.y == 2.) fragColor.xyz *= vec3(0.5, 0.5, 0.9);
        if (d.y == 3.) fragColor.xyz *= vec3(0.5, 0.9, 0.5);     
        if (d.y == 4.) fragColor.xyz *= vec3(0.9, 0.5, 0.9);        


    }

}

#define MAX_STEPS 100
#define MAX_DIST 100. 
#define SURF_DIST 0.01

float sdCircle(vec2 p, vec2 c, float r) {
    return length(p-c) - r;
}


// SDFs and operations from iq:
// https://iquilezles.org/articles/distfunctions2d/
float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}


float opUnion(float d1, float d2 ) { return min(d1,d2); }


// Scene SDF
float map(vec2 p) {
    float d = sdCircle(p, vec2(-0.35, 0.5*sin(0.2*iTime)), 0.25);
    d = abs(d)-0.05;
    d = opUnion(d, sdCircle(p, vec2(0.15,0.5*cos(0.1*iTime)), 0.125));
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates
    vec2 uv = (fragCoord - 0.5*iResolution .xy)/iResolution.y;
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y / 2.0;  

    // Scene distance
    float d = map(uv) * 255.;

    // Ripple frequency
    float f = 1.;

    vec3 ripples = mix(vec3(1.), vec3(0.9), smoothstep(0., 1., sin(f*d)*0.5+0.5));

    // Mixing ripples with (black) SDF
    vec3 col = mix(vec3(0.), ripples, smoothstep(0., 1., d));

    // Ray marching:

    // Ray origin
    vec2 ro = m;

    // Ray direction
    vec2 target = vec2(-0.8, 0.);
    vec2 rd = normalize(target - ro);

    float t = 0.0; // tracks total distance traveled from origin to target
    float circles = 1.; // visualization

    // Ray marching
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec2 p = ro + rd * t; // move point to new evaluation step
        float ts = map(p); // current SDF value @ p
        t += ts; // add to total distance traveled

        // circles for visualization
        circles = opUnion(circles, abs(sdCircle(uv, p, ts)+0.0001));

        if (t >= MAX_DIST || ts < SURF_DIST) break; // "hit"
        i++;
    }

    // Visuals
    circles = smoothstep(1., 0., 255. * (circles));

    float line = udSegment(uv, m, target);
    line = opUnion(line, sdCircle(uv, ro, 0.01));
    line = opUnion(line, sdCircle(uv, target, 0.01));
    line = smoothstep(1., 0., 255. *(line));

    vec3 fin = mix(col, vec3(0.4,0.7,0.5), circles);
    fin = mix(fin, vec3(0.2,0.4,0.7), line);

    fragColor = vec4(fin, 1.0);

}

const uint k = 1103515245U;

vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}



