void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set scale; sizes to y resolution
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // find points a certain distance from circle w/ .5 radius
        // float d = length(uv);
        // d = sin(d * 8.0 + iTime) / 8.0;
        // d = abs(d);

    // make a spiral (angle from x-axis: atan(y/x))
    float tightness = 1.;
    float speed = .6;
    float sufficientlyLargeFloat = 100.;
    float numPins = 8.0; // keep even
    // makes distance from center, angle from x-axis,
    // and time factors
    float d = length(uv)
        + atan(uv.y / uv.x) / tightness
        + iTime * speed
        + sufficientlyLargeFloat;
    d = abs(d);
    // makes it repeat
    d = sin(d*numPins)/8.0;

    d = smoothstep(0.0, .1, d);

    vec3 col = vec3(d,d,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926535897932384626433832795
#define threshold 0.5
#define range 0.2

float blobFunc(float dist){
    return pow(max(1. - dist, 0.), 2.);
}

vec2 path(float t){
    return vec2(sin(t*2.*PI), sin(4.*t*PI)/2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    float val1 = 0.;
    float val2 = 0.;
    float val3 = 0.;

    int points = 20;
    for(int i = 0; i < points; i++){
        float normalIndex = float(i)/float(points);
        float size = 0.1 + normalIndex*0.2;
        float time = iTime*0.5 + normalIndex*0.25;


        float pointDist1 = time;
        vec2 pos1 = path(pointDist1);
        float dist1 = length(pos1 - uv);
        val1 += blobFunc(dist1/size);


        float pointDist2 = time - 2./3.;
        vec2 pos2 = path(pointDist2);
        float dist2 = length(pos2 - uv);
        val2 += blobFunc(dist2/size);

        float pointDist3 = time - 4./3.;
        vec2 pos3 = path(pointDist3);
        float dist3 = length(pos3 - uv);
        val3 += blobFunc(dist3/size);
    }

    float shape1 = smoothstep(threshold - range, threshold, val1);
    float glow1 = (1. - shape1)*val1/threshold;

    float shape2 = smoothstep(threshold - range, threshold, val2);
    float glow2 = (1. - shape2)*val2/threshold;

    float shape3 = smoothstep(threshold - range, threshold, val3);
    float glow3 = (1. - shape3)*val3/threshold;

    col += shape1;
    col += vec3(0., 0.5, 1.)*glow1;

    col += shape2;
    col += vec3(1., 0., 0.5)*glow2;

    col += shape3;
    col += vec3(0.5, 1., 0.)*glow3;

    fragColor = vec4(col, 1.0);
}

