vec2 path(in float z) {
    float a = sin(z * .07);
    float b = cos(z * .09);
    return 3. * vec2(a + b, a - b);
}


float smoothMin(float a, float b, float c, float t){
    return -log(exp(t * -a) + exp(t * -b) + exp(t * -c)) / t;
}

float smoothMin(float a, float b, float t){
    return -log(exp(t * -a) + exp(t * -b)) / t;
}




vec3 tri(vec3 p) {
    return abs(fract(p) - .5);
}



float surf(in vec3 p) {
    return dot(tri(p * .5), vec3(.5)) - p.y*.6;
}




float map(vec3 p) {
    p.xy -= path(p.z);

    vec3 q = p * PI/2.;

    float gyr = dot(cos(q), sin(q.yzx));

    float osso =  gyr + 1.3;

    p.xy *= rot(T*.8);

    float cyl = length(p.xy) - 1.8;
    float carne = -smoothMin(cyl, gyr, 2.) + surf(p);

    id = step(carne, osso);

    //return carne;
    return osso;
    return min(carne, osso);
}


