vec3 hsb2rgb( in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return (c.z * mix( vec3(1.0), rgb, c.y));
}


vec3 color(vec3 ro, float t, vec3 rd, vec3 lightPos){

    vec3 col2 = hsb2rgb(vec3(0.02*ro.y*3.1514,0.9,0.9))*0.5;
    vec3 col3 = hsb2rgb(vec3(0.1*(ro.z + t * rd.z)*3.1514,0.9,0.9))*0.5;
    vec3 col = id < 0.5 ? col3*vec3(1.) : col2+(col3*vec3(clamp(mouse.y/mouse.x,0.1,1.0)*0.8)); //vec3(4,3,2)*.1;

    col += mouse.y/mouse.x*shadow(ro + t * rd, lightPos); 

    col = mix(/*vec3(1, .1, 0)*/col2, col, 1. / (t / MAX_DIST + .9));

    return col;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    mouse = (iMouse.xy == vec2(0.)) ? vec2(0.3,0.1) : iMouse.xy/iResolution.xy;



