#ifdef MOUSESTEERING
    vec3 lookAt = vec3(mouse.x*3.,-mouse.y*.3, walk);
#else
    vec3 lookAt = vec3(0, 1, walk);
#endif
    vec3 ro = lookAt + vec3(0, 0, -0.1);
    vec3 lightPos = ro + vec3(0, 0, 4);

    lookAt.xy += path(lookAt.z);
    ro.xy += path(ro.z);
    lightPos.xy += path(lightPos.z);

    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(vec3(forward.z, 0., -forward.x));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    float t = rayMarch(ro, rd);


    vec3 col = color(ro, t, rd, lightPos);

    fragColor = vec4(col * 2., 1.0);
}

