float Hash21(vec2 p)
{
    p = fract(p *vec2(234.34, 435.345));
    p += dot(p, p + 34.32);
    return fract(p.x * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 UV = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.);

    uv += iTime * .2;
    uv *= 5.;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    float n = Hash21(id);
    float width = .2 * UV.y;

    if (n < .5) gv.x *= -1.;

    //straight line
    //float d = abs(abs(gv.x + gv.y) - .5);
    //float mask = smoothstep(.01, -.01, d - width);

    //circle
    vec2 cUv = gv - sign(gv.x + gv.y + .001) * .5;
    float d = length(cUv);
    float mask = smoothstep(.01, -.01, abs(d - .5) - width);
    float angle = atan(cUv.x, cUv.y);
    float checker = mod(id.x + id.y, 2.) * 2. - 1.;
    float flow = sin(iTime + checker * angle * 10.);
    //col += flow * mask;
    //float x = fract(angle / 1.5707 + checker * iTime * .3);
    float x = angle / 1.5707 + checker * iTime * .3;
    float y = (d - (.5 - width)) / (2. * width);
    y = abs(y - .5) * 2.;
    vec2 tUv = vec2(x, y);
    col += texture(iChannel0, tUv).rgb * mask;
    col *= (1. - tUv.y);

    //if (gv.x > .48 || gv.y > .48)
    //{
    //    col = vec3(1., 0., 0.);
    //}
    // Output to screen
    fragColor = vec4(col, 1.0);
}

/*
This shows how to dither a Barycentric coordinate system. 
I played around with a bunch of variations and performance tested them. 
see https://www.shadertoy.com/view/msXfWs

This is a cleaned up and slightly improved version of dud31 original idea 
(https://www.shadertoy.com/view/DdlBDX)
*/

// Creative Commons Attribution-ShareAlike 4.0 International Public License
// Created by David Hoskins. May 2018
// https://www.shadertoy.com/view/XdGfRR
#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UIF (1.0 / float(0xffffffffU))
float hash12C(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * UI2;
	uint n = (q.x ^ q.y) * UI0;
	return float(n) * UIF;
}

// valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// input in pixels (ie not normalized uv)
float ScreenSpaceDither12(vec2 vScreenPos) {
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + iTime); 
    return fract( vDither / 103.0);
}

float deter(vec2 a, vec2 b) { 
    return a.x*b.y - a.y*b.x; 
}

vec3 bary(vec2 a, vec2 b, vec2 c, vec2 p) {
    float ar = deter(b - a, c - a);
    float w = deter(b - a, p - a) / ar;
    float v = deter(a - c, p - c) / ar;
    vec3 s = vec3(1.0 - v - w, v, w);
    return s;
}

vec2 rot(vec2 v, float th) {
    return vec2(v.x*cos(th) - v.y*sin(th), v.x*sin(th) + v.y*cos(th));
}

// a is 0-1 and contrast is 1-inf with 1 being original contrast
vec3 smoothContrast(vec3 a, float contrast) {
    vec3 powA = vec3(pow(a.x, contrast), pow(a.y, contrast), pow(a.z, contrast));
    return powA/(powA.x + powA.y + powA.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV coords centered on screen
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    // coordinates of corner
	vec2 v_a = vec2(1.5,1.0)*cos(iTime + vec2(0.0,1.0) + 0.0 );
	vec2 v_b = vec2(1.5,1.0)*cos(iTime + vec2(0.0,1.0) + 1.5 );
	vec2 v_c = vec2(1.5,1.0)*cos(iTime + vec2(0.0,1.0) + 3.0 );

    // 1.- distance to corner
    vec3 cDist = bary(v_a, v_b, v_c, p);

    // black mask for outside of triangle
    float mask = step(0., cDist.x) * step(0., cDist.y) * step(0., cDist.z);

    // change contrast value
    float contrast = 2.0;
    cDist = smoothContrast(cDist, contrast); 

    // noise pattern with range 0-1
    //float rnd = hash12C(p); // random dither
    float rnd = ScreenSpaceDither12(fragCoord.xy); // valve animated dither
    // smooth contrast creates artifacts in the dither. Clamp on the dither removes it
    rnd = clamp(rnd, 0.01, 0.99); 

    //apply float dither to three values of cDist
    vec3 col;
    if (cDist.x > rnd) {
        col = vec3(1, 0, 0);
    } else if (1.-cDist.z > rnd) {
        col = vec3(0, 1, 0);
    } else {
        col = vec3(0, 0, 1);
    }    

    // apply mask
    col = col * mask;

	fragColor = vec4(col, 1.);

    // test if all three values togethere are still 1.
    float sumCheck = fract(col.x + col.y + col.z);
    //fragColor = vec4(sumCheck);
}

// "Biplanes in the Badlands 2" by dr2 - 2023
