// The Red Baron's personal flyer (and Snoopy's nemesis)

#define AA  0   // (= 0/1) optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float PrConeDf (vec3 p, vec3 b);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float HexEdgeDist (vec2 p);
float Ssign (float x);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float Minv3 (vec3 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Hashfv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define PLEN    17
#define N_FLYER 6

mat3 flyerMat[N_FLYER];
vec4 cPath[PLEN];
vec3 flyerPos[N_FLYER], qHit, sunDir;
vec2 cIdS, cMidS, cIdB, cMidB;
float dstFar, tCur, tEnd[PLEN + 1], hgSizeB, hgSizeS, tLen, cDir, cType, colHt, flSzFac;
int nFrame, idObj, idObjGrp;
bool isSh;
const int idFus = 1, idPipe = 2, idWngM = 3, idStrut = 4, idWngT = 5, idTail = 6, idLeg = 7,
   idWhl = 8, idNose = 9, idGun = 10, idPlt = 11, idWal = 12, idGrnd = 13;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

// When is a biplane not a biplane? (from "Snoopy and the Red Baron 2")

float FlyerDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 ws;
  float d, wr, h, qy;
  int ig;
  ig = idObjGrp / 256;
  dMin /= flSzFac;
  p /= flSzFac;
  if (! isSh) d = PrRoundBoxDf (p, vec3 (1.2, 0.5, 1.1), 0.02);
  if (isSh || d < dMin) {
    q = p;
    wr = -0.2 + q.z;
    d = PrRoundBoxDf (q, vec3 (0.07 * (1. - 0.8 * wr * wr),
       0.11 * (1. - 0.6 * wr * wr), 1.), 0.05);
    d = max (d, - PrRoundBoxDf (q - vec3 (0., 0.16, 0.), vec3 (0.04, 0.03, 0.07), 0.04));
    DMINQ (idObjGrp + idFus);
    q = p;
    q.yz -= vec2 (0.17, 0.5);
    d = PrCylDf (q, 0.015, 0.15);
    DMINQ (idObjGrp + idGun);
    q = p;
    q.y -= 0.17;
    d = PrSphDf (q, 0.04);
    DMINQ (idObjGrp + idPlt);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.1, 0.03, 0.8);
    d = PrCapsDf (q, 0.02, 0.15);
    DMINQ (idObjGrp + idPipe);
    q = p;
    q.yz -= vec2 (0.03, 1.1);
    d = PrConeDf (q, vec3 (0.8, 0.6, 0.06));
    DMINQ (idObjGrp + idNose);
    q = p;
    q.yz -= vec2 (((ig == 1) ? 0.12 : 0.09), 0.4);
    qy = q.y;
    if (ig == 1) q.y += 0.12;
    h = (ig == 1) ? 0.24 : 0.42;
    q.y = mod (q.y, h) - 0.5 * h;
    d = min (1.2 - abs (q.x), abs (PrBox2Df (vec2 (abs (q.x) - 1., q.z + 0.2), vec2 (0.25, 0.05))));
    wr = 0.47;
    h = (q.z - 0.1) / wr;
    d = max (SmoothMax (PrCaps2Df (vec2 (q.y, q.z + 0.25), 0.03 * (wr - h * h), wr), - d, 0.01),
       - q.z - 0.22);
    d = max (d, abs (qy) - 0.26);
    q.y = qy;
    DMINQ (idObjGrp + idWngM);
    q = p;
    q.yz -= vec2 (((ig == 1) ? 0.12 : 0.09), 0.42);
    q.xz = abs (q.xz) - vec2 (0.5, 0.1);
    d = PrCylDf (q.xzy, 0.01, ((ig == 1) ? 0.24 : 0.21));
    DMINQ (idObjGrp + idStrut);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.1, ((ig == 1) ? 0.24 : 0.15), 0.42);
    d = PrCylDf (q.xzy, 0.01, ((ig == 1) ? 0.12 : 0.15));
    DMINQ (idObjGrp + idStrut);
    q = p;
    q.z -= -0.9;
    wr = 0.33;
    h = (q.z - 0.05) / wr;
    q.x = abs (q.x);
    d = min (0.42 - q.x, abs (q.z + 0.06));
    d = max (SmoothMax (PrCaps2Df (vec2 (q.y, q.z + 0.18), 0.03 * (wr - h * h), wr), - d, 0.01),
       - q.z - 0.14);
    d = max (d, 0.02 - 1.5 * q.x - q.z);
    DMINQ (idObjGrp + idWngT);
    q = p;
    wr = 0.28;
    q.yz -= vec2 (-0.1, -0.98);
    h = (q.z - 0.1) / wr;
    d = min (0.24 - abs (q.y - 0.3), abs (PrBox2Df (vec2 (q.y - 0.35, q.z + 0.02), vec2 (0.16, 0.04))));
    d = SmoothMax (PrCaps2Df (vec2 (q.x, dot (q.zy, sin (0.1 * pi + vec2 (0.5 * pi, 0.)))),
       0.03 * (wr - h * h), wr), - d, 0.01);
    d = max (d, - q.z - 0.04);
    DMINQ (idObjGrp + idTail);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.135, -0.24, 0.7);
    q.xy = Rot2D (q.xy, -0.55);
    q.yz = Rot2D (q.yz, 0.15);
    d = PrCylDf (q.xzy, 0.012, 0.12);
    DMINQ (idObjGrp + idLeg);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.22, -0.34, 0.715);
    d = PrCylDf (q.yzx, 0.01, 0.035);
    DMINQ (idObjGrp + idLeg);
    q.x -= 0.01;
    d = PrCylDf (q.yzx, 0.1, 0.015);
    DMINQ (idObjGrp + idWhl);
  } else dMin = min (dMin, d);
  return flSzFac * dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  for (int k = VAR_ZERO; k < N_FLYER; k ++) {
    idObjGrp = ((k < N_FLYER - 1) ? 2 : 1) * 256;
    dMin = FlyerDf (flyerMat[k] * (p - flyerPos[k]), dMin);
  }
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar / flSzFac;
  for (int k = VAR_ZERO; k < N_FLYER; k ++) {
    idObjGrp = ((k < N_FLYER - 1) ? 2 : 1) * 256;
    q = flyerMat[k] * (p - flyerPos[k]) / flSzFac - vec3 (0., 0.02, 1.07);
    d = PrCylDf (q, 0.3, 0.007);
    DMINQ (0);
  }
  return flSzFac * dMin;
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 40; j ++) {
    d = TrObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > 30.) break;
  }
  return 0.7 + 0.3 * sh;
}

float Insig (vec2 p, int ig)
{
  float s;
  if (ig == 2) s = (1. - SmoothBump (0.08, 0.12, 0.005, length (p))) *
     (1. - SmoothBump (-0.01, 0.03, 0.005, length (p)));
  else s = 1. - SmoothBump (0.03, 0.06, 0.005, length (abs (p) - 0.07));
  return s;
}

vec4 FlyerCol (int ig, int id, vec3 vn)
{
  vec4 col4;
  vec2 w;
  float s;
  col4 = (ig == 2) ? vec4 (0.1, 1., 0.1, 0.2) : vec4 (1., 0.1, 0.1, 0.2);
  if (id == idFus) {
    if (qHit.z > 1.01) {
      col4 = vec4 (0.9, 0.9, 0.9, 0.3);
      if (length (abs (qHit.xy + vec2 (0., 0.05)) * vec2 (0.6, 1.)) < 0.03) col4.rgb *= 1. -
         0.7 * SmoothBump (0.2, 0.8, 0.05, mod (64. * ((ig == 1) ? qHit.y : qHit.x), 1.));
    }
    w = abs (qHit.xz) - vec2 (0.09, 0.12);
    if (abs (qHit.y - 0.1) < 0.04 && Maxv2 (w) < 0.) col4.rgb = vec3 (0.2, 0.15, 0.05);
    col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (1.5 * qHit.yz + vec2 (0., 0.4), ig));
  } else if (id == idWngM) {
    col4.rgb = mix (col4.rgb, 1. - col4.rgb, step (0., qHit.z - 0.14 - 0.07 *
       abs (sin ((8./1.2) * pi * qHit.x))));
    s = 1.2 / 8.;
    s = mod (qHit.x + 0.5 * s, s) - 0.5 * s;
    col4.rgb *= 1. + 0.1 * SmoothBump (-0.01, 0.01, 0.002, s);
    if (abs (qHit.y) > 0.1 && qHit.y * vn.y > 0.) {
      col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (vec2 (abs (qHit.x) - 0.9, qHit.z), ig));
    }
  } else if (id == idWngT) {
    col4.rgb = mix (col4.rgb, 1. - col4.rgb, step (0., qHit.z - 0.11 - 0.03 *
       abs (sin (17. * pi * qHit.x))));
    s = 1. / 17.;
    s = mod (qHit.x + 0.5 * s, s) - 0.5 * s;
    col4.rgb *= 1. + 0.1 * SmoothBump (-0.005, 0.005, 0.001, s);
  } else if (id == idTail) {
    col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (2.5 * qHit.yz + vec2 (-0.9, -0.25), ig));
  } else if (id == idNose) {
    col4 = (qHit.z > -0.04) ? mix (col4, vec4 (1., 1., 1., 0.5), 0.5) : vec4 (0.1);
  } else if (id == idPipe) {
    col4 = vec4 (0.8, 0.8, 0.85, 0.3) * (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.05,
       mod (16. * qHit.z, 1.)));
  } else if (id == idStrut || id == idLeg) {
    col4.rgb = 0.6 * col4.rgb + vec3 (0.4);
  } else if (id == idGun) {
    col4.rgb = vec3 (0.6, 0.6, 0.7);
  } else if (id == idPlt) {
    col4 = vec4 (0.4, 0.2, 0.1, 0.);
    if (qHit.z > 0.02 && qHit.y < 0.03) col4.rgb = (ig == 2) ? vec3 (0.9) : vec3 (0.6, 0.4, 0.4);
    if (qHit.z > 0. && length (vec2 (abs (qHit.x) - 0.011, qHit.y - 0.015)) < 0.01)
       col4 = vec4 (0., 0.2, 0., 0.2);
    if (qHit.z > 0. && ig == 2 && length (qHit.xy - vec2 (0., -0.02)) < 0.012)
       col4.rgb = vec3 (0.1);
  } else if (id == idWhl) {
    if (length (qHit.yz) > 0.07) col4 = vec4 (0.05);
  }
  return col4;
}

// (from "Truchet Path Journey")

float Ddot (vec2 p)
{
  return dot (p, p);
}

vec3 TruchSDist (vec2 p)
{
  vec2 pc, pc1, pc2, dp;
  float d, d1, d2, cxy, rc, ac, s, ss;
  bool ct;
  ct = (cType == 2. || cType == 4.);
  if (ct) {
    pc1 = - vec2 (0., cDir);
    pc2 = vec2 (sqrt3/2. * Ssign (p.x), 0.5 * cDir);
    d1 = Ddot (p - pc1);
    d2 = Ddot (p - pc2);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = 0.5;
    d = abs (sqrt (d) - rc);
  } else {
    if (cDir != 0.) p = 0.5 * vec2 (p.x - cDir * sqrt3 * p.y, cDir * sqrt3 * p.x + p.y);
    pc1 = vec2 (sqrt3/2. * Ssign (p.x), 0.);
    pc2 = vec2 (sqrt3 * Ssign (p.x), 0.);
    d1 = sqrt (Ddot (p - pc1));
    d2 = abs (sqrt (Ddot (p - pc2)) - 1.5);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = (d == d1) ? 0.: 1.5;
  }
  dp = p - pc;
  cxy = cIdB.x - cIdB.y;
  s = (ct && cxy < 0. || ! ct && abs (cxy - 2.) == 1.) ? -1. : 1.;
  ac = (0.5 - atan (dp.y, - dp.x) / (2. * pi)) * s;
  if (! ct && abs (cxy - 2.) <= 1.) ac += 1./6.;
  ss = Ssign (length (dp) - rc);
  return vec3 (d * ss, rc * ss * s, ac);
}

void SetPath ()
{
  float ts, tl;
  ts = 1.;
  tl = 1.5;
  cPath[ 0] = vec4 (0., 0., -1./6., tl);
  cPath[ 1] = vec4 (1., 0., 1./3., - ts);
  cPath[ 2] = vec4 (0., 1., 5./6., tl);
  cPath[ 3] = vec4 (-1., 1., -1./3., ts);
  cPath[ 4] = vec4 (-1., 2., 1., ts);
  cPath[ 5] = vec4 (0., 1., - 1./6., tl);
  cPath[ 6] = vec4 (1., 1., 1./6., tl);
  cPath[ 7] = vec4 (1., 2., 1., ts);
  cPath[ 8] = vec4 (2., 1., 1./2., - tl);
  cPath[ 9] = vec4 (2., 0., -1./2., tl);
  cPath[10] = vec4 (3., -1., -1./6., tl);
  cPath[11] = vec4 (4., -1., 1./3., - ts);
  cPath[12] = vec4 (3., 0., -1./2., - tl);
  cPath[13] = vec4 (3., 1., 1./2., tl);
  cPath[14] = vec4 (2., 2., 5./6., tl);
  cPath[15] = vec4 (1., 2., -1./3., ts);
  cPath[16] = vec4 (1., 3., 1., ts);
  tEnd[0] = 0.;
  for (int k = 0; k < PLEN; k ++) tEnd[k + 1] = tEnd[k] + abs (cPath[k].w);
  tLen = tEnd[PLEN];
}

vec2 EvalPPos (float t)
{
  vec4 cp;
  vec2 tp, vd;
  float tt, r, a, dc;
  t /= 3.;
  tp = floor (t / tLen) * vec2 (2.);
  t = mod (t, tLen);
  for (int k = 0; k < PLEN; k ++) {
    if (t >= tEnd[k] && t < tEnd[k + 1]) {
      cp = cPath[k];
      tt = 2. * (t - tEnd[k]) / (tEnd[k + 1] - tEnd[k]) - 1.;
      break;
    }
  }
  tp += cp.xy;
  if (abs (cp.w) == 1.5) {
    r = 1.5;
    dc = sqrt3;
    a = pi / 6.;
    tt *= Ssign (cp.w);
  } else {
    r = 0.5;
    dc = 1.;
    a = - Ssign (cp.w) * pi / 3.;
  }
  vd = vec2 (-1., 1.) * sin (pi * cp.z + vec2 (0., 0.5 * pi));
  return (HexToPix (tp) + dc * vd - r * Rot2Cs (vd, sin (tt * a + vec2 (0.5 * pi, 0.)))) * hgSizeB;
}

void BConf ()
{
  float h, cxy;
  cMidB = HexToPix (cIdB * hgSizeB);
  h = Hashfv2 (cIdB);
  if (Hashfv2 (17.11 * cIdB) > 0.4) {
    cDir = floor (3. * h) - 1.;
    cType = 3.;
  } else {
    cDir = 2. * floor (2. * h) - 1.;
    cType = 4.;
  }
  cxy = cIdB.x - cIdB.y;
  if (cxy == 0.) cType = 1.;
  else if (abs (cxy) == 1.) cType = mod (cIdB.x, 2.) + 1.;
  else if (cxy == -2. || cxy == -3. || cxy == 5.) cType = 2.;
  else if (cxy == 2. || cxy == 3. || cxy == 4.) cType = 1.;
  if (cType <= 2.) {
    if (cType == 1. && (cxy == 1. || cxy == 2. || cxy == 3.)) cDir = 0.;
    else if (cType == 1. && cxy == 4.) cDir = -1.;
    else if (cType == 2. && cxy == 5.) cDir = 1.;
    else cDir = 2. * mod (cIdB.x, 2.) - 1.;
  }
}

void SConf ()
{
  vec2 q;
  float h, hh, s;
  cMidS = HexToPix (cIdS * hgSizeS);
  cIdB = PixToHex (cMidS / hgSizeB);
  BConf ();
  q = (cMidS - cMidB) / hgSizeB;
  s = min (hgSizeB * abs (TruchSDist (q).x) - 6., hgSizeB * abs (HexEdgeDist (q)));
  if (s > 0.) {
    h = Fbm2 (0.05 * cMidS * vec2 (1., 0.5));
    hh = h * h;
    colHt = 1. + (2. * h + 2. * hh + 80. * hh * hh) * smoothstep (0., 4., s);
    colHt = min (colHt, 40.);
    if (colHt > 0.1) colHt = 0.5 * (floor (2. * colHt + 0.5) - 0.5);
  } else colHt = 0.;
}

float SObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p - vec3 (cMidS, colHt).xzy;
  d = SmoothMax (length (q.xz) - hgSizeS * sqrt3/2. - 0.01, q.y, ((colHt > 0.3) ? 0.3 : 0.001));
  DMINQ (idWal);
  q = p;
  d = q.y - 0.05;
  DMINQ (idGrnd);
  return dMin;
}

float SObjRay (vec3 ro, vec3 rd)
{  // (from "Hexagonally Discretized Waves")
  vec3 vri, vf, hv;
  vec2 edN[3], pM;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.01;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= Ssign (dot (edN[k], rd.xz));
  vri = hgSizeS / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSizeS;
  dHit = 0.;
  cIdS = PixToHex (ro.xz / hgSizeS);
  pM = HexToPix (cIdS);
  cNu = true;
  for (int j = VAR_ZERO; j < 420; j ++) {
    if (cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      SConf ();
      cNu = false;
    }
    d = SObjDf (ro + dHit * rd);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cIdS = PixToHex ((ro.xz + dHit * rd.xz) / hgSizeS);
      pM += sqrt3 * edN[(s == hv.x) ? 0 : ((s == hv.y) ? 1 : 2)];
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 SObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = SObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (4. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, cg4;
  vec3 col, vn, roo, bgCol;
  vec2 vf;
  float dstObj, dstObjS, sh, c;
  int ig, id;
  roo = ro;
  dstObj = ObjRay (ro, rd);
  dstObjS = SObjRay (ro, rd);
  bgCol = SkyCol (rd);
  col = bgCol;
  if (min (dstObj, dstObjS) < dstFar) {
    ro += min (dstObj, dstObjS) * rd;
    if (dstObj < dstObjS) {
      vn = ObjNf (ro);
    } else {
      vn = SObjNf (ro);
      dstObj = dstObjS;
    }
    if (dstObj == dstObjS) {
      c = Fbm2 (ro.xz);
      cg4 = vec4 (0.6, 0.4, 0., 0.) * (0.8 + 0.2 * c);
      if (idObj == idWal) {
        col4 = vec4 (0.9, 0.5, 0.2, 0.1) * (0.8 + 0.2 * smoothstep (4., 20., ro.y));
        col4 *= 1. - 0.1 * smoothstep (0.4, 0.47, abs (fract (2. * (ro.y + 0.1) + 0.5) - 0.5));
        if (vn.y > 0.2) col4 *= (0.8 + 0.2 * c) * (1. - 0.3 * smoothstep (0.2, 0.5, vn.y));
        col4 = mix (cg4, col4, smoothstep (1., 2., ro.y));
        col4 = mix (col4, vec4 (1., 1., 1., 0.5), smoothstep (20., 35., ro.y));
        vf = vec2 (4., 2.);
      } else if (idObj == idGrnd) {
        col4 = cg4 * (0.98 + 0.02 * cos (pi * hgSizeB * TruchSDist ((ro.xz - cMidB) /
           hgSizeB).x));
        vf = vec2 (1., 1.);
      }
      vn = VaryNf (vf.x * ro, vn, vf.y * (1. - smoothstep (0.2, 0.5, dstObj / dstFar)));
    } else {
      ig = idObj / 256;
      id = idObj - 256 * ig;
      col4 = FlyerCol (ig, id, flyerMat[ig] * vn);
    }
    sh = ObjSShadow (ro + 0.01 * vn, sunDir);
    col = col4.rgb * (0.25 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
       0.75 * sh * max (dot (vn, sunDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
    col = mix (col, bgCol, 1. - exp (min (0., 2. - 5. * dstObj / dstFar)));
  }
  if (TrObjRay (roo, rd) < min (dstObj, dstFar)) col = mix (col, mix (vec3 (1.), vec3 (0.8),
     step (0.02, abs (length (qHit.xy) - 0.26))), 0.2);
  return clamp (col, 0., 1.);
}

float EvalPHt (vec2 p)
{
  return 2. + 5. * Fbm2 (0.01 * p);
}

void FlyerPM (float s, float flDir, out vec3 flPos, out mat3 flMat)
{
  vec3 p, pf, pb, vel, acc, va, ori, ca, sa;
  float ds, vm, az, el, rl;
  ds = 0.3;
  p.xz = EvalPPos (s);
  p.y = 0.;
  flPos = p;
  flPos.y = EvalPHt (p.xz);
  pf.xz = EvalPPos (s + ds);
  pf.y = 0.;
  pb.xz = EvalPPos (s - ds);
  pb.y = 0.;
  vel = (pf - pb) / (2. * ds);
  acc = (pf + pb - 2. * p) / (ds * ds);
  vm = length (vel);
  va = cross (acc, vel) / vm;
  az = atan (vel.z, vel.x) - 0.5 * pi;
  el = - sin (vel.y) / vm;
  rl = 0.03 * sign (flDir) * length (va) * sign (va.y);
  if (flDir < 0.) az += pi;
  else rl += 0.06 * sin (0.5 * pi * tCur);
  ori = vec3 (el, az, rl);
  ca = cos (ori);
  sa = sin (ori);
  flMat = mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
          mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
}

#define N_WIN  2

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, col, vd, p;
  vec2 canvas, uv, uvv, mMid[N_WIN], ut[N_WIN], mSize, msw;
  float el, az, zmFac, asp, sr, t, tGap, trVel, flDir;
  int vuId, regId;
  bool sWin;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur = mod (tCur, 1200.) + 10. * floor (dateCur.w / 3600.) + 10.;
  asp = canvas.x / canvas.y;
  sWin = true;
  if (sWin) {
    mSize = (1./6.) * vec2 (asp, 1.);
    mMid[0] = (1. - mSize.y) * vec2 (asp, -1.);
    mMid[1] = (1. - mSize.y) * vec2 (- asp, -1.);
    for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  }
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    if (sWin) {
      for (int k = 0; k < N_WIN; k ++) {
        msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
        if (Maxv2 (abs (msw)) < mSize.y) {
          regId = k + 1;
          msw /= 2. * mSize.y;
          break;
        }
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  if (sWin) {
    for (int k = 0; k < N_WIN; k ++) {
      if (Maxv2 (ut[k]) < 0.) {
        uv = (uv - mMid[k]) / mSize.y;
        vuId = k + 1;
        break;
      }
    }
    if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  }
  hgSizeB = 64.;
  hgSizeS = 1.;
  SetPath ();
  flSzFac = 2.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && vuId == regId) {
    az += 2. * pi * msw.x;
    el += pi * msw.y;
  }
  tGap = 7.;
  trVel = 0.5;
  for (int k = 0; k < N_FLYER; k ++) {
    flDir = (k < N_FLYER - 1) ? -1. : 1.;
    t = (flDir < 0.) ? tGap * (floor (trVel * tCur / tGap + 0.5) - fract (tCur / tGap) -
       0.5 * float (N_FLYER - 1)) : trVel * tCur;
    FlyerPM (t + ((flDir < 0.) ? tGap * float (k + 1) : 0.), flDir, flyerPos[k], flyerMat[k]);
    if (flDir > 0.) flyerPos[k].y += 6.;
  }
  if (vuId <= 1 && length (uv * vec2 (1. / asp, 1.)) < 1.5 * SmoothBump (0.25, 0.75, 0.01,
     fract (tCur / 60.))) vuId = 1 - vuId;
  t = trVel * tCur;
  if (vuId == 0 || vuId == 1) {
    ro.xz = EvalPPos (t - 0.8 * ((vuId == 0) ? 1. : -1.));
    ro.y = EvalPHt (ro.xz) + 10.;
    p.xz = EvalPPos (t - 0.6 * ((vuId == 0) ? 1. : -1.));
    p.y = EvalPHt (p.xz) + 8.;
    p = flyerPos[N_FLYER - 1];
    p.y -= 4.;
    vd = normalize (p - ro);
    az += atan (vd.z, - vd.x) - 0.5 * pi;
    el += 0.5 * asin (vd.y);
    el = clamp (el, -0.3 * pi, 0.1 * pi);
    zmFac = 1.8 * (1. + 2. * SmoothBump (0.25, 0.75, 0.2, fract (0.03 * tCur)));
    dstFar = 300.;
  } else if (vuId == 2) {
    ro = vec3 (0., 80., (2. / tLen) * t * hgSizeB);
    ro.xz = Rot2D (ro.xz, - pi / 3.);
    ro.x -= 1.5 * hgSizeB;
    p = flyerPos[N_FLYER - 1];
    p.y -= 4.;
    vd = normalize (p - ro);
    az = 0.3 * az + atan (vd.z, - vd.x) - 0.5 * pi;
    el = 0.3 * el + asin (vd.y);
    el = clamp (el, -0.4 * pi, -0.12 * pi);
    zmFac = 5.;
    dstFar = 400.;
  }
  vuMat = StdVuMat (el, az);
  sunDir = normalize (vec3 (0., 1., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 2. * pi * sin (0.005 * pi * tCur));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
       pi)) / zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (sWin) {
    for (int k = 0; k < N_WIN; k ++) {
      if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.4, 0.6, 0.4);
    }
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (vec3 (p.xy, sign (p.z) * (max (0., abs (p.z) - h)))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (vec2 (p.x, sign (p.y) * (max (0., abs (p.y) - h)))) - r;
}

float PrConeDf (vec3 p, vec3 b)
{
  return max (dot (vec2 (length (p.xy), p.z), b.xy), abs (p.z) - b.z);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), 1.5 * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Ssign (float x)
{
  return (x != 0.) ? sign (x) : 1.;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

// Rotation matrix 
mat2 rotateMatrix(float angle) {
  return mat2(cos(angle + vec4(0,11,33,0))); 
}

// Convert to ring coordinates
vec3 toRingCoords(vec3 point, float radius) {
  return 4.0 * vec3(length(point.xz) - radius, point.y, radius * atan(point.x, point.z));
} 

// Truss structure distance field
float trussGeometry(vec3 position) {

  position.xy = length(position.xy) * 
    cos(mod(atan(position.y, position.x) + 1.05, 2.1) - 1.05 + vec2(0,11));

  position.z = mod(position.z, 2.0) - 1.0;

  position.x -= 0.5;

  return min(
      length(vec2(position.x, abs(position.y) - 0.87)) - 0.1,
      max(
        length(vec2(position.x, 0.87 * abs(position.z) + 0.5 * position.y - 0.5)) - 0.1,
        abs(position.y) - 0.9
      )
  );
}

void mainImage(out vec4 outColor, vec2 fragCoord) {

  float rayDistance = 9.0;
  float radius;

  vec3 resolution = iResolution;
  vec3 rayDirection = normalize(vec3(fragCoord, -2.0 * resolution.y) - resolution);
  vec3 rayOrigin = vec3(10, 6, 25);

  vec3 cameraPos = mix(
    3.0 * cos(0.3 * iTime + vec3(0, 11, 0)), // Camera position - demo mode
    6.0 * iMouse.xyz / resolution - vec3(0, 3, 0), // Camera position - interactive
    step(0.0, iMouse.z) // Mix factor
  );

  // Ray march
  for(outColor = vec4(1); outColor.x > 0.0 && rayDistance > 0.01; outColor -= 0.01) {

    vec3 position = rayOrigin;

    // Rotate in YZ plane
    position.yz *= rotateMatrix(-cameraPos.y);

    // Rotate in XZ plane
    position.xz *= rotateMatrix(-cameraPos.x); 

    vec3 beamPosition = position;

    // Longitudinal beams
    beamPosition.xy = length(beamPosition.xy) * 
      cos(mod(atan(beamPosition.y, beamPosition.x) + 0.314, 0.628) - 0.314 + vec2(0, 11));
    beamPosition.x += 5.0;
    beamPosition = toRingCoords(beamPosition, 10.0);
    rayDistance = trussGeometry(beamPosition);

    // Latitudinal beams
    radius = sqrt(1.0e2 - position.z * position.z) - 5.0;
    position.z = mod(position.z, 3.0) - 1.5; 
    beamPosition = toRingCoords(position.xzy, radius);
    rayDistance = min(rayDistance, trussGeometry(beamPosition));


    rayOrigin += 0.1 * rayDistance * rayDirection; // March ray
  }

  outColor *= outColor*outColor*outColor*2.0; // Color scheme
}

// CC0: Shader meta ball intro
//  Created this to show to hacker friend how to do some simple meta ball like effect
//  in a fragment shader
#define TIME        iTime
#define RESOLUTION  iResolution

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// Distance field to circle
float circle(vec2 p, float r) {
  return length(p) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;  // Range: 0..1
  vec2 p = -1. + 2. * q; // Range: -1..1
  // Adjust for screen ratio
  p.x *= RESOLUTION.x/RESOLUTION.y;

  // Approximation of pixel size, used for smooth steping the transparency
  //  at the border of the distance field to reduce aliasing effects
  float aa = 4.0/RESOLUTION.y;

  // The final distance field
  float d = 1E4;

  // Let's do 5 balls
  for (float i = 0.0; i < 5.0; ++i) {
    // A sinusoidal movement of the balls
    vec2 off = sin(vec2(1.0, sqrt(0.5))*TIME+i);
    // Compute the distance from this ball
    float dd = circle(p - off, 0.33);
    if (p.y < -1.0+2.0*smoothstep(-0.5, 0.5, sin(TIME))) {
      // On the bottom we create the final distance field by taking the union 
      //  of the balls shapes using the min function
      // This make it looks like 5 distinct balls occassionally overlapping
      d = min(d, dd);
    } else {
      // On the top though we that the union using the powerful soft min function
      //  This gives a more meta ball look
      d = pmin(d, dd, 1.0);
    }
  }

  vec3 col = vec3(0.0);
  // Add a bit of a glow effect around the meta balls
  //  simplest way I found the to do is just use the inverted distance + finetuning
  //  constants
  col += vec3(1.0, 0.25, 0.125)*0.0001/max(d*d, 0.00001);
  float a = 10.0*sqrt(abs(d))+TIME;
  // Compute the ball color using this simple yet appealing way to generate
  //  a palette of colors
  vec3 bcol = 0.5*(1.0+cos(0.5*vec3(0.0, 1.0, 2.0)+a));
  // Compute the transparency using the smoothstep function to reduce aliasing effects
  float t = smoothstep(0.0, -aa, d);
  // Mix the background color and the ball color using the transparency
  col = mix(col, bcol, t);
  // Distance fields makes certain operations simple
  //  Take the abs() produces the outline of a shape and by subtracting with a 
  //  number we give the outline a width
  float od = abs(d)-0.005;
  // Compute the transparency for the outline 
  // Mix the background color and the outline color using the outline transparency
  float ot = smoothstep(0.0, -aa, od);
  col = mix(col, vec3(1.0), ot);

  // Approximative mapping of RGB to sRGB
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}

struct sphere {
vec3 center;
float r;
vec3 color;
};

vec4 bgcolor = vec4(0.53, 0.81, 0.98, 1);

#define MAX_SPHERES 4
#define PI 3.14

sphere spheres[MAX_SPHERES];

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float sphereDist(vec3 pos, sphere s) {
   return length(pos - s.center) - s.r;
}

float distScene(vec3 pos) {
    float dist = sphereDist(pos, spheres[0]);
    for (int i = 1; i < MAX_SPHERES; i++) {
        if (spheres[i].r > 0.0) {
          dist = smin(dist, sphereDist(pos, spheres[i]), 0.3);
        }
    }
    return dist;
}


vec3 colorScene(vec3 pos) {
    vec3 color = vec3(0);
    for (int i = 0; i < MAX_SPHERES; i++) {
        if (spheres[i].r > 0.0) {
          float dist = sphereDist(pos, spheres[i]);
          color += spheres[i].color * max((1.0 - dist/0.3), 0.0);
        }
    }
     return color;
}


vec3 trace(vec3 ray, vec3 pos) {
   ray = normalize(ray);
   float dist = distScene(pos);
   int steps = 128;
   for (int i = 0; i < steps; i++) {
      pos = pos + ray*dist;
      dist = distScene(pos);
   }
   return pos;
}

vec3 getNormal(vec3 pos) {
   float dist = distScene(pos);
   vec2 e = vec2(0.01, 0.0);
   vec3 n = dist - vec3(distScene(pos - e.xyy), distScene(pos - e.yxy), distScene(pos - e.yyx));
   return normalize(n);
}

vec4 shade(vec3 pos, vec3 light, vec3 camera) {
    vec3 sunDir = pos - light;
    sunDir = normalize(sunDir);

    vec3 normal = getNormal(pos);
    float ambiant = 0.2;
    float diffuse = clamp(dot(-sunDir, normal), 0.0, 1.0) * 0.5;
    vec3 R = normalize(2.0 * dot(normal, -sunDir) * normal + (sunDir)); 
    float specular = pow(clamp(dot(R, normalize(camera - pos)), 0.0, 1.0), 8.0) * 2.0; 

    float val = ambiant + diffuse + specular;
    return vec4(colorScene(pos) * val, 1.0) + ambiant * bgcolor * 0.5;
}

#define AA_SAMPLES 4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camera = vec3(5.0*sin(iTime*0.7), 0, -5.0*cos(iTime*0.7));
    vec3 up = vec3(0, 1, 0);
    vec3 lightPos = vec3(-2.5, 1, -1);

    spheres[0].center = vec3(0.5, 0, cos(iTime*1.2)+0.2);
    spheres[0].r = 0.5;
    spheres[0].color = vec3(1.0, 0.0, 0.0);

    spheres[1].center = vec3(-0.5 + sin(iTime*2.0) * 1.2, 0, 0);
    spheres[1].r = 0.3;
    spheres[1].color = vec3(0.0, 1.0, 0.0);


    spheres[2].center = vec3(0, sin(iTime) * 0.8+0.3, 0);
    spheres[2].r = 0.4;
    spheres[2].color = vec3(0.0, 0.0, 1.0);


    spheres[3].center = vec3(0, sin(iTime) * 0.8+0.3, sin(iTime)*0.66 + 0.2);
    spheres[3].r = 0.1;
    spheres[3].color = vec3(0.0, 1.0, 1.0);

    vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);

    vec4 outcolor = vec4(0.0);

    for (int i = 0; i < AA_SAMPLES; i++) {
        vec2 jitter = vec2(sin(2.0*PI*float(i) / float(AA_SAMPLES)), cos(2.0*PI*float(i) / float(AA_SAMPLES)))*1.75;

        vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);
        uv += jitter/iResolution.xx;

        uv = uv * 2.0;
        uv = uv * iResolution.xy;
        uv = uv / iResolution.yy;

        vec3 right = cross(camera, up);
        right = normalize(right);

        vec3 ray = right*uv.x + up*uv.y - camera;

        vec3 tracePos = trace(ray, camera);


        if (distScene(tracePos) < 0.1) {
          outcolor += shade(tracePos, lightPos, camera) / float(AA_SAMPLES);
        } else {
          outcolor += bgcolor / float(AA_SAMPLES);
        }
    }

      fragColor = outcolor;
}

// NOTE! The purpose of this ShaderToy is to show HOW an algorithm can improve the performance
// of terrain generation, however, I can't think of a way that I could implement the algorithm
// in ShaderToy where it's effectively applied. Therefore, this ShaderToy is just a visualization
// of the algorithm.

// You can change these!
const uint MAX_LOD = 12u;
const uint LOD_STEP = 1u;

float WORLD_SCALE;

const bool USE_NAIVE_METHOD = true; // pointless variable, just exists as a explanatory device

const bool SHOW_ESTIMATED_SURFACE_VOXELS = false;

const vec3 BLUE = vec3(0.357, 0.612, 0.882);
const vec3 ORANGE = vec3(0.969, 0.663, 0.133);

vec2 pixel_space_to_world_space(vec2 pixel_p) {
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = pixel_p / iResolution.xy * 2.0 - 1.0;
    uv.x *= aspect;
    uv *= WORLD_SCALE;
    return uv;
}

void setup_scene() {
    WORLD_SCALE = iResolution.y / 20.0 * 3.0;
    circle_pos = vec2(-55.0, sin(iTime * 1.4) * 30.0);
    if (iMouse.x > 0.0 && iMouse.y > 0.0) {
        circle_pos = pixel_space_to_world_space(iMouse.xy);
    }
    rect_size = vec2(12.0 + sin(iTime * 2.0) * 6.0, 60.0);
    noise_offset_a = vec2(0.0, -100.0 + sin(iTime) * 10.0);
    noise_offset_b = vec2(cos(iTime) * 10.0, +100.0);
    noise_offset_c = vec2(cos(iTime * 2.0) * 10.0, -sin(iTime) * 10.0);
}

float sd_grid(vec2 p) {
    vec2 uv = abs(fract(p + 0.5) - 0.5);
    return min(uv.x, uv.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    setup_scene();

    vec2 uv = pixel_space_to_world_space(fragCoord);

    // say this is the center of our voxel
    vec2 voxel_pos = floor(uv) + 0.5;
    vec3 voxel_color = vec3(0.0);

    if (USE_NAIVE_METHOD) {
        // normally, if we were generating a voxel world, we'd loop over every voxel to determine what
        // the per-voxel data is. To do this, we run a function per-voxel, and then apply a predicate
        // to the result of this function. Here, our predicate is just whether the value `d` is less
        // than zero, which decides whether our voxel is BLUE or ORANGE. This function and predicate
        // are very simple, but I am sure you can imagine how this idea can be extended.
        float d = map(voxel_pos);
        if (d < 0.0) {
            voxel_color = BLUE;
        } else {
            voxel_color = ORANGE;
        }
        // This is already inefficient, but to add insult to injury, we may then generate a sparse data
        // structure using this per-voxel data, in order to save memory. In this instance, we'd be using
        // a "bottom-up" algorithm for generating our hierarchy. Anyone who has ever used tree data structures
        // knows that this is inherently a bad idea, but how could we do our algorithm "top-down"?
    } else {
        // In order to do our algorithm "top-down", we'd need to determine whether this `d < 0.0` condition
        // were uniform across a given region. Getting an *exact* answer isn't even necessary, it's just
        // important that the estimate never says a region is uniform when it really isn't. Determining this
        // is the purpose of this shadertoy, demonstrating the ability to generalize estimating a predicate's
        // uniformity across the region encompassed by any given LOD level.

        // The environment of a shader is not really the best place to implement this, since we'll still
        // be running the predicate for every pixel, but if you had a CPU application, or custom GPU app
        // which enabled you to do conditional generation, then this algorithm is relevant. Shaders are
        // just the best way to visually demonstrate these things!
        for (uint lod_i = 0u; lod_i <= MAX_LOD; lod_i += LOD_STEP) {
            uint lod_level = MAX_LOD - lod_i;
            if (is_map_uniform_on_lod(voxel_pos, lod_level)) {
                float d = map(voxel_pos);
                if (d < 0.0) {
                    voxel_color = BLUE;
                } else {
                    voxel_color = ORANGE;
                }
                // A break here represents the early termination of a recursive function, which is why
                // this would reduce cost if used in a different context.
                break;
            }
        }
    }

    vec3 color = voxel_color;

    if (SHOW_ESTIMATED_SURFACE_VOXELS) {
        uint lod_i = MAX_LOD;
        uint lod_level = MAX_LOD - lod_i;
        float lod_scl = float(1u << lod_level);
        if (is_map_uniform_on_lod(uv, lod_level + 1u)) {
            color *= 0.0;
        } else {
            float grid_lines_fac = smoothstep(0.000, 0.25 / float(lod_scl), sd_grid(uv / lod_scl)) * 0.4 + 0.6;
            color *= grid_lines_fac;
        }
    } else {
        for (uint lod_i = 0u; lod_i <= MAX_LOD; lod_i += LOD_STEP) {
            uint lod_level = MAX_LOD - lod_i;
            float lod_scl = float(1u << lod_level);
            if (is_map_uniform_on_lod(uv, lod_level)) {
                float grid_lines_fac = smoothstep(0.000, 0.25 / float(lod_scl), sd_grid(uv / lod_scl)) * 0.4 + 0.6;
                color *= grid_lines_fac;
                break;
            }
        }
    }

    fragColor = vec4(color, 1.0);
}

