// These are set in the setup_scene() function of Image
vec2 circle_pos;
vec2 rect_size;
vec2 noise_offset_a;
vec2 noise_offset_b;
vec2 noise_offset_c;

// Here is where we actually define the map. We do this here as macros because
// what we can do is swap out the `MAP_CIRCLE` and `MAP_BOX` functions can be
// swapped out INDIVIDUALLY depending on the context where the map is consumed.
#define USE_NOISE 1
#if USE_NOISE
#define MAP_DEF                                                        \
    MAP_CONSTANT(sd_union, 5.0)                                        \
    MAP_NOISE(sd_add, noise_offset_a, 0.01, 7.5)                       \
    MAP_NOISE(sd_add, noise_offset_b, 0.15, 1.1)                       \
    MAP_NOISE(sd_add, noise_offset_c, 0.60, 0.3)                       \
    MAP_GRADIENT_Y(sd_add, 0.17)                                       \
    MAP_CIRCLE(sd_union, circle_pos, 40.0)                             \
    MAP_BOX(sd_union, circle_pos - vec2(0.0, -10.0), vec2(50.0, 10.0)) \
    MAP_BOX(sd_union, vec2(80.0, 0.0), rect_size)
#else
#define MAP_DEF                                                        \
    MAP_CIRCLE(sd_union, circle_pos, 40.0)                             \
    MAP_BOX(sd_union, circle_pos - vec2(0.0, -10.0), vec2(50.0, 10.0)) \
    MAP_BOX(sd_union, vec2(0.0), rect_size)
#endif

// Below this is all just implementation details.

const float MAX_DIST = 1.0e9;
const float SQRT_2 = 1.41421356237;

float nonzero_sign(float x) {
    if (x < 0.0)
        return -1.0;
    return 1.0;
}
vec2 nonzero_sign(vec2 x) {
    return vec2(nonzero_sign(x.x), nonzero_sign(x.y));
}

float sd_union(float a, float b) { return min(a, b); }
float sd_add(float a, float b) { return (a + b); }
float sd_mul(float a, float b) { return (a * b); }

// assumed circle is at (0, 0)
float sd_circle_nearest(vec2 p, float r) {
    return length(p) - r;
}
float sd_circle_furthest(vec2 p, float r) {
    return length(p) + r * 2.0;
}

// assumed box is centered at (0, 0)
float sd_box_nearest(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}
float sd_box_furthest(vec2 p, vec2 b) {
    return length(b * nonzero_sign(p) + p);
}

// assumed circle is at (0, 0)
vec2 minmax_sd_circle_in_region(vec2 region_center, vec2 region_size, float r) {
    float min_d = sd_box_nearest(-region_center, region_size);
    float max_d = sd_box_furthest(-region_center, region_size);
    return vec2(min_d, max_d) - r;
}

vec2 minmax_gradient_y_in_region(vec2 region_center, vec2 region_size, float f) {
    float min_d = (region_center.y - region_size.y) * f;
    float max_d = (region_center.y + region_size.y) * f;
    return vec2(min_d, max_d);
}

// https://www.shadertoy.com/view/XttSz2
float hash(float n) { return fract(sin(n) * 753.5453123); }
vec4 noised(in vec3 x) {
    vec3 p = floor(x);
    vec3 w = fract(x);
    vec3 u = w * w * (3.0 - 2.0 * w);
    vec3 du = 6.0 * w * (1.0 - w);

    float n = p.x + p.y * 157.0 + 113.0 * p.z;

    float a = hash(n + 0.0);
    float b = hash(n + 1.0);
    float c = hash(n + 157.0);
    float d = hash(n + 158.0);
    float e = hash(n + 113.0);
    float f = hash(n + 114.0);
    float g = hash(n + 270.0);
    float h = hash(n + 271.0);

    float k0 = a;
    float k1 = b - a;
    float k2 = c - a;
    float k3 = e - a;
    float k4 = a - b - c + d;
    float k5 = a - c - e + g;
    float k6 = a - b - e + f;
    float k7 = -a + b + c - d + e - f - g + h;

    return vec4(k0 + k1 * u.x + k2 * u.y + k3 * u.z + k4 * u.x * u.y + k5 * u.y * u.z + k6 * u.z * u.x + k7 * u.x * u.y * u.z,
                du * (vec3(k1, k2, k3) + u.yzx * vec3(k4, k5, k6) + u.zxy * vec3(k6, k4, k5) + k7 * u.yzx * u.zxy));
}

float sd_noise_conservative(vec2 p, float scale, float amp) {
    float noise_val = noised(vec3(p * scale, 0.0)).x * 2.0 - 1.0;
    return noise_val * amp;
}

vec2 minmax_noise_in_region(vec2 region_center, vec2 region_size, float scale, float amp) {
    // Use the lipschitz constant to compute min/max est. For the cubic interpolation
    // function of this noise function, said constant is 2.0 * 1.5 * scale * amp.
    //  - 2.0 * amp comes from the re-scaling of the range between -amp and amp (line 112-113)
    //  - scale comes from the re-scaling of the domain on the same line (p * scale)
    //  - 1.5 comes from the maximum abs value of the first derivative of the noise
    //    interpolation function (between 0 and 1): d/dx of 3x^2-2x^3 = 6x-6x^2.
    float lipschitz = 2.0 * 1.5 * scale * amp;
    float noise_val = sd_noise_conservative(region_center, scale, amp);
    float max_dist = length(region_size);
    return vec2(max(noise_val - max_dist * lipschitz, -amp), min(noise_val + max_dist * lipschitz, amp));
}

float map(vec2 p) {
    float d = MAX_DIST;
#define MAP_CIRCLE(op, x, r) \
    d = op(d, sd_circle_nearest(p - x, r));
#define MAP_BOX(op, x, r) \
    d = op(d, sd_box_nearest(p - x, r));
#define MAP_NOISE(op, x, s, a) \
    d = op(d, sd_noise_conservative(p - x, s, a));
#define MAP_CONSTANT(op, x) \
    d = op(d, x);
#define MAP_GRADIENT_Y(op, f) \
    d = op(d, p.y * f);
    MAP_DEF
#undef MAP_CIRCLE
#undef MAP_BOX
#undef MAP_NOISE
#undef MAP_CONSTANT
#undef MAP_GRADIENT_Y
    return d;
}

vec2 map_at_lod(vec2 p, uint lod_level) {
    float chunk_edge_length_at_lod = float(1u << lod_level);
    vec2 lod_chunk_center_p = (floor(p / chunk_edge_length_at_lod) + 0.5) * chunk_edge_length_at_lod;
    float chunk_diag_length_at_lod = SQRT_2 * 0.5 * chunk_edge_length_at_lod;

    float min_d;
    float max_d;

    bool first = true;

    // This is pretty self explanatory, but what we do here is estimate whether the
    // chunk could be completely uniform at this LOD by ensuring the furthest distance
    // in any direction from the chunk center is GREATER than the chunk's diagonal
    // radius. This, in a sense, is like assuming the chunks are circles/spheres,
    // which conservatively estimates the LOD-ing, especially when the nearest point
    // of the SDF is in cardinal directions.

#define MAP_CONSTANT(op, x)       \
    {                             \
        float d = x;              \
        if (first) {              \
            min_d = d;            \
            max_d = d;            \
        } else {                  \
            min_d = op(min_d, d); \
            max_d = op(max_d, d); \
        }                         \
        first = false;            \
    }

#define MAP_GRADIENT_Y(op, f)                                                                              \
    {                                                                                                      \
        vec2 d = minmax_gradient_y_in_region(lod_chunk_center_p, vec2(chunk_edge_length_at_lod * 0.5), f); \
        if (first) {                                                                                       \
            min_d = d[0];                                                                                  \
            max_d = d[1];                                                                                  \
        } else {                                                                                           \
            min_d = op(min_d, d[0]);                                                                       \
            max_d = op(max_d, d[1]);                                                                       \
        }                                                                                                  \
        first = false;                                                                                     \
    }

    // Using this function as the definition of the minmaxing of your function will result in
    // an imperfect but FAST (and super simply implemented) estimate!
#define SD_SPHERE_CHUNK_EST(op, f)                           \
    {                                                        \
        float d = f;                                         \
        if (first) {                                         \
            min_d = d - chunk_diag_length_at_lod;            \
            max_d = d + chunk_diag_length_at_lod;            \
        } else {                                             \
            min_d = op(min_d, d - chunk_diag_length_at_lod); \
            max_d = op(max_d, d + chunk_diag_length_at_lod); \
        }                                                    \
        first = false;                                       \
    }

    // Using this function as the definition of the minmaxing of your function will result in
    // an imperfect (but slightly better, slightly slower, and super simply implemented) estimate!
#define SD_SPHERE_CHUNK_EST_4(op, f0, f1, f2, f3)                                              \
    {                                                                                          \
        float d0 = f0;                                                                         \
        float d1 = f1;                                                                         \
        float d2 = f2;                                                                         \
        float d3 = f3;                                                                         \
        if (first) {                                                                           \
            min_d = min(min(d0, d1), min(d2, d3)) - chunk_diag_length_at_lod * 0.5;            \
            max_d = max(max(d0, d1), max(d2, d3)) + chunk_diag_length_at_lod * 0.5;            \
        } else {                                                                               \
            min_d = op(min_d, min(min(d0, d1), min(d2, d3)) - chunk_diag_length_at_lod * 0.5); \
            max_d = op(max_d, max(max(d0, d1), max(d2, d3)) + chunk_diag_length_at_lod * 0.5); \
        }                                                                                      \
        first = false;                                                                         \
    }

    // I defined a perfect circle estimator right below, so there's no need to use the SDF one
    // #define MAP_CIRCLE(op, x, r) SD_SPHERE_CHUNK_EST(op, sd_circle(lod_chunk_center_p - x, r))
#define MAP_CIRCLE(op, x, r)                                                                                  \
    {                                                                                                         \
        vec2 d = minmax_sd_circle_in_region(lod_chunk_center_p - x, vec2(chunk_edge_length_at_lod * 0.5), r); \
        if (first) {                                                                                          \
            min_d = d[0];                                                                                     \
            max_d = d[1];                                                                                     \
        } else {                                                                                              \
            min_d = op(min_d, d[0]);                                                                          \
            max_d = op(max_d, d[1]);                                                                          \
        }                                                                                                     \
        first = false;                                                                                        \
    }

#define MAP_BOX(op, x, r) SD_SPHERE_CHUNK_EST(op, sd_box_nearest(lod_chunk_center_p - x, r))

#define MAP_NOISE(op, x, s, a)                                                                               \
    {                                                                                                        \
        vec2 d = minmax_noise_in_region(lod_chunk_center_p - x, vec2(chunk_edge_length_at_lod * 0.5), s, a); \
        if (first) {                                                                                         \
            min_d = d[0];                                                                                    \
            max_d = d[1];                                                                                    \
        } else {                                                                                             \
            min_d = op(min_d, d[0]);                                                                         \
            max_d = op(max_d, d[1]);                                                                         \
        }                                                                                                    \
        first = false;                                                                                       \
    }

    MAP_DEF
#undef MAP_CIRCLE
#undef MAP_BOX
#undef MAP_NOISE
#undef MAP_CONSTANT
#undef MAP_GRADIENT_Y

    return vec2(min_d, max_d);
}

bool is_map_uniform_on_lod(vec2 p, uint lod_level) {
    if (lod_level == 0u)
        return true;
    vec2 minmax = map_at_lod(p, lod_level);
    return minmax[0] >= 0.0 || minmax[1] < 0.0;
}

