float text(vec2 uv, float ix)
{
    float col = 0.0;
    vec2 center = text_res/2.0;

    text_pos(2, 2.0 + STRHEIGHT(0.0));
    col += print_integer(ix*10.,0,uv) + _lsl() + print_integer(LAST_PATT,0,uv); 

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ix = texelFetch(iChannel1, ivec2(0,0), 0 ).x;
    int mono = int(texelFetch(iChannel1, ivec2(3,0), 0 ).x);
    int showtext = int(texelFetch(iChannel1, ivec2(4,0), 0 ).x);
    int aa = 1-int(texelFetch(iChannel1, ivec2(5,0), 0 ).x);

    text_res = iResolution.xy / DOWN_SCALE;
    float text_pixel = 0.;
    if (showtext == 1) text_pixel = text(fragCoord, ix);    

    float mx= iMouse.x / iResolution.x;
    vec2 mid = iResolution.xy / 2.;
    float mmul = 1.-mx;
    fragCoord = mid - (mid*mmul-fragCoord*mmul);

    vec4 val = vec4(0.);
    int am=0, ap=aa, j=0;
    for (int j = -am; j <= ap; j++)
        for (int i = -am; i <= ap; i++)
            val += texelFetch( iChannel0, ivec2(int(fragCoord.x)+i,int(fragCoord.y)+j), 0 );
    float n=float((am+ap+1));
    if (am+ap > 0) val /= n*n-2.;
    //if (am+ap > 0) val /= n-1.;

    if (aa == 0) val/=0.5;

    if (mono == 0) {
        fragColor = val;
    } else {
        float v=(1./liveval)*val.w;
        fragColor = vec4( v*0.9, v*0.95, v, 1.0 );
    }

    fragColor += text_pixel;
}

