vec2 N22(vec2 p)
{
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 345.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x * a.y, a.y * a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    // Remap pixel coordinates (from -1 to 1)
    //uv = uv * 2. - 1.;

    vec2 uv = (2. * fragCoord - iResolution.xy) /  iResolution.y;

    float m = 0.;
    float t = iTime * .2;
    float minDist = 100.;
    float cellIndex = 0.;

    vec3 col = vec3(0);

    if (false)
    {
        for (float i = 0.; i < 50.; i++)
        {
            vec2 n = N22(vec2(i));
            vec2 p = sin(n * t);

            float d = length(uv - p);
            m += smoothstep(.02, .01, d);

            if (d < minDist)
            {
                minDist = d;
                cellIndex = i;
            }    
        }
    }
    else
    {
         uv *= 3.;
         vec2 gv = fract(uv) - .5; // -.5 to make gv`s origon in gird center;
         vec2 id = floor(uv);
         vec2 cid = vec2(0.);

         for (float y = -1.; y <= 1.; y++)
         {
             for (float x = -1.; x <= 1.; x++)
             {
                 vec2 offs = vec2(x, y);
                 vec2 n = N22(offs + id);
                 vec2 p = offs + sin(n * t) * .5;
                 float d = length(gv - p);

                 if (d < minDist)
                 {
                     minDist = d;
                     cid = id + offs;
                 }
             }
         }
         col = vec3(minDist);
         //col.rg = cid * .1;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

#define ITERS_MAX 200
#define DIST_MIN 1e-3
#define DIST_MAX 20.
#define NORM_EPSILON 0.01

struct ray {
  vec3 p;
  bool hit;
  float len;
  int iters;

  // material
  vec3 c;
  float shin;

  float diffr;
  float specr;
};

struct sdfparams {
  vec3 c;
  float shin;

  float diffr;
  float specr;
};

struct sdf {
  float d;
  sdfparams p;
};

struct plight {
  vec3 p;
  vec3 c;
};

vec3 erot(vec3 p, vec3 ax, float r) {
  return mix(dot(ax, p) * ax, p, cos(r)) + cross(ax, p) * sin(r);
}

sdf sdf_smooth_union(sdf a, sdf b, float k) {
  float h = clamp(0.5 + (b.d - a.d) / k * 0.5, 0.0, 1.0);

  return sdf(
    mix(b.d, a.d, h) - k * h * (1. - h),
    sdfparams(
      mix(b.p.c, a.p.c, h),
      mix(b.p.shin, a.p.shin, h),
      mix(b.p.diffr, a.p.diffr, h),
      mix(b.p.specr, a.p.specr, h)
    )
  );
}

sdf sdf_union(sdf a, sdf b) {
  sdf chosen;
  if (a.d < b.d) {
    chosen = a;
  } else {
    chosen = b;
  }
  return chosen;
}

sdf sdf_wave_displace(vec3 p, float a, float b, sdf s) {
  s.d += sin(p.x * a) * sin(p.y * a) * sin(p.z * a) * b;
  return s;
}

sdf sdf_sphere(vec3 p, float r, sdfparams m) {
  return sdf(
    length(p) - r,
    m
  );
}

sdf sdf_cylinder(vec3 p, float r, float h, sdfparams m) {
  vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);

  return sdf(
    min(max(d.x, d.y), 0.) + length(max(d, 0.0)),
    m
  );
}

sdf sdf_plane(vec3 p, sdfparams m) {
  return sdf(
    p.y,
    m
  );
}

sdf wool(vec3 p, float wig, sdfparams wool_mat) {
  vec3 np = p;
  np /= vec3(1., 1., 1.2);
  np = erot(np, vec3(0., 0., 1.), wig * 0.05);

  sdf s = sdf_sphere(
    np, 2.,
    wool_mat
  );

  s.d *= 1.2;

  s = sdf_wave_displace(np, 6., 0.05, s);

  return s;
}

sdf eye(vec3 p, float blink, float eye_rad, float iris_rad, sdfparams head_mat) {
  sdfparams eye_white = sdfparams(
    vec3(1.),
    100.,
    1.,
    1.
  );

  sdfparams eye_black = sdfparams(
    vec3(0.05),
    10.,
    1.,
    0.05
  );

  bool do_iris = length(p - vec3(0., 0., -eye_rad)) < iris_rad;

  sdfparams mat;

  if (blink == 1.0) {
    mat = head_mat;
  } else {
    if (do_iris) {
      mat = eye_black;
    } else {
      mat = eye_white;
    }
  }

  sdf l = sdf_sphere(
    p, eye_rad,
    mat
  );

  return l;
}

sdf eyes(vec3 p, sdfparams head_mat) {
  float blink = ceil(max(sin(iTime * 1.5) * 150. - 149., 0.));
  float glance = floor(sin(iTime) * 0.55 + 0.5);

  vec3 offs = vec3(0.21, 0., 0.);

  vec3 lp = p;
  lp /= vec3(1., 1.05, 1.);
  lp += offs;
  lp = erot(lp, vec3(0., 1., 0.), glance * 0.5);

  vec3 rp = p;
  rp /= vec3(1., 0.95, 1.);
  rp -= offs;
  rp = erot(rp, vec3(0., 1., 0.), glance * 0.5);

  sdf leye = eye(lp, blink, 0.25, 0.06, head_mat);
  leye.d *= 1.05;
  sdf reye = eye(rp, blink, 0.2, 0.045, head_mat);
  reye.d *= 0.95;

  return sdf_union(leye, reye);
}

sdf ear(vec3 p, float wiggle, float m, sdfparams mat) {
  vec3 np = p;
  np = erot(np, vec3(0., 1., 0.), 0.25 * m);
  np = erot(np, vec3(1., 0., 0.), wiggle);
  np -= vec3(0., -0.75, 0.);
  np = erot(np, vec3(0., 0., 1.), 3.14 / 3. * m);
  np /= vec3(1., 1., .5);

  sdf c = sdf_cylinder(
    np,
    0.5, 0.0,
    mat
  );
  c.d *= 0.5;
  c.d -= 0.025;

  return c;
}

sdf ears(vec3 p, sdfparams mat) {
  float ltime = iTime + 8.;
  float rtime = iTime - 10.;
  float lwiggle = max(fract(-ltime / 4.) * 3. - 2., 0.) * sin(ltime / 4. * 6.28 * 8.);
  float rwiggle = max(fract(-rtime / 5.) * 4. - 3., 0.) * sin(rtime / 5. * 6.28 * 8.);

  vec3 offs = vec3(1., 0., 0.);

  sdf lear = ear(p - offs, lwiggle * 0.5, 1., mat);
  sdf rear = ear(p + offs, rwiggle * 0.5, -1., mat); // haha

  return sdf_union(lear, rear);
}

sdf head_base(vec3 p, sdfparams mat) {
  sdf s = sdf_sphere(
    p - vec3(0., -0.1, 0.),
    1.,
    mat
  );

  sdf c = sdf_cylinder(
    p - vec3(0., 0.6, 0.4),
    0.3, 0.5,
    mat
  );

  c.d -= 0.5;

  return sdf_smooth_union(s, c, 0.25);
}

sdf head(vec3 p, float wool_wiggle, sdfparams head_mat) {
  vec3 np = p;
  np = erot(np, vec3(0., 0., 1.), wool_wiggle * 0.015);

  sdf head = head_base(np, head_mat);
  sdf eyes = eyes(np - vec3(0., 0.9, -0.35), head_mat);
  sdf ears = ears(np - vec3(0., 1.4, 0.5), head_mat);

  return sdf_union(
    sdf_smooth_union(head, ears, 0.1),
    eyes
  );
}

sdf leg(vec3 p, sdfparams wool_mat, sdfparams skin_mat) {
  p -= vec3(0., 1.25, 0.);

  sdf leg = sdf_cylinder(
    p,
    0.125, 1.25,
    skin_mat
  );

  sdf thing = sdf_cylinder(
    p - vec3(0., 1.25, 0.),
    0.2, 0.4,
    wool_mat
  );

  vec3 fp = p;
  fp /= vec3(1., 1., 1.1);
  fp -= vec3(0., -1.1, -0.1);

  sdf foot = sdf_cylinder(
    fp,
    0.2, 0.05,
    skin_mat
  );

  foot.d /= 1.1;
  foot.d -= 0.05;

  return sdf_union(sdf_smooth_union(leg, foot, 0.25), thing);
}

sdf legs(vec3 p, sdfparams wool_mat, sdfparams skin_mat) {
  vec3 np = vec3(abs(p.x), p.yz);
  np -= vec3(1., 0., 0.);

  sdf front = leg(np, wool_mat, skin_mat);
  sdf back = leg(np - vec3(0., 0., 2.5), wool_mat, skin_mat);

  return sdf_union(front, back);
}

sdf owen(vec3 p) {
  sdfparams skin_mat = sdfparams(
    vec3(0.1),
    20.,
    1.,
    0.03
  );

  sdfparams wool_mat = sdfparams(
    vec3(0.95),
    10.,
    1.,
    0.1
  );

  vec3 np = p;
  np -= vec3(0., 4.2, 0.);

  float wool_wiggle = max(sin(iTime * 1.5), 0.) * sin(iTime * 12.);

  vec3 head_p = np;
  head_p -= vec3(0., sin(iTime / 3.) * 0.4 - 0.25, -2.);
  head_p = erot(head_p, vec3(0., 0., 1.), sin(iTime / 1.5) * 0.1);
  head_p += vec3(0., 0.25, 0.);

  sdf h = head(head_p, wool_wiggle, skin_mat);

  sdf w = wool(
    np - vec3(0., sin(iTime / 2. + 1.) * 0.1, 1.),
    wool_wiggle,
    wool_mat
  );

  sdf l = legs(
    p - vec3(0., 0., -0.25),
    wool_mat,
    skin_mat
  );

  return sdf_union(h, sdf_smooth_union(w, l, 0.5));
}

sdf scene(vec3 p) {
  sdfparams ground_mat = sdfparams(
    vec3(0.1, 0.8, 0.1),
    10.,
    1.,
    0.1
  );

  sdf o = owen(p - vec3(0., 0., -1.));
  sdf g = sdf_cylinder(
    p,
    5., 0.,
    ground_mat
  );

  return sdf_union(o, g);
}

ray raymarch(vec3 ro, vec3 rd) {
  ray r = ray(
    ro,
    false,
    0.,
    0,
    vec3(0.),
    0.,
    0.,
    0.
  );

  for (int i = 0; i < ITERS_MAX; i++) {
    sdf s = scene(r.p);
    r.p += rd * s.d;
    r.len += s.d;
    r.iters = i;
    r.c = s.p.c;
    r.shin = s.p.shin;
    r.diffr = s.p.diffr;
    r.specr = s.p.specr;

    if (s.d < DIST_MIN) {
      r.hit = true;
      break;
    }
    if (r.len > DIST_MAX) {
      break;
    }
  }

  return r;
}

vec3 norm(ray r) {
  sdf s = scene(r.p);
  vec2 eps = vec2(NORM_EPSILON, 0.);

  return normalize(s.d - vec3(
    scene(r.p - eps.xyy).d,
    scene(r.p - eps.yxy).d,
    scene(r.p - eps.yyx).d
  ));
}

vec3 _diffuse(ray r, plight l, vec3 lv, vec3 n) {
  return max(dot(lv, n), 0.) * r.c * l.c;
}

vec3 _specular(ray r, plight l, vec3 lv, vec3 n, vec3 rd) {
  vec3 ref = reflect(lv, n);
  return pow(max(dot(ref, rd), 0.), r.shin) * l.c;
}

vec3 singular_light(ray r, plight l, vec3 rd) {
  vec3 n = norm(r);
  vec3 lv = normalize(l.p - r.p);

  return r.diffr * _diffuse(r, l, lv, n) + r.specr * _specular(r, l, lv, n, rd);
}

vec3 light(ray r, vec3 rd, vec3 amb) {
  plight l1 = plight(
    vec3(1., 1., -1.) * 8.,
    vec3(1., 0.7, 0.5)
  );

  plight l2 = plight(
    vec3(-1., -1., -1.) * 16.,
    vec3(0.9, 0.9, 1.) * 0.25
  );

  plight l3 = plight(
    vec3(0., 0., 1.) * 32.,
    vec3(1., 1., 1.) * 0.75
  );

  vec3 c = amb;
  c += singular_light(r, l1, rd);
  c += singular_light(r, l2, rd);
  c += singular_light(r, l3, rd);

  return c;
}

vec3 cam(vec3 p) {
  p = erot(p, vec3(1., 0., 0.), 0.2 + sin(iTime / 3.) * 0.1);
  p = erot(p, vec3(0., 1., 0.), sin(iTime / 2.) * 0.25);
  //p = erot(p, vec3(1., 0., 0.), sin(time * 2.));
  //p = erot(p, vec3(0., 1., 0.), time);

  return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

  vec3 ro = vec3(0., 3., -12.);
  vec3 rd = normalize(vec3(uv, 1.));

  ro = cam(ro);
  rd = cam(rd);

  ray r = raymarch(ro, rd);

  vec3 fg = light(r, rd, vec3(.01, 0.025, 0.025));
  vec3 bg = vec3(0.5, 0.8, 0.8);

  vec3 col = r.hit ? fg : bg;

  fragColor = vec4(sqrt(col), 1.0);
}

float N21(vec2 p)
{
    return fract(sin(p.x * 100. + p.y * 6574.) * 5647.);
}

float SmoothNoise(vec2 uv)
{
    vec2 lv = fract(uv);
    lv = lv * lv * (3. - 2. * lv);
    vec2 id = floor(uv);

    float bl = N21(id);
    float br = N21(id + vec2(1., 0.));
    float b = mix(bl, br, lv.x);

    float tl = N21(id + vec2(0., 1.));
    float tr = N21(id + vec2(1., 1.));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float SmoothNoise2(vec2 uv)
{
    float c = SmoothNoise(uv * 4.);
    c += SmoothNoise(uv * 8.) * .5;
    c += SmoothNoise(uv * 16.) * .25;
    c += SmoothNoise(uv * 32.) * .125;
    c += SmoothNoise(uv * 64.) * .0625;
    return c / 1.9375;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv += iTime * .1;
    float c = SmoothNoise2(uv);
    vec3 col = vec3(c);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// CC0

#define PI 3.1415926535

// The center sample counts as a ring, so ringCount should be at least 2
int ringCount = 5;
int ringSampleFactor = 8;
float kernelRadius = 3.5;

int octawebRingSampleCount(int ring)
{
    return ring == 0 ? 1 : ringSampleFactor * ring;
}

vec2 octawebSample(int ring, int s, int ringSampleCount, int ringCount, float kernelRadius)
{
    // Need to place n-1 rings along the radius since center is ring 0
    float ringSpacing = kernelRadius / float(ringCount - 1);
    float r = float(ring) * ringSpacing;

    float rimSpacing = 2.0 * PI / float(ringSampleCount);
    float phi = float(s) * rimSpacing;
    // Rings are 'interleaved', probably to make the pattern less obviously repeating
    if (ring % 2 == 0)
        phi += rimSpacing * 0.5;

    float x = r * cos(phi);
    float y = r * sin(phi);

    return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Setup a coordinate system over the frame
    vec2 clip = uv * 2.0 - 1.0;
    // Fix aspect ratio
    clip.x *= iResolution.x / iResolution.y;
    vec2 coord = clip * 6.0;

    vec3 col = vec3(0);

    // Draw gridlines
    if (fract(coord.x) < 0.05 || fract(1.0 - coord.x) < 0.05)
        col = vec3(0.1);
    if (fract(coord.y) < 0.05 || fract(1.0 - coord.y) < 0.05)
        col = vec3(0.1);

    // Draw samples
    for (int ring = 0; ring < ringCount; ++ring)
    {
        int ringSampleCount = octawebRingSampleCount(ring);
        for (int si = 0; si < ringSampleCount; ++si)
        {
            vec2 s = octawebSample(ring, si, ringSampleCount, ringCount, kernelRadius);

            if (distance(coord, s) < 0.1)
            {
                col = vec3(0.8);
                break;
            }
        }
    }

    fragColor = vec4(col,1.0);
}

// SPDX-License-Identifier: CC0-1.0
//
// # GLSL PCG Random + SplitMix32
//
// This implementation supports three modes (see `MODE`)
// and provides four output functions (see `initfn` and `outputfn`).
//
// It has 32 bits of state and produces 16-bit values.
//
// Usage:
//
// ```
// rng my_rng = SeedRNG(seed, stream);
//
// uint x = rand_u16(my_rng); // random 16-bit int in `[0, 2^16)`
// float y = rand_f32(my_rng); // random float in `[0, 1)` (16 bits of random)
// float z = jitter_f32(my_rng, 2.0); // low discrepancy random
// ```
//
// Streams allow a generator to produce 2^31 *different*
// pseudorandom sequences, that will *never* overlap.
// When using the `out_id` scrambler, these sequences
// usually have correlations, so be careful.
// Each stream is identified by an unsigned odd integer.

// `0` - BW
// `1` - RGB
// `2` - RGBA
#define COLOR 0

// `0` - all pixels have the same seed
// `1` - each pixel has a unique seed
#define VAR_SEED 1

//       mode                    - period - speed/quality
// ------------------------------------------------------
// `0` - LCG                     - 2^32   - best quality
// ------------------------------------------------------
// `1` - MCG, implies VAR_SEED   - 2^30   - faster, but has a reduced period
//                                          and doesnt support streams
// ------------------------------------------------------
// `2` - counter (SplitMix-like) - 2^32   - even faster, but requires
//                                          a good seed and/or scrambler function
#define MODE 0

// Seed function:
//
// function        - details                  - speed       - quality
// ------------------------------------------------------------------
// `init_id`       - identity                 - the fastest - worst quality
// ------------------------------------------------------------------
// `init_xsh_rs`   - fixed xorsh              - fast        - somewhat acceptable quality
// ------------------------------------------------------------------
// `init_rxs_m_xs` - random xorsh, MCG, xorsh - slower      - even better
// ------------------------------------------------------------------
// `init_mmh3`     - MurmurHash3              - same here  - the bestest
#define initfn init_mmh3

// Scrambler function:
//
// `out_id`
// `out_xsh_rs`
// `out_rxs_m_xs`
// `out_mmh3`
#define outputfn out_mmh3

struct rng {
    uint state;
    #if MODE != 1
    uint stream;
    #endif
};

rng   SeedRNG(uint, uint);
void  bump(inout uint, uint);
void  bump(inout rng);

uint  init_id(uint);
uint  init_xsh_rs(uint);
uint  init_rxs_m_xs(uint);
uint  init_mmh3(uint);

uint  out_id(uint);
uint  out_xsh_rs(uint);
uint  out_rxs_m_xs(uint);
uint  out_mmh3(uint);

uint  rand_u16(inout rng);
float rand_f32(inout rng);

rng SeedRNG(uint seed, uint line) {
    seed = initfn(seed);

    #if MODE == 1
        seed |= 3u;
        rng rng = rng(seed);
    #else
        line = initfn(line);
        rng rng = rng(seed, line | 1u);
    #endif

    bump(rng);
    return rng;
}

void bump(inout uint s, uint c) {
    #if   MODE == 0
        s *= 747796405u;
        s += c;
    #elif MODE == 1
        s *= 741103597u;
    #else
        s += c;
    #endif
}

void bump(inout rng rng) {
    #if MODE != 1
        bump(rng.state, rng.stream);
    #else
        bump(rng.state, 0u);
    #endif
}

uint init_id(uint old) {
    return old;
}

uint init_xsh_rs(uint old) {
    return old ^ old >> 16;
}

uint init_rxs_m_xs(uint old) {
    uint shr = old >> 28;
    old ^= old >> (4u + shr);
    old *= 277803737u;
    old ^= old >> 22;
    return old;
}

uint init_mmh3(uint old) {
    old ^= old >> 16;
    old *= 0x85EBCA6Bu;
    old ^= old >> 13;
    old *= 0xC2B2AE35u;
    old ^= old >> 16;
    return old;
}

uint out_id(uint old) {
    return old >> 16;
}

uint out_xsh_rs(uint old) {
    uint rsh = old >> 30;
    old  ^= old >> 19;
    old >>= 11u + rsh;
    old  &= 0xFFFFu;
    return old;
}

uint out_rxs_m_xs(uint old) {
    uint shr = old >> 29;
    old  ^= old >> (3u + shr);
    old  *= 277803737u;
    old >>= 16;
    old  ^= old >> 11;
    return old;
}

uint out_mmh3(uint old) {
    return init_mmh3(old) & 0xFFFFu;
}

uint rand_u16(inout rng rng) {
    uint old = rng.state;
    bump(rng);
    return outputfn(old);
}

float rand_f32(inout rng rng) {
    uint raw = 0x3F800000u | (rand_u16(rng) << 7);
    return uintBitsToFloat(raw) - 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uint x = uint(fragCoord.x) << 0xBu;
    uint y = uint(fragCoord.y) << 0x2u;

    uint line = x | y;

    #if VAR_SEED == 1 || MODE == 1
        uint seed = line;
    #else
        uint seed = 2434u;
    #endif

    rng rng = SeedRNG(seed, line);

    #if COLOR == 0
        fragColor = vec4(vec3(rand_f32(rng)), 1.0);
    #elif COLOR == 1
        fragColor = vec4(rand_f32(rng), rand_f32(rng), rand_f32(rng), 1.0);
    #else
        fragColor = vec4(rand_f32(rng), rand_f32(rng), rand_f32(rng), rand_f32(rng));
    #endif
}

