void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    vec3 targetC = texture(iChannel0, uv).xyz;
    vec3 currentC = texture(iChannel1, uv).xyz;

    if(iFrame == 0)
        currentC = targetC;


    vec4 noise = texture(iChannel2, fragCoord/iChannelResolution[2].xy) 
    + float(iFrame) * (vec4(0,1,2,3) + 0.61803398874989484820);
    noise -= floor(noise);

    vec3 total = vec3(0);
    float offsetScale = 0. + pow
    (
        2.0,
        noise.y * (log2(iResolution.y) - 1.0)//1.0 / (noise.y*noise.y+0.125)
    );

    float angle = noise.x * 3.1415927 * 2.;
    float sinAng = sin(angle);
    float cosAng = cos(angle);


    mat2x2 rotationMatrix = mat2x2
    (
        vec2(cosAng, -sinAng),
        vec2(sinAng, cosAng)
    );




    int count = 0;
    for(int x = -1; x <= 1; x++)
    {
        for(int y = -1; y <= 1; y++)
        {
            if(x == 0 && y == 0) continue;
            vec2 offset2D = vec2(x,y)*offsetScale;

            vec2 offset2DRotated = rotationMatrix * offset2D;

            vec2 coord = (fragCoord+offset2DRotated)/iResolution.xy;

            if(coord.x < 0. || coord.x > 1. || coord.y < 0. || coord.y > 1.)
                continue;

                count++;

            vec3 target = texture
            (
                iChannel0, 
                coord
            ).xyz;  

            vec3 current = texture
            (
                iChannel1, 
                coord
            ).xyz;

            if(iFrame == 0)
                current = target;

            vec3 targetDiff = (targetC - target);

            // Saturation boost.
            targetDiff.yz *= 2.0;

            // Boost and limit gradient magnitudes.
            targetDiff /= (length(targetDiff) + 0.0625) * 1.5;

            total += current + targetDiff - currentC;
        }    
    }

    if(count > 0)
        currentC += total / float(count);

    // Hue correction, prevents hue drift when there is room in the gamut for it
    // to drift around.
    // Also helps preserve skin tones.
    vec2 targetHueDir = normalize(targetC.yz);
    if(!any(isnan(targetHueDir)) && !any(isinf(targetHueDir)))
        currentC.yz = mix(currentC.yz, (dot(targetHueDir, currentC.yz)) * targetHueDir, 0.5);

    //currentC.y *= 0.1;

    fragColor.rgb = oklab_to_rgb(currentC.rgb);
    fragColor.rgb = clamp(fragColor.rgb, 0.0, 1.0);
    fragColor.rgb = rgb_to_oklab(fragColor.rgb);
}

