mat2 M = mat2(6,8,-8,6)*.1;
float i,t,s,j;
float D(vec3 p)
{
	for(i=1e2,t = p.y+8.; i>.1; i/=4.)
		p.zx *= M,
		p.y += i*.5,
		p = i*vec3(.9,.6,.8) - abs(mod(p,i+i)-i),
		s = min(1.-p.x,2.-min(p.y,p.z)),
        t<s? t=s, j=i : j;
	return t;
}

void mainImage(out vec4 O, vec2 I)
{
    vec3 r=iResolution, d=normalize(vec3(I+I,0)-r.xyy), p=2.-iTime/r/r/.1,n,e=vec3(1,1,-1)/1e2;
    float i=0.,t=i,s=t;
    for(d.yz *= M; r.z++<2e2; t += s)
        s = D(p),
        p += d*s;
    O = cos(j*vec4(9,17,24,0))+1.;
    n = normalize(D(p+e)*e+D(p+e.xzx)*e.xzx+D(p+e.zxx)*e.zxx+D(p+e.z)*e.z);

    for(s=.01; s<.2; s/=.8)
        O *= max(D(p+n*s*t)/s/t,.02),
        O *= max(D(p+s*t)/s/t,.01);
    O = pow(O/1e3,vec4(.04))+t/6e2;
}

// PERLIN NOISE EXAMPLE
// Credits to Suboptimal Engineer's youtube tutorial https://www.youtube.com/watch?v=7fd331zsie0

vec2 randomGradient(vec2 p) {
  p = p + 0.02;
  float x = dot(p, vec2(123.4, 234.5));
  float y = dot(p, vec2(234.5, 345.6));
  vec2 gradient = vec2(x, y);
  gradient = sin(gradient);
  gradient = gradient * 43758.5453;

  // update noise function with time
  gradient = sin(gradient + iTime);
  return gradient;
}

// inigo quilez - https://iquilezles.org/articles/distfunctions2d/
float sdfCircle(in vec2 p, in float r) {
  return length(p) - r;
}

// inigo quilez - https://iquilezles.org/articles/distfunctions2d/
float sdfOrientedBox(in vec2 p, in vec2 a, in vec2 b, float th) {
  float l = length(b - a);
  vec2 d = (b - a) / l;
  vec2 q = (p - (a + b) * 0.5);
  q = mat2(d.x, -d.y, d.y, d.x) * q;
  q = abs(q) - vec2(l, th) * 0.5;
  return length(max(q, 0.0)) + min(max(q.x, q.y), 0.0);
}

vec2 cubic(vec2 p) {
  return p * p * (3.0 - p * 2.0);
}

vec2 quintic(vec2 p) {
  return p * p * p * (10.0 + p * (-15.0 + p * 6.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
