void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 U = (2.*fragCoord - iResolution.xy)/iResolution.y; // -1,1
    vec2 V = fragCoord/iResolution.xy; // [0,1

    vec2 field = B(V).xy; // get vector field 
    vec4 pre = A(V - 0.001 * field); // use vector field as gradient

    vec3 col = pow3(clamp(pre.xyz,0.,.99),1.004); // color attenuation

    // Grid of points * Time varying pixel color * twinkle
    float interval = 0.23;
    vec2 UGrid = mod(U + interval * 0.5, interval);
    float d = length(UGrid - vec2(interval*0.5));
    vec3 grid = vec3(smoothstep(0.99, 1.0, (sqrt2 - d) / sqrt2));
    vec3 palette = 0.5 + 0.5*cos(iTime+V.xyx+vec3(0,2,4));
    float twinkle = map(sin(iTime/3.), -1.,1., 0.3, 1.);
    col +=  grid * normalize(palette);// * twinkle;

    fragColor = vec4(col,1.0);
}

