//methods from https://www.shadertoy.com/view/3tBGzh
#define A(U) texture(iChannel0, U)
#define B(U) texture(iChannel1, U)
#define C(U) texture(iChannel2, U)

const float sqrt2 = 1.4142135623730951;
const float sqrt3 = 1.7320508075688772;


//utility functions
mat2 rot(float a){
    return mat2(cos(a),-sin(a),sin(a),cos(a));
}

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

float unipolar(float x){
    return map(x,-1.,1.,0.,1.);
}

vec2 unipolar2(vec2 u){
    return vec2(unipolar(u.x), unipolar(u.y));
}

vec3 pow3(vec3 u, float a){
    return vec3(pow(u.x,a), pow(u.y,a), pow(u.z,a));
}

