   vec2 outVF = pos;
   vec2 inVF = -pos;
   vec2 rotVF = vec2(-y,x);
   vec2 hyperbolaVF = vec2(x,-y);
   vec2 rightVF = vec2(x*x + y*y, 0);
   vec2 touchingCirclesVFA = vec2(x*x - y*y, 2.0 * x* y);
   vec2 touchingCirclesVFB = vec2(-x*x + y*y, -2.0 * x* y);

   // interpolate between vector fields
   vec2 VF =
   freqPlus(1.0)  * inVF +
   freqPlus(2.0)  * rotVF +
   freqPlus(4.0)  * touchingCirclesVFA +
   freqPlus(8.0)  * hyperbolaVF +
   freqPlus(16.0) * rightVF;

   fragColor = vec4(VF,0,1.0);
}

