Surface sdScene(vec3 p, vec2 mouse, bool mouseActive, float time)
{
    Surface ret = EmptySurface;

    vec3 c = vec3(1);
    vec3 q = mod((p - vec3(0.5, 0.5, 0.0)) + 0.5 * c,c) - 0.5 * c;
    float radius = 0.2;
    vec3 color = vec3(1);
    ret = minWithColor(ret, sdSphere(q, radius, color));
    ret = minWithColor(ret, sdBasis(p, 1.0f));
    return ret;
}

Surface rayMarch(vec3 ro, vec3 rd, float start, float end, vec2 mouse, bool mouseActive, float time)
{
    float depth = start;
    Surface co; // closest object

    for (int i = 0; i < MAX_MARCHING_STEPS; i++)
    {
        vec3 p = ro + depth * rd;
        co = sdScene(p, mouse, mouseActive, time);
        depth += co.sd;
        if (co.sd < PRECISION || depth > end) break;
    }

    co.sd = depth;

    return co;
}

vec3 calcNormal(in vec3 p, vec2 mouse, bool mouseActive, in float time)
{
    vec2 e = vec2(1.0, -1.0) * 0.0005; // epsilon
    return normalize(
        e.xyy * sdScene(p + e.xyy, mouse, mouseActive, time).sd +
        e.yyx * sdScene(p + e.yyx, mouse, mouseActive, time).sd +
        e.yxy * sdScene(p + e.yxy, mouse, mouseActive, time).sd +
        e.xxx * sdScene(p + e.xxx, mouse, mouseActive, time).sd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 backgroundColor = vec3(0.5);
    float time = iTime;

    float cameraDistance = 4.25;

    float aspectRatio = iResolution.x / iResolution.y;
    vec2 mouse = cameraDistance * 0.5 * viewport_to_world(iMouse.xy, iResolution);
    bool mouseActive = iMouse.z > 0.0;

    vec3 col = vec3(0);
    vec3 ro = vec3(-0.25, 0, -cameraDistance); // ray origin that represents camera position

    float bob = sin(iTime * 0.1) * 0.05f;
    ro += vec3(0, bob, 0);

    vec3 target = vec3(0) + vec3(0.5, -0.5, 0.0);
    if (mouseActive)
    {
        target = target + vec3(mouse, 0);
    }
    vec3 toTarget = target - ro;
    mat3 lookAtInv = lookAtInv(toTarget);

//    vec3 rd = normalize(vec3(uv, 1) + vec3(0.15, -0.15, 0.0));
//    if (mouseActive)
//    {
        vec3 rd = lookAtInv * normalize(vec3(uv, 1)); // ray direction
//    }


    Surface co = rayMarch(ro, rd, MIN_DIST, MAX_DIST, mouse, mouseActive, time); // closest object

    if (co.sd > MAX_DIST)
    {
        col = backgroundColor; // ray didn't hit anything
    }
    else
    {
        vec3 p = ro + rd * co.sd; // point on surface
        vec3 normal = calcNormal(p, mouse, mouseActive, time);
        vec3 lightPosition = vec3(4, -2, 0);
        vec3 lightDirection = normalize(lightPosition - p);

        float ambientContrib = 0.01;
        float diffuseContrib = 1.0 - ambientContrib;
        vec3 ambient = ambientContrib * co.col;
        vec3 diffuse = diffuseContrib * saturate(dot(normal, lightDirection)) * co.col;

        col = ambient + diffuse;
    }

    col = pow(col, vec3(1.0 / 2.2));

    // Output to screen
    fragColor = vec4(col, 1.0);
}

