const float PI = 3.1415926535897932384626433832795;
const float TWO_PI = 6.283185307179586476925286766559;
const float DEGREES_TO_RADIANS = PI / 180.0;

vec3 lerp (in vec3 a, in vec3 b, in float t)
{
    return (1.0 - t) * a + t * b;
}

float saturate(in float value)
{
    return clamp(value, 0.0, 1.0);
}

// origin at viewport center, y in range [-1, 1], corrected for aspect ratio
vec2 viewport_to_world( in vec2 fragCoord, in vec3 resolution )
{
    return (2.0 * fragCoord - resolution.xy) / resolution.y;
}

// ray marching

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 200.0;
const float PRECISION = 0.001;

struct Surface
{
    float sd; // signed distance value
    vec3 col; // color
};

const Surface EmptySurface = Surface(MAX_DIST, vec3(0));

// operations

Surface minWithColor(Surface obj1, Surface obj2)
{
    if (obj2.sd < obj1.sd)
    {
        return obj2;
    }
    else
    {
        return obj1;
    }
}

// SDFs

// torus
// p = center
// r1 = major radius (distance from center to circular cross-section center
// r2 = radius of circular cross-section
Surface sdTorus(vec3 p, float r1, float r2, vec3 col)
{
    vec2 q = vec2(length(p.xy) - r1, p.z);
    return Surface(length(q) - r2, col);
}

// cone
// p = center of base
// c = sin/cos of angle
// h = height
Surface sdCone(vec3 p, vec2 c, float h, vec3 col)
{
  vec2 q = h * vec2(c.x / c.y, -1.0);
  vec2 w = vec2(length(p.xy), p.z);
  vec2 a = w - q * saturate(dot(w,q) / dot(q,q));
  vec2 b = w - q * vec2(saturate(w.x / q.x), 1.0);
  float k = sign(q.y);
  float d = min(dot(a, a), dot(b, b));
  float s = max(k * (w.x * q.y - w.y * q.x), k * (w.y - q.y));
  return Surface(sqrt(d) * sign(s), col);
}

Surface sdCone(vec3 p, float angle, float h, vec3 col)
{
    return sdCone(p, vec2(sin(angle), cos(angle)), h, col);
}

// infinite cylinder
// p = point on center axis
// r = radius
Surface sdCylinder(vec3 p, float r, vec3 col)
{
    return Surface(length(p.xy) - r, col);
}

// finite cylinder
// p = point on center axis
// r = radius
// h = height
Surface sdCylinder(vec3 p, float r, float h, vec3 col)
{
  vec2 d = abs(vec2(length(p.xy), p.z - 0.5 * h)) - vec2(r, 0.5 * h);
  return Surface(min(max(d.x, d.y), 0.0) + length(max(d, 0.0)), col);
}

// arrow
Surface sdArrow(vec3 p, float shaftRadius, float totalLength, vec3 col)
{
    float capAngle = 20.0 * DEGREES_TO_RADIANS;
    float capLength = 2.0 * shaftRadius / tan(capAngle);
    Surface ret = EmptySurface;
    ret = minWithColor(ret, sdCylinder(p, shaftRadius, totalLength - capLength, col));
    ret = minWithColor(ret, sdCone((p - vec3(0, 0, totalLength)), capAngle, capLength, col));
    return ret;
}

// sphere
// p = center
// r = radius
Surface sdSphere(vec3 p, float r, vec3 col)
{
    return Surface(length(p) - r, col);
}

// box
// p = center
// b = size
Surface sdBox(vec3 p, vec3 b, vec3 col)
{
    vec3 q = abs(p) - b;
    float d = length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
    return Surface(d, col);
}

// xz-plane
// p = point on plane
Surface sdFloor(vec3 p, vec3 col)
{
    float d = p.z + 1.0;
    return Surface(d, col);
}

mat3 lookAtInv(vec3 lookAtDirection)
{
    vec3 forward = normalize(lookAtDirection);

    const vec3 upHint = vec3(0, 1, 0);
    vec3 rightTest = cross(upHint, forward);
    vec3 right =
        length(rightTest) < 0.0001
            ? normalize(cross(forward, vec3(0, 0, 1)))
            : normalize(rightTest);

    vec3 up = normalize(cross(forward, right));

    return mat3(right, up, forward);
}

Surface sdBasis( vec3 p, float scale )
{
    float arrowRadius = max(0.015, scale * 0.04);

    vec3 colRed = vec3(1, 0, 0);
    vec3 colGreen = vec3(0, 1, 0);
    vec3 colBlue = vec3(0, 0, 1);

    Surface col = EmptySurface;
    col = minWithColor(col, sdBox(p, vec3(arrowRadius), vec3(1)));
    col = minWithColor(col, sdArrow(p * lookAtInv(vec3(1, 0, 0)), arrowRadius, scale, colRed));
    col = minWithColor(col, sdArrow(p * lookAtInv(vec3(0, 1, 0)), arrowRadius, scale, colGreen));
    col = minWithColor(col, sdArrow(p * lookAtInv(vec3(0, 0, 1)), arrowRadius, scale, colBlue));
    return col;
}

