float noise( in vec2 p )
{
  const float K1 = 0.366025404; // (sqrt(3)-1)/2;
  const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
  vec2  a = p - i + (i.x+i.y)*K2;
  float m = step(a.y,a.x); 
  vec2  o = vec2(m,1.0-m);
  vec2  b = a - o + K2;
  vec2  c = a - 1.0 + 2.0*K2;
  vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
  vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
  return dot( n, vec3(70.0) );
}

//perform one step of netwon's method to finalize the estimate
#define ONE_NEWTON_STEP

// of equation x^3+c1*x+c2=0
/* Stolen from http://perso.ens-lyon.fr/christophe.winisdoerffer/INTRO_NUM/NumericalRecipesinF77.pdf,
   page 179 */
// subsequently stolen from https://www.shadertoy.com/view/MdfSDn
float cubicRoot(float c1, float c2) {
	float q = -c1/3.;
	float r = c2/2.;
	float q3_r2 = q*q*q - r*r;
	if(q3_r2 < 0.) {
		float a = -sign(r)*pow(abs(r)+sqrt(-q3_r2),.333333);
		float b = a == 0. ? 0. : q/a;
		return a+b;
	}
	float theta = acos(r/pow(q,1.5));
	return -2.*pow(q,.5)*cos(theta/3.);
}

vec2 cls_one(vec2 p, float f) {
  //sorry this is unreadable
  float f2 = f*f; //sq
  float cmn = 8.*f2*f2;
  float x = sin(p.x*f)/f;
  float pp = ((-4.*p.y-4.)*f2 + 1.)/cmn;
  float qq = -x/cmn;
  float sol = cubicRoot(pp, qq);

  x = asin(clamp(sol*f,-1.,1.))/f;
  return vec2(x,-cos(f*2.*x));
}

vec3 sine_SDF(vec2 p, float freq) {
  float wavelen = PI/freq;

  //map p to be within the principal half cycle
  float cell = round(p.x/wavelen)*wavelen;
  float sgn = sign(cos(p.x*freq));
  p.x = (p.x-cell)*sgn;

  vec2 off = vec2(-PI/freq/2.,0);
  //approximate either side of the principal half cycle with
  //the distance to the 2nd chebyshev polynomial of the 1st kind
  vec2 a = -off+cls_one(off+p, freq/2.);
  vec2 b =  off-cls_one(off-p, freq/2.);

  //pick closest, comment out to see how the one-sided approximation looks
  if (length(p-b) < length(p-a)) a = b;

#ifdef ONE_NEWTON_STEP
  //newton's method update via lagrange multipliers
  //visually very close after one step, but more increases accuracy quadratically
  vec3 K = vec3(a,p.x-p.y);

  //it might be possible to simplify this a lot...
  vec3 lagrange = vec3(2.*(K.x-p.x)+K.z*-cos(K.x*freq)*freq,
      2.*(K.y-p.y)-K.z,
      K.y+sin(K.x*freq));
  K -= (inverse(mat3(2.-K.z*-sin(K.x*freq)*freq*freq,0,cos(K.x*freq)*freq,0,2,1,-cos(K.x*freq)*freq,-1,0))*lagrange);
  a = K.xy;
  a.y = -sin(a.x*freq);
#endif

  float dist = length(p-a)*sign(p.y+sin(p.x*freq));
  //map the closest point back to global coordinates
  a.x *= sgn; a.x += cell;
  return vec3(dist,a);
}

vec2 rotate(vec2 uv, float rot) {
  // algebra formula for rotation by matrix , https://en.wikipedia.org/wiki/Rotation_matrix
  mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
  // rotation of uv with matrix algebra formula where is set the rotation angle 
  return m * uv;
}

// ohhhhhh inigo quilez what would i do without you...

float sdCircle(vec2 p, float r) {
  return length(p) - r;
}

float sdTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2) {
  vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
  vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
  vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
  vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
  vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
  float s = sign( e0.x*e2.y - e0.y*e2.x );
  vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                    vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                    vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
  return -sqrt(d.x)*sign(d.y);
}

float sdOrientedVesica( vec2 p, vec2 a, vec2 b, float w ) {
  float r = 0.5*length(b-a);
  float d = 0.5*(r*r-w*w)/w;
  vec2 v = (b-a)/r;
  vec2 c = (b+a)*0.5;
  vec2 q = 0.5*abs(mat2(v.y,v.x,-v.x,v.y)*(p-c));
  vec3 h = (r*q.x<d*(q.y-r)) ? vec3(0.0,r,0.0) : vec3(-d,0.0,d+w);
  return length( q-h.xy) - h.z;
}

// polynomial smooth min
float smin(float a, float b, float k) {
  float h = max( k-abs(a-b), 0.0 )/k;
  return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float rand(vec2 n) { 
  return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noisenoise(vec2 p){
  vec2 ip = floor(p);
  vec2 u = fract(p);
  u = u*u*(3.0-2.0*u);

  float res = mix(
    mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
    mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
  return res*res;
}

const mat2 mtx = mat2( 0.80,  0.60, -0.60,  0.80 );

float fbm(vec2 p, float time) {
  float f = 0.0;

  f += 0.500000*noisenoise( p + time  ); p = mtx*p*2.02;
  f += 0.031250*noisenoise( p ); p = mtx*p*2.01;
  f += 0.250000*noisenoise( p ); p = mtx*p*2.03;
  f += 0.125000*noisenoise( p ); p = mtx*p*2.01;
  f += 0.062500*noisenoise( p ); p = mtx*p*2.04;
  f += 0.015625*noisenoise( p + sin(time) );

  return f/0.96875;
}

float pattern(in vec2 p, float time) {
	return fbm(p + fbm(p + fbm(p, time), time), time);
}

vec4 maple(vec2 uv, vec2 pos, float rot, float scale) {
  uv -= pos;
  uv *= scale;
  uv += pos;

  uv.x += sin(uv.y * PI) * 0.05;

  vec2 p = uv;

  uv -= pos;
  uv = rotate(uv, rot);
  uv.x = -abs(min(uv.x, -uv.x));
  uv += pos;

  float round = 0.05;

  float d = 9e9;
  //d = smin(d, sdOrientedVesica(uv, pos, pos + vec2(-0.35, 0.0), 0.06), round);
  d = smin(d, sdOrientedVesica(uv, pos, pos + vec2(-0.305, 0.15), 0.04), round);
  d = smin(d, sdOrientedVesica(uv, pos, pos + vec2(-0.18, 0.3), 0.04), round);
  d = smin(d, sdOrientedVesica(uv, pos + vec2(-0.07, 0.02), pos + vec2(-0.3, 0.3), 0.08), round);
  d = smin(d, sdOrientedVesica(uv, pos, pos + vec2(0, 0.45), 0.1), round);
  d = smin(d, sdOrientedVesica(uv, pos + vec2(0.0, 0.1), pos + vec2(-0.065, 0.4), 0.02), round);
  d = smin(d, sdOrientedVesica(uv, pos + vec2(0.0, 0.1), pos + vec2(-0.1, 0.34), 0.05), round);

  float rad = 0.01;
  float alpha = smoothstep(rad, rad - 15. / iResolution.x, d);

  vec3 col = vec3(1.0, 0.3, 0.0);
  col -= max(0.02 - d, 0.0) * 1.0;

  // outline
  col = mix(vec3(0.1), col, clamp(smoothstep(0.01, 0.05, rad - d), 0.0, 1.0));

  col += (0.28 - length(vec2(p.x * 0.8, p.y) - vec2(-0.1, 0.5))) * 0.2;

  return vec4(col, alpha);
}

vec4 eye(vec2 uv, vec2 pos, float t) {
  uv -= pos;
  vec2 uvI = uv;

  float blink = 1.0 - pow(1.0 - ((sin(t * PI * 2.0)) * 0.5 + 0.5), 150.0);
  //float blink = 1.0;

  uv.y /= blink;

  float size = 0.14 - blink * 0.02;
  vec2 eyePos = vec2(uv.x, uv.y + sign(uv.y) * size);

  float rad = 0.2;
  float dist = length(eyePos);
  bool inside = dist < rad;

  float alpha = smoothstep(rad + 10. / iResolution.x, rad, dist);
  vec3 col = vec3(1.0);

  col -= smoothstep(0.8, 1.2, length(mix(uv, uvI, 0.6) * vec2(0.75, 1.0) - vec2(0.0, -0.1)) * 7.0) * 0.2;

  vec2 irisPos = vec2(noise(vec2(t * 1.3, 0.0)), noise(vec2(0.0, -t * 1.3))) * 0.006;
  float irisDist = 1.0 - length(irisPos - uvI) / 0.2;
  float irisMix = smoothstep(0.6, 0.8, irisDist);
  col = vec3(mix(col.r, 0.0, mix(irisMix, sqrt(irisMix), 0.3)), mix(col.g, 0.0, mix(irisMix, sqrt(irisMix), 0.8)), mix(col.b, 0.0, sqrt(irisMix)));

  return vec4(col.rgb, alpha);
}

vec4 limb(vec2 uv, float t, float shift) {
  if ((uv.y - uv.x) > 0.2) return vec4(0.0);
  uv += vec2(0.1, -0.73);
  float rad = 0.01 + noise(vec2(uv.y, shift)) * 0.01 + smoothstep(-0.3, -0.1, uv.y) * 0.1;
  float dist = abs(sine_SDF((vec2(uv.y, uv.x) + vec2(0.0, -0.5)) * 2.0, 2.75 + sin(t * PI * 2.0) * 0.07).x);
  float alpha = smoothstep(rad + 50. / iResolution.x, rad, dist);
  return vec4(vec3(0.0), alpha);
}
vec4 limb2(vec2 uv, float t, float shift) {
  //if (uv.x > 0.15) return vec4(0.0);
  vec2 p = uv;
  uv += vec2(-0.55, 0.55);
  float rad = 0.01 + noise(vec2(uv.y, shift)) * 0.01;
  float dist = abs(sine_SDF((uv + vec2(0.0, -0.5)) * 2.0, 1.25 + sin(t * PI * 2.0) * 0.07).x);
  float alpha = smoothstep(rad + 50. / iResolution.x, rad, dist);
  return mix(vec4(vec3(0.0), alpha), vec4(0.0), clamp((p.x - 0.13) / (0.13 - 0.1), 0.0, 1.0));
}
vec4 limb3(vec2 uv, float t, float shift) {
  //if (uv.x > 0.4) return vec4(0.0);
  vec2 p = uv;
  uv += vec2(-0.9, 0.77);
  float rad = 0.01 + noise(vec2(uv.y, shift)) * 0.01;
  float dist = abs(sine_SDF((uv + vec2(0.0, -0.5)) * 2.0, 1.55 + sin(t * PI * 2.0 - 0.5) * 0.07).x);
  float alpha = smoothstep(rad + 50. / iResolution.x, rad, dist);
  return mix(vec4(vec3(0.0), alpha), vec4(0.0), clamp((p.x - 0.41) / (0.5 - 0.41), 0.0, 1.0));
}

vec4 head(vec2 uv, vec2 pos, float shift, float t) {
  uv -= pos;

  float dist = abs(uv.x) + abs(uv.y) + length(uv) * 0.2;
  float rad = 0.36;

  float alpha = smoothstep(rad + 15. / iResolution.x, rad, dist);
  vec3 col = mix(vec3(0.1), vec3(0.5), clamp(smoothstep(0.01, 0.04, rad - dist), 0.0, 1.0));
  col += ((0.28 + sin(shift) * 0.02) - length(vec2(uv.x * 0.8, uv.y) - vec2(-0.15, 0.2)));

  vec4 l = maple(uv, vec2(0.0, 0.15), sin(t * PI * 2.0 - 1.5) * 0.2, 1.2);

  return vec4(mix(l.rgb, col, alpha), max(alpha, l.a));
}

vec4 leaf(vec2 uv, vec2 pos, float angle, float scale, vec3 baseCol) {
  uv -= pos;

  uv *= scale;
  uv = rotate(uv, angle);
  uv += vec2(0.15, 0.0);

  float size = 0.14;
  vec2 eyePos = vec2(uv.x, uv.y + sign(uv.y) * size);

  float rad = 0.2;
  float dist = length(eyePos);
  bool inside = dist < rad;

  float alpha = smoothstep(rad + 15. / iResolution.x, rad, dist);
  vec3 col = baseCol;

  col -= smoothstep(0.8, 1.2, length(rotate(uv, -angle) + vec2(0.05, -0.1)) * 7.0) * 0.1;

  float shadeScale = 1.0;
  float xDist = shadeScale * 0.5 - (length(uv - vec2(0.0, 0.48)) * shadeScale);

  col -= smoothstep(0.98, 1.0, shadeScale - abs(xDist)) * 0.1;

  // outline
  col = mix(vec3(0.1), col, clamp(smoothstep(0.0, 0.013, rad - dist), 0.0, 1.0));

  return vec4(col, alpha);
}

#define rgb(r,g,b) (vec3(r,g,b) / 255.0)

// see viewpalette.html
#define PALETTE_SIZE 8
vec3 colors[PALETTE_SIZE] = vec3[](
  rgb(201, 204, 161),
  rgb(202, 160, 90),
  rgb(174, 106, 71),
  rgb(139, 64, 73),
  rgb(84, 51, 68),
  rgb(81, 82, 98),
  rgb(99, 120, 125),
  rgb(142, 160, 145)
);

vec3 getPalette(vec3 col) {
  // comparasion
  float minDist = 9e9;
  vec3 newCol;

  for (int i = 0; i < PALETTE_SIZE; i++) {
    vec3 compareCol = colors[i];

    vec3 distVec = abs(col - compareCol);
    float dist = (distVec.r + distVec.g + distVec.b) / 3.0;

    if (dist < minDist) {
      newCol = compareCol;
      minDist = dist;
    }
  }

  return newCol;
}


float Bayer2(vec2 a) {
  a = floor(a);
  return fract(a.x/2. + a.y*a.y * .75);
}

#define Bayer4(a)   ( Bayer2( .5*(a))/4. + Bayer2(a) )
#define Bayer8(a)   ( Bayer4( .5*(a))/4. + Bayer2(a) )
#define Bayer16(a)  ( Bayer8( .5*(a))/4. + Bayer2(a) )
#define Bayer32(a)  ( Bayer16(.5*(a))/4. + Bayer2(a) )
#define Bayer64(a)  ( Bayer32(.5*(a))/4. + Bayer2(a) )

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord.xy / iResolution.xy;

  float t = fract(iTime * 0.2);
  float shift = t * 5.0;

  float itimer = fract(t + 0.2);
  float downslope = pow(max(cos(itimer * 2.0 * PI * 0.188) * 2.0 - 1.0, 0.0), 0.6);
  float downslope2 = max(pow(max(cos(fract(itimer + 0.05) * 2.0 * PI * 0.188) * 2.0 - 1.0, 0.0), 0.6), 0.0);
  float upslope = pow(max(cos(itimer * 2.0 * PI * 2.0) + itimer - 1.0, 0.0), 0.5);
  float intense = max(downslope, upslope);

  // square & center
  uv -= 0.5;
  uv.x /= iResolution.y / iResolution.x;
  uv += 0.5;

  if (uv.x < 0.0 || uv.x > 1.0) {
    fragColor = vec4(vec3(0.0), 1.0);
    return;
  }

  // pix
  float pix = 256.0;
  uv = floor(uv * pix) / pix + 0.001;

  //uv.x += noise(uv * 2.0 + 0.5 * shift) * 0.003;
  //uv.y += noise(uv * 2.0 - 0.5 * shift) * 0.003;

  float fbmnoise = fbm(uv * 2.0, sin(t * PI * 2.0));

  // uncenter & unsquare
  vec2 puv = uv;
  puv -= 0.5;
  puv.x *= iResolution.y / iResolution.x;
  puv += 0.5;

  vec2 wuv = puv + vec2(sin(fbmnoise * 2.0 * PI), cos(fbmnoise * 2.0 * PI)) * 0.14 * (1.0 - intense);
  float disp = 0.05 * (1.0 - intense);
  vec3 bgcol = (
    texture(iChannel0, wuv + vec2(disp, disp)).rgb +
    texture(iChannel0, wuv + vec2(-disp, disp)).rgb +
    texture(iChannel0, wuv + vec2(disp, -disp)).rgb +
    texture(iChannel0, wuv + vec2(-disp, -disp)).rgb
  ) / 4. - vec3(0.02);

  vec3 chromacol = vec3(0.0, 0.25, 1.0);

  vec3 col = chromacol;

  vec4 ll = limb(vec2(1.0 - uv.x, uv.y), t, shift);
  col = mix(col, ll.rgb, ll.a);
  vec4 ll2 = limb2(uv, t, shift);
  col = mix(col, ll2.rgb, ll2.a);
  vec4 ll3 = limb3(uv, t, shift);
  col = mix(col, ll3.rgb, ll3.a);

  vec2 hpos = vec2(0.5, 0.6);
  vec2 hmov1 = vec2(sin(t * PI * 2.0), cos(t * PI * 2.0));
  vec2 hmov2 = vec2(cos(t * PI * 2.0), sin(t * PI * 2.0 + 1.0));
  vec2 hmov = vec2(noise(hmov1.xy * 0.2 + 10.0), noise(hmov2.xy * 0.2 + 5.0));
  vec2 huv = rotate((uv + hmov * 0.03) - hpos, sin(t * PI * 2.0) * 0.1) + hpos;
  vec4 h = head(huv, hpos, intense, t);
  col = mix(col, h.rgb, h.a);

  vec4 e = eye(huv, hpos, t);
  col = mix(col, e.rgb, e.a);

  vec4 l1 = leaf(uv, vec2(0.5 + sin(t * PI * 2.0) * 0.02, 0.2 + sin(t * PI * 2.0) * 0.01), -sin(t * PI * 2.0 + 0.9) * 0.2 + 0.3 + PI, 1.0, vec3(1.0, 0.5, 0.1));
  col = mix(col, l1.rgb, l1.a);
  vec4 l2 = leaf(uv, vec2(0.15 + sin(t * PI * 2.0 - 0.5) * 0.04, 0.1), sin(t * PI * 2.0 - 1.2) * 0.2 - 0.5 + PI, 1.5, vec3(1.0, 0.3, 0.1));
  col = mix(col, l2.rgb, l2.a);
  vec4 l3 = leaf(uv, vec2(0.8 + sin(t * PI * 2.0) * 0.02, 0.08 + sin(t * PI * 2.0) * 0.01), -sin(t * PI * 2.0 + 0.1) * 0.2 + 0.4 + PI, 1.7, vec3(1.0, 0.55, 0.1));
  col = mix(col, l3.rgb, l3.a);

  float chroma = smoothstep(length(col - chromacol), 0.0, 0.5) / 0.3;

  float posterize = 7.0;
  col += mix(
    hash13(vec3(uv.xy * iResolution.xy, shift)) * 1.4,
    Bayer64(uv.xy * pix + shift) * 0.5,
    intense
  ) * (1. / posterize);
  //col = floor(col.rgb * posterize + hash13(vec3(uv.xy * iResolution.xy, shift)) * 0.5) / posterize;
  //col = floor(col.rgb * posterize + Bayer64(uv.xy * pix + shift) * 0.5) / posterize;
  col = getPalette(col);
  //col = floor(col.rgb * posterize) / posterize;

  col = pow(col, vec3(mix(1.0, 2.5 + hash11(t * 50.0) * 1.0, 1.0 - intense)));

  float bgblend = mix(chroma, 1.0, pow(intense, 1.5));
  float fuckening = mix(bgblend, min(bgblend, 1.0 - e.a), 1.0 - downslope2);

  vec3 back = mix(bgcol, vec3(0.0), 0.2);

  fragColor = vec4(mix(back, col, fuckening), 1.0);
}

