        if(faceID==0){

            //vec3 p = convert2DTo3D(uv);      
            vec3 p = vec3(uv, 0);      

            col = funcFace0(p);

            preCalc = 1;

        }

    }

    // If precalculation has already occurred, read in the texture.
    if(preCalc == 0) col = tx0(iChannel0, uv);



    // Update the cubemap faces.
    fragColor = col;

}

