#define U(u) texelFetch(prevBuffer, ivec2(u), 0) //
const float FOCUS_DIST = .45;

// https://iquilezles.org/articles/distfunctions2d/
float sdParabola( in vec2 pos, in float k )
{
    if (pos.x == 0.) pos.x = .01; // fix for horizontal line (bug?)
    pos.x = abs(pos.x);
    float ik = 1.0/k;
    float p = ik*(pos.y - 0.5*ik)/3.0;
    float q = 0.25*ik*ik*pos.x;
    float h = q*q - p*p*p;
    float r = sqrt(abs(h));
    float x = (h>0.0) ? 
        pow(q+r,1.0/3.0) - pow(abs(q-r),1.0/3.0)*sign(r-q) :
        2.0*cos(atan(r,q)/3.0)*sqrt(p);
    return length(pos-vec2(x,k*x*x)) * sign(pos.x-x);
}

float map(vec2 p)
{
    p.x = -abs(p.x);

    float d = -sdParabola(p.yx + vec2(.0, .7), 1.);
    d = max(d, abs(p.y) - .3);
    return d;
}

#define keyClick(ascii)   ( texelFetch(keyBuffer,ivec2(ascii,1),0).x > 0.)

#define KEY_SPACE 32

const float BOUND_GAMMA = .01; // damping factor for boundary

float initWave(vec2 u, vec2 c, vec2 R)
{
    vec2 delta = u - c;
    float d2 = dot(delta, delta);

    return 1e-6 * R.x * cos(sqrt(d2 * .0001)) / (1. + d2 * .2);
}

void updateBuffer(out vec4 O, vec2 u, vec2 R, int iFrame, vec4 iMouse, sampler2D prevBuffer, sampler2D keyBuffer, bool addWaves)
{
    vec2 o = vec2(1, 0),
         p = (u - .5 * R) / R.y;

    vec4 state = texelFetch(prevBuffer, ivec2(0), 0);

    // handle persistent state
    if (ivec2(u) == ivec2(0))
    {
        if (iFrame == 0 ||
            state.w != R.x * R.y ||
            keyClick(KEY_SPACE))
        {
            O = vec4(0);
            O.w = R.x * R.y;
            return;
        }

        O = state;

        // timing is relative to horizontal dimension, to allow first bounce to complete before next wave encroaches
        if (addWaves && ++O.z > R.y / 1.5)
            O.z = 0.;

        return;
    }

    if (iFrame == 0 ||
        state.w != R.x * R.y ||
        keyClick(KEY_SPACE))
    {
        // init
        O = vec4(0);
        return;
    }
    else
    {
        const float C = .5; // Courant

        O = U(u).xxyy; // copies the current value from x to y coordinate to record new previous, put current previous into z for us to use

        if (int(u.y) < 5)
            O.x = O.x - C * (O.x - U(u + o.yx).x) - BOUND_GAMMA*(O.x-O.z); // absorbing bottom boundary
        else if (int(u.y) == int(R.y) - 1)
            O.x = O.x - C * (O.x - U(u - o.yx).x) - BOUND_GAMMA*(O.x-O.z); // absorbing top boundary
        else if (int(u.x) == 0)
            O.x = O.x - C * (O - U(u + o)).x - BOUND_GAMMA*(O.x-O.z); // absorbing left boundary
        else if (int(u.x) == int(R.x) - 1)
            O.x = O.x - C * (O - U(u - o)).x - BOUND_GAMMA*(O.x-O.z); // absorbing right boundary
        else
            O.x = -O.z + 2.*O.x + C*(U(u + o) + U(u - o) + U(u + o.yx) + U(u - o.yx) - 4. * O).x; // apply wave equation
    }

    if (addWaves)
    {
        // add disturbance at left focal point
        if (state.z < 1. || iFrame == 0)
            O.y += initWave(u, vec2(-FOCUS_DIST, 0) * R.y + .5 * R.xy, R);

        // add disturbance for mouse click
        if (iMouse.z > 0. && iMouse.w > 0.)
            O.y += initWave(u, iMouse.xy, R);
    }

    // parabola boundary (reflecting)
    if (map(p) < 0.) O = vec4(0);
}

