#define sat(a) clamp(a, 0., 1.)

#define EPSILON    0.0001
#define SPHERE_POS    vec3(.0, .0, 0.)
#define SPHERE_RADIUS 1.
#define SPHERE_COLOR  vec3(1., 0.0, 0.0)
#define LIGHT_POS vec3(5.0, -7.0, 3.0)

vec3  eye = vec3(.0, .0, -7.);

float map( vec3 pos ) {
    return max(
        sdBox(pos, vec3(1.0, 1.0, 0.5)),
        sdSphere(pos, 1.0)
    );
}

vec3 gradiant( vec3 pos ) {
    vec3 offset = vec3(EPSILON, 0.0, 0.0);
    return normalize(vec3(
        map(pos - offset.xyy) - map(pos + offset.xyy),
        map(pos - offset.yxy) - map(pos + offset.yxy),
        map(pos - offset.yyx) - map(pos + offset.yyx)
    ));
}

vec3 lighting( vec3 pos ) {
    vec3 normal = gradiant(pos);
    vec3 dir = normalize(LIGHT_POS - pos);
    return sat(dot(normal, dir) * SPHERE_COLOR);
}

vec3 rayMarching( vec3 pos, vec3 dir ) {
    float dist = 0.0;
    for (int i = 0; i < 100; i++) {
        pos += dir * dist;
        dist = map(pos);
        if (dist < EPSILON) {
            break;
        }
    }

    if (dist < EPSILON) {
        return lighting(pos) + vec3(.1,.0,.0);
    } else {
        return vec3(0.0, 0.0, 0.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv  = (fragCoord - 0.5 * iResolution.xy) / iResolution.xx;
    vec3 dir = normalize(vec3(uv, 1.0));

    fragColor = vec4(rayMarching(rotateY(eye, iTime), rotateY(dir, iTime)), 1.0);
}

