#define PI2         6.28318530718

#define MIN_DIST    1e-4
#define MAX_DIST    75.


mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 a){return fract(sin(dot(a,vec2(27.609,57.583)))*4376.545);}
//@iq ALL THE SDFS
float box(vec3 p, vec3 b){vec3 d=abs(p)-b;return length(max(d,0.))+min(max(d.x,max(d.y,d.z)),.0);}
float box(vec2 p, vec2 b){vec2 d=abs(p)-b;return length(max(d,0.))+min(max(d.x,d.y),.0);}

//globals s = saved / g = global
vec3 s_hit,g_hit;
vec2 s_id,g_id;
float travelspeed = 0.;

//scales for all things
const float scale = 1./.245;
const float scale_h = scale*.5;
const vec2 s = vec2(scale)*2.;
const float bscale = scale*.46;
const vec2 pos = vec2(.5,-.5);
const vec2[4] ps4 = vec2[4](pos.yx,pos.xx,pos.xy,pos.yy);

// 4 tap technique @Shane
vec2 map(vec3 q3){
    vec2 res = vec2(1e5,0.);

    q3.xz += vec2(travelspeed,-travelspeed);

    vec2 p,
         ip,
         id = vec2(0),
         ct = vec2(0);

    float t=1e5;

    for(int i =0; i<4; i++){
        ct = ps4[i]/2. -  ps4[0]/2.;
        p = q3.xz - ct*s;
        ip = floor(p/s) + .5;
        p -= (ip)*s;
        vec2 idi = (ip + ct)*s;

        float px = sin(idi.x*.873)*.275;
        float py = cos(idi.y*.9325)*.265;
        float ah = -floor((px+py)*scale)*scale;
        float hs = hash21(idi)*2.;
        ah+=.2*sin(T*hs);
        vec3 q = vec3(p.x,q3.y+9.-ah,p.y);

        // just a box
        float b = box(q,vec3(bscale))-.125;
        if(b<t) {
            t = b;
            s_id = idi+float(i)+hs;
            s_hit = q;
        }

    }

	if(t<res.x) res = vec2(t,2.);

    return res;
}

vec2 marcher(vec3 ro, vec3 rd, inout vec3 p, inout bool hit, int steps) {
    hit = false; float d=0., m = 0.;
    for(int i=0;i<steps;i++) {
        p = ro + rd * d;
        vec2 t = map(p);
        if( t.x<d*MIN_DIST) hit = true;
        d += i<32? t.x*.25:t.x*.75;
        m  = t.y;
        if(d>MAX_DIST) break;
    } 
    return vec2(d,m);
}

vec3 getFace(int face, vec3 p, inout vec3 mask) {

    vec3 color = vec3(.5);
    float px = 10./R.x;
    vec2 uv = fract(p.xy/scale_h)-.5;
    vec2 id = floor(p.xy/scale_h);

    float rnd = hash21(id+g_id.x+id.y);
    float hsh = fract(rnd*47.31);
    if(rnd>.5) uv.x*=-1.;

    vec2 gx = length(uv-.5)<length(uv+.5)? vec2(uv-.5):vec2(uv+.5);

    float cc = length(gx)-.5;
    if(hsh>.85) cc = min(length(uv.x),length(uv.y));
    cc=abs(cc)-.125;

    mask=vec3(cc,0,1);
    color = mix(color, vec3(.15),smoothstep(px,-px,cc));

    return vec3(1.-color);
}

// based on bmp mapping from @Passion
// https://www.shadertoy.com/view/ld3yDn
vec3 doBumpMap( vec3 p, vec3 n, float bf, float per, int face, inout vec3 mask){
    vec2 e = vec2(per*MIN_DIST, 0);  
    mat3 m = mat3( 
        getFace(face, p - e.xyy,mask), 
        getFace(face, p - e.yxy,mask), 
        getFace(face, p - e.yyx,mask)
    );
    vec3 g = vec3(.299,.587,.114) * m; 
    g = (g-dot(getFace(face, p,mask),vec3(.299,.587,.114)))/e.x; 
    g -= n*dot(n,g);  
    return normalize(n+g*bf);
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec3 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, int bnc, inout float d) {

    vec3 RC=vec3(0);
    vec3 p = ro;
    float m = 0.;
    bool hit = false;

    vec2 ray = marcher(ro,rd,p,hit,95);
    d = ray.x;
    m = ray.y;
    g_hit = s_hit;
    g_id = s_id;

    if(hit)
    {
        vec3 n = normal(p,d);
        vec3 lpos =  vec3(-8.,11,8.);
        vec3 l = normalize(lpos);

        vec3 h = vec3(.45);

        vec3 hp = g_hit;
        vec3 tn = n;

        vec3 cuv;
        int face;

        if(m==2.){
            tn = n;
            //@Shane https://www.shadertoy.com/view/3sVBDd
            //finding the face of a cube using normal
            vec3 aN = abs(tn);
            ivec3 idF = ivec3(tn.x<-.25? 0 : 5, tn.y<-.25? 1 : 4, tn.z<-.25? 2 : 3);
            face = aN.x>.5? idF.x : aN.y>.5? idF.y : idF.z;

            // set coords
            if(face==0) cuv = hp.yzx;
            if(face==1) cuv = hp.zxy;
            if(face==2) cuv = hp.xyz;
            if(face==3) cuv = hp.xyz;
            if(face==4) cuv = hp.xzy;
            if(face==5) cuv = hp.yzx;

            // get bump map surface
            vec3 mask = vec3(0);

            n=doBumpMap(cuv,n,.5,d,face,mask);

            vec3 tx1 = vec3(.667,.286,.031);
            vec3 tx2 = texture(iChannel0,cuv.xy*.5).rgb;

            mask.x=smoothstep(.011,.01,mask.x);

            h=mix(h,tx1*tx2,mask.z);
            h=mix(h,tx2,mask.x);

            ref =min(mask.x,mask.z)<.5? h:vec3(.0001);
        }

        float diff = clamp(dot(n,l),.1,1.);
        float shdw = 1.;

        for( float t=.01; t < 16.; ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 12.*h/t);
            t += h*.4;
            if( shdw<MIN_DIST ) break;
        }

        diff = mix(diff,diff*shdw,.65);

        float spec = .65 * pow(max(dot(normalize(p-ro),reflect(l,n)),.2),24.);

        RC = (h*diff) + min(spec,shdw);

        ro = p+n*.05;
        rd = reflect(rd,n);

    } else {
        RC = vec3(.05);
    } 

    return RC;
}

const vec3 FC = vec3(0.059,0.090,0.102);
void mainImage( out vec4 O, in vec2 F )
{   
    travelspeed=T*.5;

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,16);
    vec3 rd = normalize(vec3(uv, -1.));

    // mouse //
    float x = M.xy==vec2(0)||M.z<0. ? .07+.02*sin(T*.1) : -(M.y/R.y*.25-.125)*PI;
    float y = M.xy==vec2(0)||M.z<0. ? 0. : -(M.x/R.x*2.-1.)*PI;

    mat2 rx =rot(-.68+x),ry =rot(.58+y);

    ro.zy*=rx;rd.zy*=rx;
    ro.xz*=ry;rd.xz*=ry;

    vec3 C=vec3(0),RC=vec3(0),ref=vec3(0),fill=vec3(1),p = ro;
    float d = 0., fA = 0.;

    // reflection loop + stop unroll
    for(int i = 0; i < 2 + min(iFrame, 0); i++){
        RC = render(ro,rd,ref,2-i,d);
        C = mix(C,clamp(C+RC,vec3(0),vec3(1)),fill);
        fill *= ref; 
        if(i==0)fA=d;
    }
    // fog dusting
    C = mix(C,FC, 1.-exp(-.000025*fA*fA*fA));
    C=pow(C, vec3(.4545));
    O = vec4(C,1);
}

// https://blog.csdn.net/weixin_28710515/article/details/89533138
const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPISILON = 1e-4;

float sphereSDF(vec3 samplePoint){
    return length(samplePoint)-1.0;
}
float sceneSDF(vec3 samplePoint){
    return sphereSDF(samplePoint);
}
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
    float depth = start;
    for(int i=0;i<MAX_MARCHING_STEPS;i++){
        float dist = sceneSDF(eye + marchingDirection*depth);
        if(dist < EPISILON) return depth;
        depth += dist;
        if(depth >= end) return depth;
    }
    return depth;
}
vec3 rayDirection(float fov, vec2 size, vec2 fragCoord){
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, -z));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rd = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 ro = vec3(0.0, 0.0, 5.0);
    float dist = shortestDistanceToSurface(ro, rd, MIN_DIST, MAX_DIST);

    if(dist > MAX_DIST - EPISILON){
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;
    }
    // Output to screen
    fragColor = vec4(1.0,0.0,0.0,1.0);
}

