const vec2 target_resolution = vec2(940, 86);

#define TAU 6.28318530718
#define MAX_ITER 5

float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	float time = iTime * .3 + 23.0;
    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5; 

    vec2 uv0 = uv; 
    uv0.x *= 5.5;
    uv0.y *= 1.;
    uv0.y += iTime * 0.1;

    vec2 p = mod(uv0*TAU, TAU) - 250.0;

	vec2 i = vec2(p);
	float c = 1.0;
	float inten = .009;

    //pattern control
    float iTimeCtrl = iTime * 0.12;

    float scale = 2.0; 
    float timetest = -sin((iTimeCtrl * 1.0/scale - 0.25) * 3.1415926 * 4.0/2.0) * 0.5 + 0.5;
    float endPattern = mix(5.0, 3.5, uv.y + pow(1.0-uv.y, 5.0));
    float pattern = mix(3.0, endPattern, timetest * 1.2);

    //waterpatterntilingpattern
	for (int n = 0; n < int(MAX_ITER); n++) 
	{
		float t = time * (1.0 - (2.5/ float(n+1)));
        float t1= time * .2;
		i = p + vec2(cos(t1 - i.x) + sin(t + i.y), sin(t - i.y) + cos(t1 + i.x));
		c += 1.0/length(vec2(p.x / (sin(i.x+ t1)/inten),p.y / (cos(i.y+t)/inten)));
	}

	c /= float(pattern);

    //1.12 
    //1. 1,
	c = 1.25 - pow(c, 1.1);

    float intensityblue = mix(13.0, 9.0, timetest);
    vec3 blueChange = mix(vec3(0.000,0.212,0.302), vec3(0.000,0.349,0.502), timetest);
    //7.5
	vec3 colour = vec3(pow(abs(c), intensityblue));
    colour = clamp(colour + blueChange, 0.0, 1.0);

	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = 2.0 / iResolution.xy;
	uv *= 2.0;
	float f = floor(mod(iTime*.5, 2.0)); 	// Flash value.
	vec2 first = step(pixel, uv) * f;		   	// Rule out first screen pixels and flash.
	uv  = step(fract(uv), pixel);				// Add one line of pixels per tile.
	colour = mix(colour, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y); // Yellow line
	#endif

    //WhiteEdge
    float offset = uv.y + iTimeCtrl;
    float y = mod(offset, scale);

    float d = smoothstep(0.2 * scale, 0.0  * scale, y) * 1.0;
    float d1 = smoothstep(0.9  * scale, 1.0  * scale, y) * 1.0;
    vec3 maskColor =  vec3(d) + vec3(d1);

    //maskColor *= pow(1.0 - uv.y, 2.0);   //test0 0-1.0
    //maskColor *= pow(1.0 - uv.y, 2.0)/2.0 + 0.5; //test1 0.5-1.0
    maskColor *= smoothstep(0.0, 1.0, pow(1.0 - uv.y, 1.0) + 0.15) * 0.8; //test2 0.2-1.0

    float near = pow(uv.y, 3.5) * mix(0.75, 0.5, timetest);
    vec3 frontEdge = vec3(near);

    fragColor.rgb = colour;
    fragColor.rgb = colour + maskColor + frontEdge ; 

    //mask
    float mask = pow(uv.y, 1.0);
    //0.492
    mask = smoothstep(0.0,0.472,uv.y);

    //fragColor *= (mask);
    //fragColor = vec4(endColor, 1.0);
    //fragColor = vec4(frontEdge + maskColor,1.0);

    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}

