float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdGyroid(vec2 p, float f, float z) {
    return sin(f*p.x)*cos(f*p.y) + sin(f*p.y)*cos(f*z) + sin(f*z)*cos(f*p.x);
} 

float SceneDist(vec2 p) {
    float d = sdGyroid(p, 20.0, 0.025*iTime);
    float t = sin(0.5 * iTime) * 0.02;

    return d + t;

}

vec2 GetNormal(vec2 p) {

    float epsilon = 0.0001;
    vec2 dx = vec2(epsilon, 0.);
    vec2 dy = vec2(0., epsilon);

    float ddx = SceneDist(p + dx) - SceneDist(p - dx);
    float ddy = SceneDist(p + dy) - SceneDist(p - dy);

    return normalize(vec2(ddx, ddy));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution .xy)/iResolution.y;
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y / 2.0;

    if (iMouse.z == 0.0) { m.x = 0.0; };

    float f = 0.1;
    float d = SceneDist(uv) * 255.;

    float t = d * 0.5;
    float fac = 0.5 * cos(f * d) + 0.5; // creating ripples & shifting from 0 to 1

    vec4 solidColor = vec4(0.1, 0.1, 0.1, 1.0);

    vec4 col1 = vec4(1.0);
    vec4 col2 = vec4(0.85, 0.85, 0.85, 1.0);
    vec2 n = GetNormal(uv);
    vec4 col3 = vec4(n, 1.0, 1.0);

    vec4 prettyColor = mix(col1, col3, fac); 

    fragColor = mix(solidColor, prettyColor, smoothstep(0.0,1.0,d) );

}

// Panotools Lens Distortion

// Open-Source License:
// GPL v3

// Fragment Shader Based Upon:
// aleklesovoi's "CRT TV Shader" Shadertoy Example (https://www.shadertoy.com/view/DldXWS)
// Natron OpenFX-Misc (/openfx-misc/Distortion/DistortionModel.cpp)
// Blender Tracking Brown-Conrady Distortion Model (https://archive.blender.org/developer/D9037)

vec2 PanotoolsDistortion(in vec2 uv)
{
    // Distortion
    float a = 0.0;
    float b = 0.346;
    float c = 0.0;

    // Center Offset
    float cx = 0.0;
    float cy = 0.0;

    vec2 offset = vec2(cx / iResolution.x, cy / iResolution.y);

    // Panotools uses UV coordinates with a [-1:1] range
    uv = ((uv + offset) * 2.0) - 1.0;

    // Compute the distortion
    float x2 = uv.x * uv.x;
    float y2 = uv.y * uv.y;

    float r2 = x2 + y2;
    float d = 1.0 - (a + b + c);
    float r = sqrt(r2);
    float scale = (a * r2 + c) * r + b * r2 + d;

    uv.x *= scale;
    uv.y *= scale;

    // Transform the UV coordinates back to a [0:1] range
    uv = (uv * 0.5) + 0.5;

    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Use a "Screen Space" UV Coordinate [0;1] range
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Apply the lens distortion
    uv = PanotoolsDistortion(uv);
    vec3 result = texture(iChannel0, uv).rgb;

    // Return the final output
    fragColor = vec4(result, 1.0);
}

#define BLOOM_PASS iChannel1

// https://github.com/GarrettGunnell/AcerolaFX/blob/main/Shaders/AcerolaFX_Tonemapping.fx
const mat3 ACESInputMat = mat3(
    vec3(0.59719, 0.35458, 0.04823),
    vec3(0.07600, 0.90834, 0.01566),
    vec3(0.02840, 0.13383, 0.83777)
);

const mat3 ACESOutputMat = mat3(
    vec3( 1.60475, -0.53108, -0.07367),
    vec3(-0.10208,  1.10813, -0.00605),
    vec3(-0.00327, -0.07276,  1.07602)
);

vec3 RRTAndODTFit(vec3 v) {
    vec3 a = v * (v + 0.0245786f) - 0.000090537f;
    vec3 b = v * (0.983729f * v + 0.4329510f) + 0.238081f;
    return a / b;
}

vec3 HillACES(vec3 col) {
    col = col * ACESInputMat;
    col = RRTAndODTFit(col);
    return clamp(col * ACESOutputMat, 0.0, 1.0);
}

vec2 getTile(vec2 p, float tile)
{
    float tileWidth = 1.-exp2(-floor(tile));
    return vec2((p)/exp2(floor(tile))/2.+vec2(tileWidth, 0));
}

vec4 computeBloom(sampler2D bloomTex, vec2 uv, float mip, float power, float str, float threshold)
{
    vec4 albedo = vec4(0);
	for(float i = 0.; i < mip; i++)
	{
        vec4 col = max(vec4(0), texture(bloomTex, getTile(uv.xy, i)) - threshold);
		albedo += col * exp2(-power*i);
	}
    return albedo * str;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 outputColor = texture(iChannel0, uv, 0.0).rgb + computeBloom(BLOOM_PASS, uv, 6.0, 0.7, 0.5, 0.0).rgb;

    fragColor = vec4(HillACES(outputColor), 1.0);
    //fragColor = log(texture(iChannel1, uv) + 1.0)*4.0;
}

