#define csgInit() float r = 1e30
#define csgEnd() return r
#define csgUnion(d) r = min(r, d)
#define csgMirrorX(p) vec2(abs(p.x), p.y)
#define csgMirrorY(p) vec2(p.x, abs(p.y))

float sdSegment(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdRing(vec2 p, vec2 o, float r)
{
    return abs(distance(p, o) - r);
}

float sdPoint(vec2 p, vec2 o)
{
    return distance(p, o);
}

float sdPointedEggRing(vec2 p, vec2 o, float r, float h)
{
    return abs(p.y - o.y > 0.0? length(vec2(abs(p.x), p.y) - o + vec2(h, 0.0)) - (r+h) : length(p - o) - r);
}

float sdCubicBezier(vec2 uv, vec2 s, vec2 c1, vec2 c2, vec2 e, float res)
{
    float r = 1e20;
    float st = 1./res;
    vec2 prev = s;
    for(float i = 0.; i < 1.; i += st)
    {
        float j = 1.-i;
        vec2 point = j*j*j*s+3.*j*j*i*c1+3.*j*i*i*c2+i*i*i*e;
        r = min(r, sdSegment(uv, prev, point));
        prev = point;
    }
    r = min(r, sdSegment(uv, prev, e));
    return r;
}

float sdGlyph(vec2 uv, int type)
{
    csgInit();
    csgUnion(sdRing(uv, vec2(0), 1.0));
    switch(type)
    {
        case 0:
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, 1), vec2(0.15, 0.7)));
            csgUnion(sdRing(uv, vec2(0, 0.66), 0.154));
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, 0.506), vec2(0.953, -0.3)));
            csgUnion(sdSegment(uv, vec2(0.953, -0.3), vec2(-0.953, -0.3)));
            csgUnion(sdSegment(uv, vec2(0, 0.506), vec2(0, -1)));
            csgUnion(sdSegment(uv, vec2(-0.1, 0), vec2(0.1, 0.1)));
            csgUnion(sdSegment(uv, vec2(-0.1, -0.1), vec2(0.1, 0)));
            csgEnd();
        case 1:
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, 1), vec2(0.4, 0.6)));
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0.4, 0.6), vec2(0.0, -0.3)));
            csgUnion(sdSegment(uv, vec2(0.953, -0.3), vec2(-0.953, -0.3)));
