vec3 glyph(vec2 uv, float thickness, int type)
{
    float sdf = sdGlyph(uv, type);
    return smoothstep(0.02, 0.0, sdf - thickness) * colors[type];
}

vec2 transformCoordinates(in vec2 uv, float scale, float rotA, float rotB)
{
    uv *= rotMat(rotA);
    uv.x /= scale;
    uv *= rotMat(rotB);
    return uv;
}

vec3 glyphLayer(vec2 uv, float prob, float time, float speed)
{
    vec2 cid = floor(uv);
    vec4 cellRand0 = hash42(cid) * vec4(vec3(40.0), 1.0);
    vec4 cellRand1 = hash42(cid + 10.0);
    vec4 cellRand2 = hash42(cid + 20.0);
    float scale = 3.0 + cellRand2.x*3.0;
    vec2 cp = scale*fract(uv)-scale*0.5;
    if (cellRand1.w > prob)
    {
        return vec3(0.0);
    }
    return glyph(transformCoordinates(cp, sin(time*speed*(1.0 + cellRand1.x) + cellRand0.x), time*speed*0.5*cellRand1.y + cellRand0.y, -time*speed + cellRand0.z), 0.01, int(cellRand0.w * 4.0));
}

vec3 glyph3d(vec2 uv, vec2 offset)
{
    float scale = 0.5;
    vec3 col = vec3(0);
    for(float i = 0.0; i < 12.0; i++)
    {
        col += glyphLayer(uv*scale + offset + hash21(scale)*100.0, 0.5, iTime, 1.);
        scale *= 1.2;
    }
    return col;
}

vec3 glyphRow(vec2 uv, float time)
{
    uv.y += time;
    float xrand = hash12(floor(uv.yy));
    uv.x += time * (xrand-0.5)*4.0;
    float rand = hash12(floor(uv));
    return glyph(3.0 * fract(uv) - 1.5, 0.01, int(rand*4.));
}

