    float mip = exp2(floor(log2(1.-uv.x)));
    vec2 rot = vec2(1, 0);
    vec2 ratio = vec2(iResolution.y/iResolution.x, 1);
    for(float o = 0.; o < H_BLUR_RES; o++)
