         + hash12(fragCoord+fract(iTime))-0.5
        #endif
        )/H_BLUR_RES)*2.-1.;
        float w = exp(-5.0*t*t);
        m += w;
        vec2 newCoords = uv.xy + H_BLUR_SIZE*rot*t*ratio;
        #if DO_MIPMAP_CHECK == 1
        float newCoordsMip = exp2(floor(log2(1.-newCoords.x)));
        if(newCoordsMip == mip)
        #endif
            a += texture(iChannel0, newCoords)*w;
    }

	fragColor = a/m;
    fragColor = mix(texture(iChannel1, uv), fragColor, 0.3);
