    vec3(0.59719, 0.35458, 0.04823),
    vec3(0.07600, 0.90834, 0.01566),
    vec3(0.02840, 0.13383, 0.83777)
);

const mat3 ACESOutputMat = mat3(
    vec3( 1.60475, -0.53108, -0.07367),
