            csgUnion(sdSegment(uv, vec2(0, 0.506), vec2(0, -1)));
            csgUnion(sdSegment(uv, vec2(-0.1, 0), vec2(0.1, 0.1)));
            csgUnion(sdSegment(uv, vec2(-0.1, -0.1), vec2(0.1, 0)));
            csgEnd();
