            csgUnion(sdSegment(uv, vec2(0, 1), vec2(0, -0.3)));
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, -0.3), vec2(0.15, -0.45)));
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0.15, -0.45), vec2(0, -0.6)));
            csgEnd();
        case 2:
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, -0.2), vec2(0.25, 0.05)));
            csgUnion(sdRing(uv, vec2(0, -0.5), 0.3));
            csgUnion(sdPoint(uv, vec2(0, -0.5)));
            csgUnion(sdSegment(csgMirrorX(uv), vec2(0, 0.4), vec2(0.25, 0.65)));
            csgUnion(sdSegment(uv, vec2(0.1, 0.5), vec2(-0.1, 0.5)));
            csgUnion(sdSegment(uv, vec2(-0.25, 0.65), vec2(0.25, 0.65)));
            csgUnion(sdSegment(uv, vec2(0, -0.2), vec2(0, 0.4)));
            csgEnd();
        case 3:
            csgUnion(sdPointedEggRing(uv, vec2(0.0, -0.5), 0.5, 0.95));
            csgUnion(sdRing(uv, vec2(0.0, -0.5), 0.5));
            csgUnion(sdPoint(uv, vec2(0.0, -0.5)));
