}

float map(vec2 uv)
{
    return sdGlpyh(uv, int(iTime) % 4);
}

vec3 colorSdf(vec2 uv, vec4 m)
{
    #define PERIOD 200.
    #define STR 0.2
    #define STROKE_WIDTH 0.01
    #define MOUSE_WIDTH 0.005
    float d = map(uv);
    float px = fwidth(d);
    float md = abs(map(m.xy));
    vec3 col = mix(vec3(0.094,0.494,0.988), vec3(1.000,0.082,0.980), sign(d)*0.5+0.5);
    col *= smoothstep(-px*PERIOD, px*PERIOD, cos(d*PERIOD))*STR+1.-STR;
    col = mix(col, vec3(1), smoothstep(STROKE_WIDTH+px, STROKE_WIDTH-px, abs(d)));
    if(m.z > 0.)
    {
        float mdc = smoothstep(px, -px, min(abs(distance(m.xy, uv)-md+MOUSE_WIDTH)-MOUSE_WIDTH, distance(m.xy, uv)-MOUSE_WIDTH*2.));
        col = mix(col, vec3(1.000,0.875,0.000), mdc);
    }
    col /= d+1.;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec4 m = vec4((iMouse.xy*2.-iResolution.xy)/iResolution.y, iMouse.zw);
    float px = 2.0/iResolution.y;

    vec3 col = vec3(colorSdf(uv*1.2, m*1.2));

