vec3 pow3(float d, float phase) {
    float basepower = 2.0 + cos(phase * 0.321) * 0.85;
    float PI = 3.14159;

    float offset = 0.08;

    vec3 offsets = 0.4 * phase + offset * vec3(
                cos(phase + PI * 2.0 / 3.0),
                cos(phase + PI * 4.0 / 3.0),
                cos(phase));

    vec3 powers = vec3(basepower, basepower, basepower);

    return vec3(pow(d, powers.x) + offsets.x, 
                pow(d, powers.y) + offsets.y, 
                pow(d, powers.z) + offsets.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
