#define NUM_HEXAGON 32.
#define RADIUS 0.05
#define ROTATION 20.
#define LINE_SIZE 0.005



const float PI = 3.14;


float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float eps = min((2. + iTime*0.25), 9.) / iResolution.y;


    vec3 finalColor = vec3(0.03);


    for(float i = 1.; i < NUM_HEXAGON+1.; i++) {
        //radius of each hexagon
        float r = RADIUS * i /** (iTime/2.)*/;
        //rotation mat2 of each hexagon
        mat2 rot = rotationMatrix(iTime*ROTATION*i);

        float sdistance = sdHexagon(uv * min(iTime*0.05, 0.8) * rot, r);
        float res = smoothstep(LINE_SIZE, eps, abs(sdistance));
        vec3 color = 0.5 + 0.5 * cos(iTime + vec3(0,1,2) + i/8.);
        finalColor = mix(color, finalColor, res);
    }   


    fragColor = vec4(finalColor,1.0);
}

