float shiftEq(float a, float b, float c,
              float x, float y, float z)
{
                       float r =-1.;
    if(a==x && b==y && c==z){r = 0.;}
    if(c==x && a==y && b==z){r = 1.;}
    if(b==x && c==y && a==z){r = 2.;}
    return r;
}
