uint packUnorm4x8(in vec4 unpacked)
{
    uvec4 utex = uvec4(unpacked * 255.0);
    return ((utex.x << 24) | (utex.y << 16) | (utex.z << 8) | (utex.w));
}

vec4 unpackUnorm4x8(in uint packed)
{
    return vec4(
        float((packed & 4278190080u) >> 24),
        float((packed & 16711680u) >> 16),
        float((packed & 65280u) >> 8),
        float((packed & 255u))
    ) * 0.0039215686;
}

image_code:
float hash1(vec2 p) {
	p = 57.0 * fract(p * 1.4142135623);
	return fract(p.x * p.y);
}

float noise(vec2 x) {
	vec2 p = floor(x);
	vec2 w = fract(x);
    vec2 s = vec2(1.,0.);

	float a = hash1(p + s.yy);
	float b = hash1(p + s.xy);
	float c = hash1(p + s.yx);
	float d = hash1(p + s.xx);

	return 2.*(a + (b - a) * w.x + (c - a) * w.y + (a - b - c + d) * w.x * w.y);
}

float c01(float p) {
	return clamp(p, 0.0, 1.0);
}

const mat2 m = mat2(0.8, 0.6, -0.6, 0.8);

// https://iquilezles.org/articles/fbm/
float fbm(vec2 x) {
	float f = 1.9;
	float s = 0.55;
	float a = 0.0;
	float b = 0.5;
	for (int i = 0; i < 5; i++) {
		vec2 offset = vec2(1.0, 1.5) * float(i);
		float n = noise(x + offset);
		a += b * n;
		b *= s;
		x = f * m * x;
	}
	return a;
}

float sdfPlane(vec3 p) {
	vec3 p2 = p;
	return p.y + fbm(p2.xz * 0.8) * cos(iTime / 2.0);
}

float sdf(vec3 p) {
	return sdfPlane(p + vec3(10., 2., 10.));
}

vec3 calcNormal(vec3 pos) {
	vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
	return normalize(
		e.xyy * sdf(pos + e.xyy) + e.yyx * sdf(pos + e.yyx) + e.yxy * sdf(pos + e.yxy) + e.xxx * sdf(pos + e.xxx)
	);
}

vec3 normal(vec3 point) {
	const vec3 smallStep = vec3(0.00001, 0, 0);

	return normalize(
		vec3(
			sdf(point + smallStep.xyy) - sdf(point - smallStep.xyy),
			sdf(point + smallStep.yxy) - sdf(point - smallStep.yxy),
			sdf(point + smallStep.yyx) - sdf(point - smallStep.yyx)
		)
	);
}

float raymarch(vec3 rayOrigin, vec3 rayDirection) {
	float distance = 0.0;
	float maxDistance = 50.0;
	float minHitDistance = 0.001;

	for (int i = 0; i < 256; i++) {
		if (distance > maxDistance) break;

		vec3 pos = rayOrigin + rayDirection * distance;

		float res = sdf(pos);

		if (res < minHitDistance) return distance + res;

		distance += res * 0.4;
	}

	return -1.0;
}

float shadow(vec3 ro, vec3 rd, float mint, float tmax) {
	float res = 1.0;
	float t = mint;
	for (int i = 0; i < 256; i++) {
		float h = sdf(ro + rd * t);

		if (t > tmax) return res;
		if (h < 0.001) return 0.0;
		res = min(res, 18.0 * h / t);
		t += h;
	}
	return res;
}

vec3 directionalLight(
	vec3 rayDirection,
	vec3 normal,
	vec3 point,
	vec3 baseColor,
	vec3 lightDir,
	vec3 lightColor,
	float shininess
) {
	lightDir = normalize(lightDir);
	vec3 hal = normalize(lightDir - rayDirection);
	float diffuse = dot(normal, lightDir);
	diffuse = c01(diffuse);
	diffuse *= shadow(point, lightDir, 0.02, 2.5);

	float pho = c01(pow(dot(normal, hal), shininess));
	float spe = pho * diffuse * 0.3;

	return baseColor * 2.2 * diffuse * lightColor + 5.0 * spe * lightColor * 0.4;

}

vec3 lightning(float distance, vec3 col, vec3 point, vec3 rayDirection, vec3 rayOrigin, vec3 normal) {
	vec3 newColor = col;

	newColor += directionalLight(rayDirection, normal, point, col, vec3(2.0, 2.0, 2.0), vec3(0.902, 0.902, 0.902), 32.0);

    return newColor / 2.;
}

vec3 render(vec3 rayOrigin, vec3 rayDirection) {
	vec3 col = vec3(0.0);

	float hit = raymarch(rayOrigin, rayDirection);

	if (hit > 0.0) {
		vec3 point = rayOrigin + rayDirection * hit;
		vec3 normal = calcNormal(point);

        col =  vec3(0.3451, 0.3176, 0.3098);
		col = lightning(hit, col, point, rayDirection, rayOrigin, normal);
	}

	return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
	float camspeed = .4 * iTime;
	vec3 ta = vec3(0.0);
	vec3 ro = vec3(5.2 * cos(camspeed), 3.0, 5.2 * sin(camspeed));

	vec3 cf = normalize(-ro);
	vec3 cs = normalize(cross(cf, vec3(sin(0.0), cos(0.0), 0.0)));
	vec3 cu = normalize(cross(cs, cf));

	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 rd = normalize(p.x * cs + p.y * cu + 2.5 * cf);

	vec3 col = render(ro, rd);
	col = pow(col, vec3(1.0 / 2.2));

	fragColor = vec4(col, 1.0);
}

