void mainImage (out vec4 O, vec2 u)
{  
    vec3 m, r, o, C = vec3(u/=iResolution.x, 1); 
    float t = iTime, c = fract (.04*(t-u.x)), i;
    O *= i;
    r.z = t;
    for ( ++o.y; i++<98.; o += C * (length (cos (o + t)) - .3))
        m.xy = .55 - mat2(-m.y,m.x,m)*m.yx+(u-.4)*cos(.3*t),
        r += C*(length(cos(r))-.6);
    u -= .4;
    for (ivec4 b; (b.x^b.y^b.z)%99 > b.z-7; )
        b = ivec4 ( t*vec2(2,4) + 6.*u*O.a, O+=.1 );
    O.rgb =
        c < .2 ? o/50.
      : c < .3 ? max ( u = fract( u/dot(u,u)-t ), u.x-u ).yyy
      : c < .5 ? m
      : c < .7 ? fract(r*3.) - (r.z-t)/30.
      : ( c < .8 ? texture(iChannel1, vec2(t + .3/length(u), atan(u.yx, u)/3.14))
      :  O/74.).rgb;
}

