vec3 palette( float t ) {
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b * cos(6.28318*(c*t*d));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y; 
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 3.0; i++){
        uv = fract(uv *1.5) -0.5;

        float d = length(uv)* exp(-length(uv0));

        vec3 col = palette(length(uv0) + i * 0.4 +iTime * 0.4);

        d = sin(d * 8.0 + iTime)/8.0;
        d = abs(d);

        d = 0.01/d;

        finalColor += col * d;

        }

        fragColor = vec4(finalColor,1.0);
   }

vec3 palette(float t) {
  vec3 a = vec3(0.5, 0.5, 0.5);
  vec3 b = vec3(0.75, 0.5, 0.5);
  vec3 c = vec3(1.0, 0.55, 0.60);
  vec3 d = vec3(0.8, 0.333, 0.667);

  return a + b * cos(6.28318 * t + c - d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
  vec2 uv0 = uv * (iTime - 5.0) * uv.yx;
  vec3 fCol = vec3(0.0);

  for (float i = 0.0; i < 3.0; i++) {
    float d = length(uv + (0.1 / iTime));

    d = sin(d * 8.0 + iTime) / 8.0;
    d *= cos(5.0 / iTime);
    d *= sin(iTime * 0.02);
    uv.y -= d + cos(iTime * 0.15); // Up/Down
    uv.x /= iResolution.y + sin(iTime / d + uv.y); // Left/Right

    d = abs(d) / 0.2;
    // Time varying pixel color
    vec3 col = palette(length(uv0) + iTime * 4.0);
    d = 0.2 * d;

    col *= d;
    fCol -= sin(iTime * col) - d;
  }

  uv += (iTime * uv.x) + (iTime - uv.y);

  // Apply audio data from channel 1
  vec2 audioUV = vec2(iTime * 0.2, iTime * 0.3); // Adjust the scaling factor as needed
  float audioData = texture(iChannel0, audioUV + (iTime + 0.2)).r;

  fCol *= audioData + iTime * 0.5;

  // Apply fractal coloring
  fCol -= (0.5 * 0.25) * iTime + sin(fCol / (iTime - 2.0) + vec3(0.0, 3.0, 4.0));

  // Output to screen
  fragColor = vec4(fCol, 1.0);
}

float sdfCircle(vec2 uv, float r, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    return length(vec2(x, y)) - r;
}

float sdfTriangle ( in vec2 p, in float r)
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );

    return-length(p)*sign(p.y);
}

vec2 offset(float t, float r, float o) 
{
    vec2 rv = vec2(sin(iTime*t - o), cos(iTime*t - o))*r;
    return rv *(sin(iTime*t)* 0.4 + 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy -0.5;
    uv.x *= iResolution.x/iResolution.y;

    float eps = 4.  /iResolution.y;

    vec3 col = vec3(0.15);
    for (float i = 1.; i < 7.; i++) {
        float circle = smoothstep(0., eps, sdfCircle(uv, 0.05, offset(2., 0.3, i)));
        vec3 color = 0.5 + 0.5 * i * cos(iTime + uv.xyx + vec3(0,2,4));
        col = mix(color, col, circle);
    }

    float triangle = smoothstep(0., eps, sdfTriangle(uv,(2.+sin(iTime*2.))*0.1-0.05));

    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0,2,4));

    col = mix(color, col, triangle);

    fragColor = vec4(col,1.0);
}

#define PI 3.1415926536
#define LINE_INTENSITY .5
#define GLOW_INTENSITY 1.1
#define COLOR_CHANGE_SPEED .1

/* TODOs:
 * - implement more shapes (tri-, penta-, hexa-, octagon)
 * - reduce brightness blow-out of bright colors
 */

float noise21(vec2 p){
    return fract(sin(p.x*100.+p.y*8943.)*5647.);
}

float smoothNoise(vec2 uv){
    vec2 localUv = smoothstep(.0, 1., fract(uv));
    vec2 id = floor(uv);

    float bottomLeft = noise21(id);
    float bottomRight = noise21(id+vec2(1.,.0));
    float bottom = mix(bottomLeft, bottomRight, localUv.x);

    float topLeft = noise21(id+vec2(.0,1.));
    float topRight = noise21(id+vec2(1.,1.));
    float top = mix(topLeft, topRight, localUv.x);

    return mix(bottom, top, localUv.y);
}

float smoothNoiseLayers(vec2 uv, float amountOfLayers){
    float result = .0;
    float sumOfAmplitudes = .0;
    float frequency = 4.;
    float amplitude = 1.;
    for(float i = .0; i < amountOfLayers; i++){
        result += smoothNoise(uv*frequency)*amplitude;
        sumOfAmplitudes += amplitude;
        frequency *= 2.;
        amplitude /= 2.;
    }
    return result/sumOfAmplitudes;
}

vec2 rotateUv(vec2 uv, float rotation){
    vec2 cosVal = uv * cos(rotation);
    vec2 sinVal = uv * sin(rotation);
    return vec2(cosVal.x +sinVal.y, cosVal.y -sinVal.x);
}

vec2 remapRotateUv(vec2 uv, vec2 newOrigin, float rotation){
    vec2 newPoint = rotateUv(newOrigin, rotation);
    vec2 movingUv = uv -newPoint;
    return movingUv;
}

float glowyOutline(float sdfValue, vec2 point){
    float noiseOffset = .5-smoothNoiseLayers(point +(iTime *.2), 5.);
    sdfValue += noiseOffset * .02;
    noiseOffset *= .1;
    return pow(.01 *(LINE_INTENSITY +noiseOffset) /abs(sdfValue), 1. /(GLOW_INTENSITY +noiseOffset));
}

vec3 runeCircle(vec2 point, float size, vec3 color){
    float sdf = length(point) -size;
    return color *glowyOutline(sdf, point);
}

vec3 runeSquare(vec2 point, float size, vec3 color){
    vec2 dist = abs(point) -vec2(size);
    float outerDistance = length(max(dist, .0));
    float innerDistance = min(max(dist.x, dist.y), .0);
    float sdf = outerDistance +innerDistance;
    return color *glowyOutline(sdf, point);
}

vec3 getColor(){
    return .5 +.5 *cos(iTime *COLOR_CHANGE_SPEED +vec3(0, 2, 4));
}

vec3 getComplementaryColor(){
    vec3 color = getColor();
    float minVal = min(color.r, min(color.g, color.b));
    float maxVal = max(color.r, max(color.g, color.b));
    return vec3(maxVal - minVal)-color;
}

vec3 glowySeal(vec2 uv){
    float piFourth = PI /4.;

    float randomBrightnessReduction = .9 +smoothNoiseLayers(uv +iTime, 5.) *.1;
    vec3 col = getColor() * randomBrightnessReduction;
    vec3 inverseCol = getComplementaryColor() * randomBrightnessReduction;

    vec3 finalColor = vec3(.0);
    finalColor += runeSquare(rotateUv(uv, iTime), .6, col);           // outer square
    finalColor += runeSquare(rotateUv(uv, iTime +piFourth), .6, col); // outer square (offset)
    finalColor += runeSquare(rotateUv(uv, -iTime), .35, inverseCol);  // inner square
    finalColor += runeCircle(uv, .7, col);                            // outer circle
    finalColor += runeCircle(uv, .42, inverseCol);                    // inner circle
    for(float i = .0; i < 8.; i++){                                   // small circles on the outside
        finalColor += runeCircle(remapRotateUv(uv, vec2(.6), -iTime -piFourth *i), .15, col);
    }

    return finalColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy *2. -1.;
    uv.x *= iResolution.x /iResolution.y;
    fragColor = vec4(glowySeal(uv), 1.0);
}

// CC0: A space flower?
//  @byt3m3chanic doing pretty cool with variable cell size truchets.
//  Didn't really understand what he did so I tried something inspired by it
//  I both failed and succeeded. It didn't turn out as intended but kind of neat anyway.


// See @byt3m3chanic's tweet: https://twitter.com/byt3m3chanic/status/1676035945320046592?s=20

// Music: Fearbace & Nostre - Still Isolated
//  Which I happened to be listening to at the time.


#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define REV(x)      exp2((x)*zoom)
#define FWD(x)      (log2(x)/zoom)

const float zoom = log2(1.3);

#define REP 26.0
#define KALEIDOSCOPE

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

float smoothKaleidoscope(inout vec2 p, float sm, float rep) {
  vec2 hp = p;

  vec2 hpp = toPolar(hp);
  float rn = modMirror1(hpp.y, TAU/rep);

  float sa = PI/rep - pabs(PI/rep - abs(hpp.y), sm);
  hpp.y = sign(hpp.y)*(sa);

  hp = toRect(hpp);

  p = hp;

  return rn;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float segment(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p-a, ba = b-a;
  float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
  return length(pa - ba*h);
}

vec3 effect(vec2 p, vec2 pp) {
  vec2 op = p;
  float tm = TIME;
  float mtm = fract(tm);
  float ftm = floor(tm);

  mat2 rot = ROT(0.075*TIME); 

  const float rep = REP;
  const float sm  = 0.1*36.0/REP;
  p *= transpose(rot);
#if defined(KALEIDOSCOPE)
  float nn = smoothKaleidoscope(p, sm, rep);
#endif  
  p *= rot;
  p *= ROT(-0.5*length(p));
  p += 0.5*sin(vec2(1.0, sqrt(0.5))*TIME*0.08);


  float zz = REV(mtm);
  vec2 p2 = p;
  p2 /= zz;
  vec2 s2 = sign(p2);
  p2 = abs(p2);
  vec2 fp2 = FWD((p2));

  vec2 n = floor(fp2);
  float h = hash(s2.x+s2.y+n-ftm);

  vec2 x0 = REV(n);
  vec2 x1 = REV(n+1.0);

  vec2 m = (x0+x1)*0.5;
  vec2 w = x1-x0;

  vec2 modi = h > 0.5 ? vec2(1.0, 1.0) : vec2(1.0, -1.0);

  vec2 p3 = p2;
  p3 -= m;
  p3 = abs(p3);
  p3 -= 0.5*w;
  float d3 = length(p3);

  vec2 p4 = p2;
  p4 -= m;
  float d4 = segment(p4, -0.5*w*modi, 0.5*w*modi);
  d4 = min(d4, d4);
  d4 *= zz;

  float d6 = min(abs(p.x), abs(p.y));
  vec3 col = vec3(0.0);

  float fo =1.0 - exp(-10.0*(d6-0.02));
  float ll = length(pp);
  vec3 gcol4 = 0.0025*(1.0+cos(vec3(0.0, 1.0, 2.0)+TIME+TAU*h+ll));
  vec3 gcol6 = 0.005*(1.0+cos(vec3(0.0, 1.0, 2.0)+TIME+ll));

  col += fo*gcol4/max(d4, 0.001);
  col = clamp(col, 0.0, 1.0);
  col += gcol6/max(d6, 0.0001);
  col = clamp(col, 0.0, 1.0);
  col -= 0.05*vec3(0.0,1.0,2.0).zyx*(ll);
  col = sqrt(col);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  fragColor = vec4(col, 1.0);
}

// Constants
const float sqsize = 0.35; // Size
const float pi = 3.14159265;
const float tau = pi * 2.0;
const float dur = 600.0; // 10 x 10
// Function for rotating UV
mat2 rot2d(float angle) {
    float s = sin(angle); float c = cos(angle);
    return mat2(c, -s, s, c);
}
// Main
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Center UV, fix aspect ratio
    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv.x *= (iResolution.x / iResolution.y);
    // Rotate UV, create gradient
    vec2 sq_uv = uv * rot2d(iTime / dur * tau);
    float sq_rot = 0.5 - 0.5 * (atan(-sq_uv.x, -sq_uv.y) / pi);
    // Anti-alias the gradient
    float cgaa = length(fwidth(sq_rot));
    sq_rot *= smoothstep(1.0, 1.0 - cgaa * 2.0, sq_rot);
    // Set colors
    vec3 sq = vec3(0.0, 0.0, sq_rot);
    vec3 bg = vec3(0.0, 0.0, 1.0);
    // Mix colors based on anti-aliased box shape
    // This is excessive
    float aa = length(fwidth(uv));
    vec3 col = mix(sq, bg, smoothstep(sqsize, sqsize + aa,
    max(abs(uv.x), abs(uv.y))));
    // Final
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5, 0.5);
    uv.x *= iResolution.x/iResolution.y;
    uv *= (cos(iTime) + 1.2)*0.23;
    uv += vec2(0.5, 0.5);

    vec2 tc = uv*iChannelResolution[0].xy;
    vec2 fr = fract(tc);

    int split1 = int(iMouse.x*(iMouse.x/iResolution.x));
    float mx = iResolution.x-iMouse.x;
    int split2 = int(iResolution.x - mx*(mx/iResolution.x));

    vec3 col;
    if(int(fragCoord.x) == split1 || int(fragCoord.x) == split2) {
        col = vec3(1.0);
    } else {
        if(fragCoord.x < float(split1)) {
            ivec2 itc = ivec2(floor(tc + vec2(0.5, 0.5)));
            col = texelFetch(iChannel0, itc + ivec2(0, 0), 0).xyz;
        } else if(fragCoord.x < float(split2)) {
            ivec2 itc = ivec2(floor(tc));

            vec3 c0 = texelFetch(iChannel0, itc + ivec2(0, 0), 0).xyz;
            vec3 c1 = texelFetch(iChannel0, itc + ivec2(1, 0), 0).xyz;
            vec3 c2 = texelFetch(iChannel0, itc + ivec2(0, 1), 0).xyz;
            vec3 c3 = texelFetch(iChannel0, itc + ivec2(1, 1), 0).xyz;

            col =  mix(mix(c0, c1, fr.x), mix(c2, c3, fr.x), fr.y);
        } else {
            vec2 bn_res = iChannelResolution[1].xy;
            vec4 bn = texture(iChannel1, mod(fragCoord, bn_res) / bn_res);
            ivec2 itc = ivec2(floor(tc));

            if(bn.x > fr.x) {
                if(bn.y > fr.y) {
                    col = texelFetch(iChannel0, itc + ivec2(0, 0), 0).xyz;
                } else {
                    col = texelFetch(iChannel0, itc + ivec2(0, 1), 0).xyz;
                }
            } else {
                if(bn.y > fr.y) {
                    col = texelFetch(iChannel0, itc + ivec2(1, 0), 0).xyz;
                } else {
                    col = texelFetch(iChannel0, itc + ivec2(1, 1), 0).xyz;
                }
            }
        }
    }


    // Output to screen
    fragColor = vec4(col, 1.0);
}

