#define MIN_DIST    1e-4
#define MAX_DIST    55.

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){ return fract(sin(dot(p,vec2(26.34,45.32)))*4314.23); }

//@iq sdfs & extrude
float box(vec3 p, vec3 b){vec3 q=abs(p)-b;return length(max(q,0.))+min(max(q.x,max(q.y,q.z)),0.);}
float box(vec2 p, vec2 a){vec2 q=abs(p)-a;return length(max(q,0.))+min(max(q.x,q.y),0.);}
float cap(vec3 p, float h, float r){p.y-=clamp(p.y,0.,h);return length(p)-r;}
float opx(in float sdf, in float pz, in float h){vec2 w=vec2(sdf,abs(pz)-h);return min(max(w.x,w.y),0.)+length(max(w,0.));}

float lsp(float b, float e, float t){return clamp((t-b)/(e-b),0.,1.); }
float eoc(float t){return (t = t-1.)*t*t+1.; }

const float scale = .5;
const float tc = .0125;
const float amt = 2.;

float glow,tmod=0.,ga1=0.,ga2=0.,ga3=0.,ga4=0.,time = 0.,speed = 0.,movement;
mat2 r45,cam;
vec3 hit,hp;
vec2 sid,gid;

vec2 map (vec3 pos, float sg) {
    vec2 res=vec2(1e5,0.);

    pos.xz-=movement;
    pos*=scale;

    vec2 id= floor(pos.xz), q = fract(pos.xz)-.5; 
    float ck = mod(id.x+id.y,2.)*2.-1.;
    //mod vars to mix things up
    float m = floor(mod(id.y,3.)+1.);//1.75;//
    float n = floor(mod(3.+2.5*sin(id.x*.4),5.)+1.);//2.15;//
    //mod for long slices
    vec2 cd=vec2(mod(id.x,n),mod(id.y,m))*2.-1.;

    float rnd = hash21(id);
    if(rnd>.5) q.y=-q.y;

    float sn = (id.x*.95)+(id.y*1.25);
    sn = mod(sn,8.);

    float t1 = lsp(sn,sn+1.,tmod);
    float t2 = lsp(sn+4.,sn+5.,tmod);
    t1 = eoc(t1); t1 = t1*t1*t1;
    t2 = eoc(t2); t2 = t2*t2*t2;
    if(cd.y<.5 && cd.x<.5) q*=rot((t1+t2)*1.5707);

    float dir = (ck>.5^^rnd<.5)? 1.:-1.;

    float hs=fract(rnd*32.83);
    vec3 qp= vec3(q.x,pos.y,q.y);
    vec2 p = length(q.xy-.5) < length(q.xy+.5) ? q.xy-.5 : q.yx+.5;

    // main pattern
    float d = abs(length(p)-.5)-tc;
    if(hs>.85) d = min(length(q.x),length(q.y))-tc;
    rnd=fract(rnd*32.83);

    float d1 = length(q)-.465;
    float d4 = min(length(vec2(q.x,abs(q.y)-.44)),length(vec2(abs(q.x)-.44,q.y)))-.028;
    float sw = 0.;

    if(cd.x>.5 ) { d = length(q.y)-tc;d4=length(vec2(abs(q.x)-.44,q.y))-.028;}
    if(cd.y>.5 ) { d = length(q.x)-tc;d4=length(vec2(q.x,abs(q.y)-.44))-.028;}
    if(cd.y>.5 && cd.x>.5) { d = 1.; d4 = 1.; sw = (t1-t2)*.25;}


    d=min(max(d,d1),d4);
    d= opx(d,qp.y-.1,.5);
    d1= opx(d1,qp.y-sw+.275,.3);

    if(rnd>.5&&hs<.85) { qp.xz*=r45;float c1 = box(qp-vec3(0,.35,0),vec3(1,.1,.2));d=max(d,-c1);}

    if(d<res.x) {
        res = vec2(d/scale,1.);
        hp = vec3(q.x,pos.y,q.y);
    }

    if(d1<res.x) {
        res = vec2(d1,5.);
        hp = vec3(q.x,pos.y,q.y);
    }

    rnd=fract(rnd*52.71);
    float lp = rnd>.925 ? length(vec3(q.x,pos.y-1.,q.y))-.1 : 1e5;
    if(sg==1. && (cd.y>.5 && cd.x>.5)) { glow += .00075/(.00075+lp*lp);}
    if(lp<res.x&& (cd.y>.5 && cd.x>.5)) {
        res = vec2(lp,6.);
        hp = pos;
    }

    float f = pos.y;
    if(f<res.x) {
        res = vec2(f,2.);
        hp = pos;
    }

    return res;
}

//Tetrahedron technique
//https://iquilezles.org/articles/normalsSDF
vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h = vec2(1.,-1.)*.5773;
    return normalize( h.xyy*map( p + h.xyy*e,0.).x + 
					  h.yyx*map( p + h.yyx*e,0.).x + 
					  h.yxy*map( p + h.yxy*e,0.).x + 
					  h.xxx*map( p + h.xxx*e,0.).x );
}

vec2 marcher(vec3 ro, vec3 rd) {
    float d = 0., m = 0.;
    for(int i=0;i<128;i++){
        vec2 ray = map(ro + rd * d,1.);
        if(ray.x<MIN_DIST*d||d>MAX_DIST) break;
        d += i<64?ray.x*.35:ray.x*.85;
        m  = ray.y;
    }
    return vec2(d,m);
}

void mainImage(out vec4 fragColor,in vec2 F){

    r45=rot(.78539816339);
    speed = T*.4;
    movement = T*.43;
    tmod = mod(speed,13.);

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,8);
    vec3 rd = normalize(vec3(uv,-1));

    float x = M.xy==vec2(0) || M.z <0. ? .0 : (M.y/R.y * .1-.05)*PI;
    float y = M.xy==vec2(0) || M.z <0. ? .0 : (M.x/R.x * 1.-.5)*PI;

    float ff = .3*sin(T*.08);
    mat2 rx = rot(-.82-ff-x), ry = rot(.88+ff-y);

    ro.zy *= rx; ro.xz *= ry; 
    rd.zy *= rx; rd.xz *= ry;

    vec3 C = vec3(0),p= vec3(0);

    vec2 ray = marcher(ro,rd);
    float d=ray.x, m=ray.y;
    hit = hp;
    sid = gid;

    if(d<MAX_DIST)
    {
             p = ro + rd * d;
        vec3 n = normal(p,d);
        vec3 lpos =vec3(6.,12.,-12.);
        vec3 l = normalize(lpos-p);

        float diff = clamp(dot(n,l),.09,.99);
        float shdw = 1.;

        for( float t=.01; t < 16.; ) {
            float h = map(p + l*t,0.).x;
