            shdw = min(shdw, 12.*h/t);
            t += h*.4;
            if( shdw<MIN_DIST ) break;
        }

        diff = mix(diff,diff*shdw,.65);

        vec3 h = vec3(.1);
        if(m==1.) {
            vec2 hp = vec2(abs(hit.x),hit.y);
            vec3 clx = texture(iChannel0,vec2(abs(hit.x),hit.y)).rgb;
            vec3 clm = texture(iChannel0,vec2(abs(hit.z),hit.y)).rgb;
            h = clm*clx;
        }

        if(m==2.) h=texture(iChannel1,hit.xz*.5).rgb;

        if(m==5.) h=texture(iChannel0,abs(hit.xz)).rgb;

        if(m==6.) h=vec3(1);

        C = (diff*h);
    } 

    C = mix(C,vec3(.89),clamp(glow*.5,0.,1.));
    C = mix(C,vec3(.102,.086,.059),1.-exp(-.0005*d*d*d));
    C = pow(max(C,0.),vec3(.4545));

    fragColor = vec4(C,1);
}

#define rep1 0.7
#define rep2 0.5
#define rep rep1 + rep2
#define r1 rep1 / 3.
#define r2 rep2 / 3.

#define PI 3.14159265359
#define GRAD_METHOD 0

// === customizable functions ===

// HACK: as long as this shortly returns max/min
// after a small positive value, nothing will visually break :)
float fromsd(float x){
#if GRAD_METHOD == 0
    // normal
    return smoothstep(0.02, 0.0, x);
#elif GRAD_METHOD == 1
    // hole
    return smoothstep(-0.02, 0.0, x) * smoothstep(0.02, 0.0, x);
#elif GRAD_METHOD == 2
    // pyramid
    return (1. - smoothstep(-0.01, 0.01, sin(20.*2.*PI*(x)))) * smoothstep(0.02, 0.0, x);
#elif GRAD_METHOD == 3
    // don't know how to describe this
    return smoothstep(0.02, 0., x) + sin(20.*PI*clamp(x,-.1,.1))*abs(1./(10.*PI*x));
#else
    // neon, i guess
    return clamp(0.03/max(0., x + 0.03)-0.34, 0., 1.);
#endif
}

vec4 getcol(vec2 pos){
    vec2 posdiff = vec2(0.25 * sin(iTime / 5.), 0.0);
    return vec4(vec3(1.0, 0.0, 0.0) * (1. - length(pos + posdiff))
                + vec3(0.0, 1.0, 0.0) * (1. - length(pos))
                + vec3(0.0, 0.0, 1.0) * (1. - length(pos - posdiff)), 1.0);
}

// === main driver, for rotation and such ===
#define halfsqrt2 0.70710678118
#define scale 2.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *= scale;

    // 45deg
    mat2 invrot = mat2(halfsqrt2, -halfsqrt2, halfsqrt2, halfsqrt2);
    mat2 rot    = mat2(halfsqrt2, halfsqrt2, -halfsqrt2, halfsqrt2);
    uv = invrot * uv;

    vec2 uv_part = uv;
    uv_part.y = mod(uv_part.y, rep);
    vec2 uv_pos = uv - uv_part;

    float dx1 = iTime / 4.0;
    float dx2 = iTime / 2.0;
    vec2 cent1 = vec2(repCenter(rep1, uv_part.x - dx1) + dx1, rep1 / 2.0) + uv_pos;
    vec2 cent2 = vec2(repCenter(rep2, uv_part.x - dx2) + dx2, rep1 + rep2 / 2.0) + uv_pos;
    float intr1 = smoothstep(-0.3, 0.3, sin(-cent1.x * .4 + iTime));
    float intr2 = smoothstep(0.3, -0.3, sin(-cent2.x * .2 + iTime));
    float sd1 = sdInterpRep(rep1, r1, uv_part - vec2(dx1, rep1 / 2.0), intr1);
    float sd2 = sdInterpRep(rep2, r2, uv_part - vec2(dx2, rep1 + rep2 / 2.0), intr2);
    cent1 /= scale;
    cent2 /= scale;

    cent1 = rot * cent1;
    cent2 = rot * cent2;

    fragColor = getcol(cent1) * fromsd(sd1) + getcol(cent2) * fromsd(sd2);
}

