#define INTERP_TECHNIQUE 1

// https://iquilezles.org/articles/distfunctions2d/

float sdSquare(float r, vec2 pos){
    vec2 d = abs(pos) - r;
    return length(max(d, 0.)) + min(max(d.x, d.y), 0.);
}

float sdCirc(float r, vec2 pos){
    return length(pos) - r;
}

float sdInterp(float r, vec2 pos, float interp){
    float interp_c = clamp(interp, 0.0, 1.0);
#if INTERP_TECHNIQUE == 0
    // directly interpolating the SDFs
    return sdSquare(r, pos) * interp_c + sdCirc(r, pos) * (1. - interp_c);
#else
    // interpolate the square radius instead
    return sdSquare(r * (1. - interp_c), pos) - r * interp_c;
#endif
}

// Infinite.
// The reason why I modulo'd everything is because the sd functions kind of
// clash into each other.
// Reasonable if rep > 2 * r.
float sdInterpRep(float rep, float r, vec2 pos, float interp) {
    vec2 pos_2 = vec2(mod(pos.x + rep / 2.0, rep) - rep / 2.0, pos.y);
    return sdInterp(r, pos_2, interp);
}

float repCenter(float r, float x) {
    return round(x / r) * r;
}

