vec3 palette(float t) {
  vec3 a = vec3(0.5, 0.5, 0.5);
  vec3 b = vec3(0.75, 0.5, 0.5);
  vec3 c = vec3(1.0, 0.55, 0.60);
  vec3 d = vec3(0.8, 0.333, 0.667);

  return a + b * cos(6.28318 / iTime - (c + t - d));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv * (iTime - 5.0 / uv.yx);
    vec3 fCol = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {
        float d = length(uv + (0.1 / iTime));

        d = sin(d * 8.0 + iTime) / 8.0;
        d *= cos(5.0 / iTime);
        d *= sin(iTime * 0.02);
        uv.y -= d + cos(iTime * 0.15); // Up/Down
        uv.x /= iResolution.y + sin(iTime / d + uv.y); // Left/Right

        d = abs(d) / 0.2;
        // Time varying pixel color
        vec3 col = palette(length(uv0) + iTime * 4.0);
        d = 0.2 * d;

        col *= d;
        fCol += col - d;
    }

    uv += (iTime * uv.x) + (iTime - uv.y);

    // Apply audio data from channel 1
    vec2 audioUV = vec2(iTime * 0.2, iTime + 0.3); // Adjust the scaling factor as needed
    float audioData = texture(iChannel0, audioUV - (iTime + .2)).r;
    fCol *= audioData + iTime * .5;

    // Apply fractal coloring
    fCol *= (0.5 - 0.25) /iTime + sin(fCol / (iTime - 2.0) + vec3(0.0, 3.0, 4.0));

    // Output to screen
    fragColor = vec4(fCol, 1.0);
}

// Brown-Conrady Lens Distortion

// Open-Source License:
// GPL v3

// Fragment Shader Based Upon:
// aleklesovoi's "CRT TV Shader" Shadertoy Example (https://www.shadertoy.com/view/DldXWS)

// Blender Tracking Brown-Conrady Distortion Model "ApplyBrownDistortionModel()" function in "rna_tracking.c"
// https://archive.blender.org/developer/D9037

vec2 BrownConradyDistortion(in vec2 uv)
{
    // Radial Distortion
    float k1 = 0.3;
    float k2 = 0.1;
    float k3 = 0.0;
    float k4 = 0.0;

    // Tangential Distortion
    float p1 = 0.0;
    float p2 = 0.0;

    // Brown Conrady uses UV coordinates with a [-1:1] range
    uv = (uv * 2.0) - 1.0;

    // Compute the distortion
    float x2 = uv.x * uv.x;
    float y2 = uv.y * uv.y;

    float xy2 = uv.x * uv.y;
    float r2 = x2 + y2;

    float r_coeff = 1.0 + (((k4 * r2 + k3) * r2 + k2) * r2 + k1) * r2;
    float tx = p1 * (r2 + (2.0 * x2)) + (p2 * xy2);
    float ty = p2 * (r2 + (2.0 * y2)) + (p1 * xy2);

    uv.x *= r_coeff + tx;
    uv.y *= r_coeff + ty;

    // Transform the UV coordinates back to a [0:1] range
    uv = (uv * 0.5) + 0.5;

    // Use the distortion parameter as a scaling factor to keep the image resized as close as possible to the actual viewport dimensions
    float scale = abs(k1) < 1.0 ? 1.0 - abs(k1) : 1.0 / (k1 + 1.0);

    // Scale the image from center
    uv = (uv * scale) - (scale * 0.5) + 0.5;

    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Use a "Screen Space" UV Coordinate [0;1] range
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Apply the lens distortion
    uv = BrownConradyDistortion(uv);
    vec3 result = texture(iChannel0, uv).rgb;

    // Return the final output
    fragColor = vec4(result, 1.0);
}

// FBM Sphere (raycasting) by Julien Sulpis (https://twitter.com/jsulpis)
// https://www.shadertoy.com/view/mslBRr
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Sphere params
#define SPHERE_POSITION vec3(0, 1, 6)
#define SPHERE_RADIUS 2.
#define NOISE_STRENGTH .5
#define ROTATION_SPEED .1

// Ray casting
#define INFINITY 1e10

//===============================================//
//  Generic utilities stolen from smarter people //
//===============================================//

// https://iquilezles.org/articles/intersectors/
// sphere of size ra centered at point ce
float sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra ) {
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return INFINITY; // no intersection
    return -b - sqrt(h);
}

float noise(vec3 p) {
    return texture(iChannel0, p * .05).r;
}

// Comes from a course by SimonDev (https://www.youtube.com/channel/UCEwhtpXrg5MmwlH04ANpL8A)
// https://simondev.teachable.com/p/glsl-shaders-from-scratch
float fbm(vec3 p, int octaves, float persistence, float lacunarity, float exponentiation) {
  float amplitude = 0.5;
  float frequency = 2.0;
  float total = 0.0;
  float normalization = 0.0;

  for(int i = 0; i < octaves; ++i) {
    float noiseValue = noise(p * frequency);
    total += noiseValue * amplitude;
    normalization += amplitude;
    amplitude *= persistence;
    frequency *= lacunarity;
  }

  total /= normalization;
  total = total * 0.8 + 0.1;
  total = pow(total, exponentiation);

  return total;
}

// Inigo Quilez - https://www.shadertoy.com/view/4djSDy
float sphOcclusion(in vec3 pos, in vec3 nor, in vec3 origin, in float radius) {
  vec3 di = origin - pos;
  float l = length(di);
  float nl = dot(nor, di / l);
  float h = l / radius;
  float h2 = h * h;
  float k2 = 1.0 - h2 * nl * nl;

  // above/below horizon
  // EXACT: Quilez - https://iquilezles.org/articles/sphereao
  float res = max(0.0, nl) / h2;

  // intersecting horizon 
  if(k2 > 0.001) {
    // EXACT : Lagarde/de Rousiers - https://seblagarde.files.wordpress.com/2015/07/course_notes_moving_frostbite_to_pbr_v32.pdf
    res = nl * acos(-nl * sqrt((h2 - 1.0) / (1.0 - nl * nl))) - sqrt(k2 * (h2 - 1.0));
    res = res / h2 + atan(sqrt(k2 / (h2 - 1.0)));
    res /= 3.141593;
  }

  return res;
}

mat3 rotateY(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(//
    vec3(c, 0, s),//
    vec3(0, 1, 0),//
    vec3(-s, 0, c)//
  );
}

//================//
//  Project code  //
//================//

float fbmSphereDist(in vec3 ro, in vec3 rd) {
    float smoothSphereDist = sphIntersect(ro, rd, SPHERE_POSITION, SPHERE_RADIUS);

    vec3 intersection = ro + smoothSphereDist * rd;

    float fresnel = clamp(dot(rd, SPHERE_POSITION - intersection), 0., 1.);

    vec3 intersectionWithRotation = (intersection - SPHERE_POSITION) * rotateY(iTime * ROTATION_SPEED) + SPHERE_POSITION;
    float noise = fbm(intersectionWithRotation, 6, .5, 2., 4.5) * NOISE_STRENGTH * fresnel;


  return sphIntersect(ro, rd, SPHERE_POSITION, SPHERE_RADIUS + noise);
}


vec3 getNormal(vec3 p) {
  vec3 rd = SPHERE_POSITION - p;
  float dist = fbmSphereDist(p, rd);
  vec2 e = vec2(.001, 0);

  vec3 normal = dist - vec3(
    fbmSphereDist(p - e.xyy, rd), 
    fbmSphereDist(p - e.yxy, rd), 
    fbmSphereDist(p + e.yyx, rd)
  );
  return normalize(normal);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord / iResolution.xy - 0.5) * iResolution.xy / iResolution.y;

  vec3 ro = vec3(0, 1, 0);
  vec3 rd = normalize(vec3(uv, 1));

  // background
  vec3 color = vec3(0.01);

  // sphere
  float distance = fbmSphereDist(ro, rd);
  vec3 position = ro + rd * distance;
  float backgroundMask = step(INFINITY, distance);

  float sphereAO = sphOcclusion(position, getNormal(position), SPHERE_POSITION, SPHERE_RADIUS);
  vec3 sphereColor = vec3(sphereAO);

  color = mix(color, sphereColor, 1.0 - backgroundMask);

  // gamma
  color = pow(color, vec3(1.0 / 2.2));

  fragColor = vec4(color, 1.0);
}

// FBM Sphere (raymarching) by Julien Sulpis (https://twitter.com/jsulpis)
// https://www.shadertoy.com/view/ddsBRr
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Sphere params
#define SPHERE_POSITION vec3(0, 1, 6)
#define SPHERE_RADIUS 2.
#define NOISE_STRENGTH .5
#define ROTATION_SPEED .1

// Ray marching params
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

//===============================================//
//  Generic utilities stolen from smarter people //
//===============================================//

vec4 mod289(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
  return mod289(((x * 34.0) + 1.0) * x);
}

// https://github.com/yiwenl/glsl-fbm/blob/master/3d.glsl
float noise(vec3 p) {
  vec3 a = floor(p);
  vec3 d = p - a;
  d = d * d * (3.0 - 2.0 * d);

  vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
  vec4 k1 = permute(b.xyxy);
  vec4 k2 = permute(k1.xyxy + b.zzww);

  vec4 c = k2 + a.zzzz;
  vec4 k3 = permute(c);
  vec4 k4 = permute(c + 1.0);

  vec4 o1 = fract(k3 * (1.0 / 41.0));
  vec4 o2 = fract(k4 * (1.0 / 41.0));

  vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
  vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

  return o4.y * d.y + o4.x * (1.0 - d.y);
}

// Comes from a course by SimonDev (https://www.youtube.com/channel/UCEwhtpXrg5MmwlH04ANpL8A)
// https://simondev.teachable.com/p/glsl-shaders-from-scratch
float fbm(vec3 p, int octaves, float persistence, float lacunarity, float exponentiation) {
  float amplitude = 0.5;
  float frequency = 2.0;
  float total = 0.0;
  float normalization = 0.0;

  for(int i = 0; i < octaves; ++i) {
    float noiseValue = noise(p * frequency);
    total += noiseValue * amplitude;
    normalization += amplitude;
    amplitude *= persistence;
    frequency *= lacunarity;
  }

  total /= normalization;
  total = total * 0.8 + 0.1;
  total = pow(total, exponentiation);

  return total;
}

// Inigo Quilez - https://www.shadertoy.com/view/4djSDy
float sphOcclusion(in vec3 pos, in vec3 nor, in vec3 origin, in float radius) {
  vec3 di = origin - pos;
  float l = length(di);
  float nl = dot(nor, di / l);
  float h = l / radius;
  float h2 = h * h;
  float k2 = 1.0 - h2 * nl * nl;

  // above/below horizon
  // EXACT: Quilez - https://iquilezles.org/articles/sphereao
  float res = max(0.0, nl) / h2;

  // intersecting horizon 
  if(k2 > 0.001) {
    // EXACT : Lagarde/de Rousiers - https://seblagarde.files.wordpress.com/2015/07/course_notes_moving_frostbite_to_pbr_v32.pdf
    res = nl * acos(-nl * sqrt((h2 - 1.0) / (1.0 - nl * nl))) - sqrt(k2 * (h2 - 1.0));
    res = res / h2 + atan(sqrt(k2 / (h2 - 1.0)));
    res /= 3.141593;
  }

  return res;
}

mat3 rotateY(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(//
    vec3(c, 0, s),//
    vec3(0, 1, 0),//
    vec3(-s, 0, c)//
  );
}

