/**
* Standard SDF function for a sphere but with fbm noise added on the radius
*/
float sphereSDF(vec3 p) {
  vec3 position = (p - SPHERE_POSITION) * rotateY(iTime * ROTATION_SPEED) + SPHERE_POSITION;
  float noise = fbm(position, 6, .5, 2., 4.) * NOISE_STRENGTH;

  return length(position - SPHERE_POSITION) - (SPHERE_RADIUS + noise);
}

float rayMarch(vec3 ro, vec3 rd) {
  float dO = 0.;

  for(int i = 0; i < MAX_STEPS; i++) {
    vec3 p = ro + rd * dO;
    float dS = sphereSDF(p);
    dO += dS;
    if(dO > MAX_DIST || dS < SURF_DIST)
      break;
  }
  return dO;
}

vec3 getNormal(vec3 p) {
  float dist = sphereSDF(p);
  vec2 e = vec2(.001, 0);

  vec3 normal = dist - vec3(
    sphereSDF(p - e.xyy), 
    sphereSDF(p - e.yxy), 
    sphereSDF(p + e.yyx)
  );
  return normalize(normal);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord / iResolution.xy - 0.5) * iResolution.xy / iResolution.y;

  vec3 ro = vec3(0, 1, 0);
  vec3 rd = normalize(vec3(uv, 1));

  // background
  vec3 color = vec3(0.01);

  // sphere
  float distance = rayMarch(ro, rd);
  vec3 position = ro + rd * distance;
  float backgroundMask = step(MAX_DIST, distance);

  float sphereAO = sphOcclusion(position, getNormal(position), SPHERE_POSITION, SPHERE_RADIUS);
  vec3 sphereColor = vec3(sphereAO);

  color = mix(color, sphereColor, 1.0 - backgroundMask);

  // gamma
  color = pow(color, vec3(1.0 / 2.2));

  fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 hist = texelFetch( iChannel1, ivec2(fragCoord), 0 );
    float val = (hist.x + hist.y + hist.z) * (0.54/3.);

    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    float blue = abs(uv.y) < 0.43 ? 0.15 : 0.;
    float white = blue>0. && abs(uv.y) > 0.427 && abs(uv.x)<0.85 ? 1. : 0.;

    fragColor = vec4( white+val*0.95, white+val*0.95, white+blue+val, 1.0 );
}

