// Line draw function from https://www.shadertoy.com/view/4ljfRD by Loeizd

float drawLine (vec2 p1, vec2 p2, vec2 uv, float width)
{
    p1=(p1-0.5)*0.7;p2=(p2-0.5)*0.7;uv=(uv-0.5)*0.7;

    float r = 0.;
    float one_px = 1. / iResolution.x; //not really one px

    // get dist between points
    float d = distance(p1, p2);

    // get dist between current pixel and p1
    float duv = distance(p1, uv);

    //if point is on line, according to dist, it should match current uv 
    r = 1.-floor(1.-(width*one_px)+distance (mix(p1, p2, clamp(duv/d, 0., 1.)),  uv));

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime * 1.5;

    if (abs(uv.y-0.5) > 0.425) { fragColor=vec4(0.); return; }; // we don't draw here

    const float xmstep = 0.4;
    const float ymstep = 0.36;
    const float xmul = 0.08;
    const float ymul = 0.14;

    const float xstart = 0.155;
    const float ystart = 0.25;

    const float xsegments = 14.;
    const float ysegments = 9.;

    const float xstep = (1.01-xstart*2.) / (xsegments - 1.);
    const float ystep = (0.98-ystart*2.) / (ysegments - 1.);

    float tm=time*0.275;
    vec2 cp = vec2(xstart, ystart);

    const float xtmul = 2.;
    const float ytmul = 2.1;

    const float linesize = 0.8;

    float lines= 0.;
    for (float y=0.; y<ysegments; y+=1.) {
        float stx = xmstep+time*xtmul+ymstep*y;
        float sty = xmstep+time*ytmul+ymstep*(y+1.);
        vec2 lastp= vec2(-1.);
        for (float x=0.; x<xsegments; x+=1.) {
            vec2 p1 = vec2(cp.x+sin(stx)*xmul,cp.y+cos(sty+tm)*ymul);
            if (lastp.x >= 0.) lines += drawLine(lastp, p1, uv, linesize);
            if (y < ysegments - 1.) lines += drawLine(p1, vec2(cp.x+sin(stx+ymstep)*xmul, cp.y+ystep+cos(sty+ymstep+tm)*ymul), uv, linesize);

            lastp = p1;
            cp.x += xstep;
            stx += xmstep;
            sty += xmstep;
        }
        cp = vec2(xstart, cp.y + ystep);
    }

    fragColor = vec4(clamp(lines, 0.,1.));
}

