#ifdef GL_ES
precision mediump float;
#endif

#define PI (3.14159265359)

float c01(float p) {
  return clamp(p, 0.0, 1.0);
}

void fa(inout vec3 p) {
  p.xyz = abs(p.xyz);
}

void fm(inout vec3 p) {
  float a = min(p.x - p.y, 0.0);
  p.x -= a;
  p.y += a;
  a = min(p.x - p.z, 0.0);
  p.x -= a;
  p.z += a;
  a = min(p.y - p.z, 0.0);
  p.y -= a;
  p.z += a;
}

void fp(inout vec3 p, vec3 n, float d) {
  p.xyz -= 2.0 * min(0.0, dot(p.xyz, n) - d) * n;
}

void scale(inout vec3 p, float f) {
  p *= f;
}

void rotateX(inout vec3 p, float angle) {
  p *= mat3(
    1.0,
    0.0,
    0.0,
    0.0,
    cos(angle),
    -sin(angle),
    0.0,
    sin(angle),
    cos(angle)
  );
}

void rotateY(inout vec3 p, float angle) {
  p *= mat3(
    cos(angle),
    0.0,
    sin(angle),
    0.0,
    1.0,
    0.0,
    -sin(angle),
    0.0,
    cos(angle)
  );
}

void rotateZ(inout vec3 p, float angle) {
  p *= mat3(
    cos(angle),
    -sin(angle),
    0.0,
    sin(angle),
    cos(angle),
    0.0,
    0.0,
    0.0,
    1.0
  );
}

void inverseTranslate(inout vec3 p, vec3 t) {
  p -= t;
}

void rotate(inout vec3 p, vec3 angles) {
  rotateX(p, angles.x);
  rotateY(p, angles.y);
  rotateZ(p, angles.z);
}

void translate(inout vec3 p, vec3 t) {
  p += t;
}

struct hitData {
  float distance;
  vec3 hitColor;
  bool canReflect;
};

hitData minhitData(hitData a, hitData b) {
  if (a.distance < b.distance) return a;
  return b;
}

float sdfBox(vec3 point, vec3 size) {
  vec3 d = abs(point) - size;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdfSphere(vec3 p, float r) {
  return length(p) - r;
}

float sdfPlane(vec3 p) {
  return p.y;
}

float sdf_mengerb5c5e786_7b59_439b_9b77_49b133c6f47e(vec3 p) {
    vec3 t = vec3(1.5 + cos(iTime * 1.0) * 0.8, 1.0, 1.0);

    for (int i = 0; i < 3; i++) {
        fa(p);
        fm(p);
        scale(p, 3.0);
        translate(
            p,
            t * vec3(-2.0, -2.0, 0.0)
        );
        fp(
            p,
            vec3(0.0, 0.0, -1.0),
            t.x * -1.0
        );
    }


    return sdfBox(p, vec3(1.5 + cos(iTime * 1.0) * 0.8, 1.0, 1.0)) / 27.0;

}

hitData sdf_sphere_7064d573_52bb_4c0f_8c8f_9d1962bfa45d(vec3 p) {
  vec3 o = p;

  hitData res;
  hitData res2;
  res = hitData(sdfSphere(p, 1.4) / 1.0, vec3(0.0667, 0.5843, 0.7294), false);

  return res;

}

hitData sdf_menger_78a3d364_8ac6_4348_b521_a04e36c86545(vec3 p) {
  vec3 o = p;
  rotate(p, vec3(0.0, 0.0, 0.0) + cos(iTime * 0.5) * vec3(2.0, 2.0, 2.0));

  hitData res;
  hitData res2;
  res = hitData(
    sdf_mengerb5c5e786_7b59_439b_9b77_49b133c6f47e(p) / 1.0,
    vec3(0.4431, 0.6627, 0.7216),
    false
  );

  res2 = sdf_sphere_7064d573_52bb_4c0f_8c8f_9d1962bfa45d(o);
  if (res.distance < res2.distance) res = res2;

  return res;

}

hitData sdf_plane_c72c613d_1c9b_408b_b88a_b63bfcc53acb(vec3 p) {
  vec3 o = p;
  inverseTranslate(p, vec3(0.0, 0.0, 0.0));
  translate(p, vec3(0.0, 3.0, 0.0));

  hitData res;
  hitData res2;
  res = hitData(
    sdfPlane(p) / 1.0,
    mod(p.x * 3.0, 2.0) <= 1.0 ^^ mod(p.z * 3.0, 2.0) >= 1.0
      ? vec3(0.0667, 0.0588, 0.0588)
      : vec3(0.5059, 0.4784, 0.4784),
    false
  );

  return res;

}

hitData sdf(vec3 p) {
  hitData res = sdf_menger_78a3d364_8ac6_4348_b521_a04e36c86545(p);

  res = minhitData(res, sdf_plane_c72c613d_1c9b_408b_b88a_b63bfcc53acb(p));

  return res;
}

vec3 calcNormal(vec3 pos) {
  vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
  return normalize(
    e.xyy * sdf(pos + e.xyy).distance +
      e.yyx * sdf(pos + e.yyx).distance +
      e.yxy * sdf(pos + e.yxy).distance +
      e.xxx * sdf(pos + e.xxx).distance
  );
}

vec3 normal(vec3 point) {
  const vec3 smallStep = vec3(0.00001, 0, 0);

  return normalize(
    vec3(
      sdf(point + smallStep.xyy).distance - sdf(point - smallStep.xyy).distance,
      sdf(point + smallStep.yxy).distance - sdf(point - smallStep.yxy).distance,
      sdf(point + smallStep.yyx).distance - sdf(point - smallStep.yyx).distance
    )
  );
}

hitData raymarch(vec3 rayOrigin, vec3 rayDirection) {
  float distance = 0.0;
  float maxDistance = 50.0;
  float minHitDistance = 0.001;

  for (int i = 0; i < 256; i++) {
    if (distance > maxDistance) break;

    vec3 pos = rayOrigin + rayDirection * distance;

    hitData res = sdf(pos);

    if (res.distance < minHitDistance)
      return hitData(distance + res.distance, res.hitColor, res.canReflect);

    distance += res.distance;
  }

  return hitData(-1.0, vec3(0.0), false);
}

float ambientOcc(vec3 point, vec3 normal) {
  float occ = 0.0;
  float sca = 1.0;
  for (int i = 0; i < 5; i++) {
    float h = 0.01 + 0.12 * float(i) / 4.0;
    float d = sdf(point + h * normal).distance;
    occ += (h - d) * sca;
    sca *= 0.95;
    if (occ > 0.35) break;
  }
  return c01(1.0 - 3.0 * occ) * (0.5 + 0.5 * normal.y);
}

float shadow(vec3 ro, vec3 rd, float mint, float tmax) {
  float res = 1.0;
  float t = mint;
  for (int i = 0; i < 256; i++) {
    float h = sdf(ro + rd * t).distance;

    if (t > tmax) return res;
    if (h < 0.001) return 0.0;
    res = min(res, 18.0 * h / t);
    t += h;
  }
  return res;
}

vec3 spotlight(
  vec3 rayDirection,
  vec3 normal,
  vec3 point,
  vec3 baseColor,
  vec3 lightPos,
  vec3 lightColor,
  float shininess
) {
  vec3 lightDir = normalize(lightPos - point);
  vec3 hal = normalize(lightDir - rayDirection);
  float diffuse = dot(normal, lightDir);
  diffuse = c01(diffuse);
  diffuse *= ambientOcc(point, normal);
  diffuse *= shadow(point, lightDir, 0.02, 2.5);

  float pho = c01(pow(dot(normal, hal), shininess));
  float spe = pho * diffuse * 0.3;

  return baseColor * 2.2 * diffuse * lightColor + 5.0 * spe * lightColor * 0.4;
}

vec3 lightning(
  float distance,
  vec3 col,
  vec3 point,
  vec3 rayDirection,
  vec3 rayOrigin,
  vec3 normal
) {
  vec3 newColor = col;

  newColor += spotlight(
    rayDirection,
    normal,
    point,
    col,
    vec3(10.0, 10.0, 0.0),
    vec3(0.4, 0.8314, 0.9294),
    32.0
  );

  newColor += spotlight(
    rayDirection,
    normal,
    point,
    col,
    vec3(0.0 + cos(iTime * 1.0) * 1.0, 13.0, 0.0 + sin(iTime * 1.0) * 1.0),
    vec3(0.4, 0.8314, 0.9294),
    32.0
  );


  newColor /= 3.;
  vec3 distanceColor = vec3(0.0314, 0.1373, 0.4039);
  float distanceColorIntensity =
    1.0 - exp(-0.0001 * distance * distance * distance);
  return mix(newColor, vec3(0.0314, 0.1373, 0.4039), distanceColorIntensity);
}

vec3 render(vec3 rayOrigin, vec3 rayDirection) {
  vec3 col = vec3(0.);

  hitData hit = raymarch(rayOrigin, rayDirection);

  if (hit.distance > 0.0) {
    vec3 point = rayOrigin + rayDirection * hit.distance;
    vec3 normal = calcNormal(point);
    col = hit.hitColor;
    col = lightning(hit.distance, col, point, rayDirection, rayOrigin, normal);
  }

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime / 25.0 * 1.5;

    float camspeed = 7.0;
    vec3 ta = vec3(0.);
    vec3 ro = vec3(
        4.9 * cos(time * camspeed),
        3.0,
        4.9 * sin(time * camspeed)
    );

    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf, vec3(sin(.0), cos(.0), 0.0)));
    vec3 cu = normalize(cross(cs, cf));

    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 rd = normalize(p.x * cs + p.y * cu  + 2.5 * cf);

    vec3 col = render(ro, rd);
    col = pow(col, vec3(1.0/2.2)) ;

    fragColor = vec4(col, 1.0);
}

// Fork of "Zoom-o-rotors" by Xecutor. https://shadertoy.com/view/dslBz8
// 2023-07-04 15:24:58

#define R iResolution
#define T iTime/5.0


vec4 hsb2rgb( in vec4 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return vec4(c.z * mix( vec3(1.0), rgb, c.y), 1.);
}



void mainImage( out vec4 o, in vec2 p )
{
    vec2 u = (p*2.0-R.xy)/R.x;
    float l = pow(length(u),0.5);
    float l0 = l;
    l -= fract(T)/5.0;

    float tSlice = fract(T/100.0);
    float invtSlice = 1.0 - tSlice;


    float tt = T * sign(sin(20.0*l*acos(0.0)));
    u *= mat2(cos(tt), -sin(tt), sin(tt), cos(tt));
    l = l - floor(l*10.0) / 10.0;
    l = l > 0.01 ? pow(l, 0.15):0.0;
    l *= l0;
    float t = atan(u.y,u.x)/3.1415;
    float rawt = t * 3.1415;
    t = abs(t-floor(t*20.0)/20.0);
    t = clamp(t,0.0,0.05)*20.0;
    t = t>0.05?pow(t,0.15):0.0;
    t *= 1.4;
    o = vec4(t*l,t*l,t*l,1.0) * hsb2rgb( vec4( tSlice*rawt + invtSlice*(l0+tt+l/t), 1.0, 1.0, 1.0) );
}

