// SDF of a shape made of a set line and arc segments
float sdShape( in vec2 p, int kType[7], float kPath[17] )
{
    vec2 vb = vec2(kPath[0],kPath[1]);

    float d = dot2(p-vb);
    int off = 0;
    float s = 1.0;
    for( int i=0; i<kType.length(); i++ )
    {
        vec2 va = vb;
        vec2 ds;

        if( kType[i]==0) // line (x,y)
        {
            vb = vec2(kPath[off+2],kPath[off+3]);
            ds = sdSqLine( p, va, vb );
            off += 2;
        }
        else if( kType[i]==1) // arc (x,y,r)
        {
            vb = vec2(kPath[off+3],kPath[off+4]);
            ds = sdSqArc(p, va, vb, kPath[off+2], d );
        	off += 3;

        }

        // in/out test
        bvec3 cond = bvec3( p.y>=va.y, p.y<vb.y, ds.y>0.0 );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  

        d = min( d, ds.x );
    }
	return s*sqrt(d);
}

// correct outside, incorrect inside
float sdA( in vec2 p )
{
    float d = sdCircle( p, vec2(-0.4, 0.3), 0.5);
    d = min(d,sdBox( p, vec2( 0.4,-0.4), vec2(0.4,0.4) ));
    d = min(d,sdBox( p, vec2( 0.0, 0.0), vec2(0.4,0.8) ));
    return d;
}

// correct inside, incorrect outside
float sdB( in vec2 p )
{
   float d =     sdBox( p, vec2( 0.0, 2.0), vec2(5.0,1.2) );
       d = min(d,sdBox( p, vec2( 1.2, 1.0), vec2(0.8,1.0) ));
       d = min(d,sdBox( p, vec2( 1.4,-0.3), vec2(0.6,5.0) ));
       d = min(d,sdBox( p, vec2( 0.0,-2.0), vec2(2.0,1.2) ));
       d = min(d,sdBox( p, vec2(-1.2,-0.8), vec2(0.8,0.6) ));
       d = min(d,sdBox( p, vec2(-1.5, 0.3), vec2(0.6,0.7) ));
       d = min(d,sdCrescent( p-vec2(-0.4-1.0, 0.3), 1.1, 0.5, 1.0, 1.0, -1.0 ));
    return -d;
}

// correct both in side and outside
float sdC( in vec2 p )
{
	int kType[] = int[](0,0,0,0,0,0,1);
	float kPath[] = float[](-0.4, 0.8,
                             0.4, 0.8,
                             0.4,-0.0,
                             0.8,-0.0,
                             0.8,-0.8,
                            -0.4,-0.8,
                            -0.4,-0.2, 0.0,
                            -0.4, 0.8 );
    return sdShape(p,kType,kPath );
}

float opOnion( in float d, in float r )
{
  return abs(d) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // split screen horizontally into 3 parts.
    int numSplitScreens = 3;
    vec2 splitRes = vec2(iResolution.x / float(numSplitScreens), iResolution.y);
    int splitScreenIdx = int(floor(fragCoord.x / splitRes.x));
    vec2 splitCoord = vec2(fragCoord.x - float(splitScreenIdx) * splitRes.x, fragCoord.y);
    vec2 splitMouseCoord = vec2(iMouse.x - float(splitScreenIdx) * splitRes.x, iMouse.y);
    fragColor = vec4(splitCoord/splitRes.x, 0.0, 1.0);

    // normalized pixel coordinates within given split screen
    vec2 p = (splitCoord*2.0-splitRes.xy)/min(splitRes.y, splitRes.x);
    vec2 m = (splitMouseCoord*2.0-splitRes.xy)/min(splitRes.y, splitRes.x);

    p *= 1.3; // zoom out
    m *= 1.3;

    // distance computations:
    // render each distance at different part of the screen.
    float d;
    switch (splitScreenIdx) {
        case 0: d = sdA(p); break; // interior modeling (exact only inside)
        case 1: d = sdC(p); break; // boundary modeling (exact everywhere)
        case 2: d = sdB(p); break; // exterior modeling (exact only outside)
    }

    float onionMix = smoothstep(0.0, 1.0, clamp(0.5 + sin(iTime * 0.5 - 0.4) * 3.0, 0.0, 1.0));
    float onionMixSdf = float(onionMix > 0.0);

    float strokeWidth = 0.15 + 0.05 * sin(iTime);
    strokeWidth *= onionMix;
    d = mix(d, opOnion(d, strokeWidth), onionMixSdf);


    // coloring
    vec3 col = (d<0.0) ? vec3(0.6,0.2,1.0) : vec3(0.9,0.1,0.3);
    col *= 1.0 - exp(-9.0*abs(d));
	col *= 1.0 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );


    int mouseScreenIdx = int(floor(iMouse.x / splitRes.x));
    if( iMouse.z>0.001 && mouseScreenIdx == splitScreenIdx)
    {
        switch (splitScreenIdx) {
            case 0: d = sdA(m); break;
            case 1: d = sdC(m); break;
            case 2: d = sdB(m); break;
        }
        d = mix(d, opOnion(d, strokeWidth), onionMixSdf);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    float divLine = pow(abs(fract(fragCoord.x / splitRes.x) * 2.0 - 1.0), 300.0);
    divLine *= 1.0 - clamp(abs(fragCoord.x - iResolution.x * 0.5) - iResolution.x * 0.5 + 10.0, 0.0, 1.0);

    col = mix(col, vec3(1.0,1.0,1.0), divLine);

    // output
	fragColor = vec4(col, 1.0);
}

